import React, { useEffect, useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import {
  Building2, TrendingUp, Users, Calendar, DollarSign,
  Activity, Clock, CheckCircle2, AlertCircle, Sparkles,
  BarChart3, PieChart, ArrowUpRight, ArrowDownRight,
  Bot, Zap, Target, FileText, ExternalLink, Check, Brush
} from 'lucide-react';
import {
  AreaChart, Area, BarChart, Bar, PieChart as RechartsPie, Pie, Cell,
  XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer, Legend
} from 'recharts';
import i18n from '../../i18n';
import useStore from '../../store/useStore';
import { updateRoomStatus, getDailyReport } from '../../services/api';
import './EnterpriseDashboard.css';

// Chart data
const revenueData = [
  { name: 'Jan', revenue: 320000, bookings: 42 },
  { name: 'Feb', revenue: 280000, bookings: 38 },
  { name: 'Mar', revenue: 350000, bookings: 48 },
  { name: 'Apr', revenue: 420000, bookings: 55 },
  { name: 'May', revenue: 480000, bookings: 62 },
  { name: 'Jun', revenue: 520000, bookings: 68 },
];

const occupancyData = [
  { nameKey: 'dashboard.rooms.status.occupied', value: 72, color: '#10b981' },
  { nameKey: 'dashboard.rooms.status.available', value: 18, color: '#3b82f6' },
  { nameKey: 'dashboard.rooms.status.maintenance', value: 6, color: '#f59e0b' },
  { nameKey: 'dashboard.rooms.status.cleaning', value: 4, color: '#8b5cf6' },
];

const agentPerformance = [
  { name: 'Scraper', tasks: 156, success: 148 },
  { name: 'Marketing', tasks: 89, success: 82 },
  { name: 'Creative', tasks: 45, success: 43 },
  { name: 'Sales', tasks: 67, success: 58 },
  { name: 'Operations', tasks: 234, success: 228 },
];

const containerVariants = {
  hidden: { opacity: 0 },
  visible: {
    opacity: 1,
    transition: {
      staggerChildren: 0.1,
    },
  },
};

const itemVariants = {
  hidden: { opacity: 0, y: 20 },
  visible: { opacity: 1, y: 0 },
};

const EnterpriseDashboard = () => {
  const { stats, rooms, agents, setDailyReport, setGeneratingReport, isGeneratingReport, dailyReport, lang, updateRoom } = useStore();
  const t = (key, options) => i18n.t(key, options);
  const [activeTab, setActiveTab] = useState('overview');
  const [selectedRoom, setSelectedRoom] = useState(null);
  const [roomActionLoading, setRoomActionLoading] = useState(false);

  useEffect(() => {
    if (lang && i18n.language !== lang) {
      i18n.changeLanguage(lang);
    }
  }, [lang]);

  const handleGenerateReport = async () => {
    setGeneratingReport(true);
    try {
      const reportResponse = await getDailyReport();
      setDailyReport(reportResponse);
    } catch (error) {
      console.error('Error generating report:', error);
    } finally {
      setGeneratingReport(false);
    }
  };

  const isRTL = lang === 'he';

  return (
    <motion.div
      className="enterprise-dashboard"
      variants={containerVariants}
      initial="hidden"
      animate="visible"
      dir={isRTL ? 'rtl' : 'ltr'}
    >
      {/* Header */}
      <motion.div className="dashboard-header" variants={itemVariants}>
        <div className="header-content">
          <h1 className="dashboard-title">
            <Building2 size={32} />
            <span className="text-gradient">{t('branding.name')}</span>
          </h1>
          <p className="dashboard-subtitle">{t('dashboard.subtitle')}</p>
        </div>
        <motion.button
          whileHover={{ scale: 1.02 }}
          whileTap={{ scale: 0.98 }}
          onClick={handleGenerateReport}
          disabled={isGeneratingReport}
          className="btn-primary report-btn"
        >
          {isGeneratingReport ? (
            <>
              <Activity size={20} className="animate-spin" />
              {t('dashboard.generatingReport')}
            </>
          ) : (
            <>
              <FileText size={20} />
              {t('dashboard.generateReport')}
            </>
          )}
        </motion.button>
      </motion.div>

      {/* Stats Grid */}
      <motion.div className="stats-grid" variants={itemVariants}>
        <StatCard
          title={t('dashboard.stats.revenueToday')}
          value={`â‚ª${stats.revenue.today.toLocaleString()}`}
          trend={stats.revenue.trend}
          icon={DollarSign}
          color="gold"
          t={t}
        />
        <StatCard
          title={t('dashboard.stats.occupancy')}
          value={`${stats.occupancy.current}%`}
          trend={stats.occupancy.trend}
          icon={Users}
          color="blue"
          t={t}
        />
        <StatCard
          title={t('dashboard.stats.bookingsToday')}
          value={stats.bookings.today}
          trend={stats.bookings.trend}
          icon={Calendar}
          color="purple"
          t={t}
        />
        <StatCard
          title={t('dashboard.stats.automationSavings')}
          value={`â‚ª${stats.savings.today.toLocaleString()}`}
          trend={stats.savings.automationRate}
          icon={Zap}
          color="emerald"
          trendLabel={t('dashboard.stats.automationRate')}
          t={t}
        />
      </motion.div>

      {/* Main Content Grid */}
      <div className="dashboard-grid">
        {/* Revenue Chart */}
        <motion.div className="glass-card chart-card revenue-chart" variants={itemVariants}>
          <div className="card-header">
            <h3><BarChart3 size={20} /> {t('dashboard.charts.revenueBookings')}</h3>
            <div className="chart-legend">
              <span className="legend-item"><span className="dot gold" /> {t('dashboard.charts.revenue')}</span>
              <span className="legend-item"><span className="dot blue" /> {t('dashboard.charts.bookings')}</span>
            </div>
          </div>
          <div className="chart-container">
            <ResponsiveContainer width="100%" height={280}>
              <AreaChart data={revenueData}>
                <defs>
                  <linearGradient id="colorRevenue" x1="0" y1="0" x2="0" y2="1">
                    <stop offset="5%" stopColor="#eab308" stopOpacity={0.4} />
                    <stop offset="95%" stopColor="#eab308" stopOpacity={0} />
                  </linearGradient>
                  <linearGradient id="colorBookings" x1="0" y1="0" x2="0" y2="1">
                    <stop offset="5%" stopColor="#3b82f6" stopOpacity={0.4} />
                    <stop offset="95%" stopColor="#3b82f6" stopOpacity={0} />
                  </linearGradient>
                </defs>
                <CartesianGrid strokeDasharray="3 3" stroke="rgba(255,255,255,0.1)" />
                <XAxis dataKey="name" stroke="#64748b" />
                <YAxis stroke="#64748b" />
                <Tooltip
                  contentStyle={{
                    background: 'rgba(15, 23, 42, 0.9)',
                    border: '1px solid rgba(255,255,255,0.1)',
                    borderRadius: '12px',
                    color: '#fff',
                  }}
                />
                <Area
                  type="monotone"
                  dataKey="revenue"
                  stroke="#eab308"
                  fillOpacity={1}
                  fill="url(#colorRevenue)"
                  strokeWidth={2}
                />
                <Area
                  type="monotone"
                  dataKey="bookings"
                  stroke="#3b82f6"
                  fillOpacity={1}
                  fill="url(#colorBookings)"
                  strokeWidth={2}
                  yAxisId={0}
                />
              </AreaChart>
            </ResponsiveContainer>
          </div>
        </motion.div>

        {/* Occupancy Chart */}
        <motion.div className="glass-card chart-card occupancy-chart" variants={itemVariants}>
          <div className="card-header">
            <h3><PieChart size={20} /> {t('dashboard.charts.roomStatus')}</h3>
          </div>
          <div className="chart-container pie-chart-container">
            <ResponsiveContainer width="100%" height={240}>
              <RechartsPie>
                <Pie
                  data={occupancyData}
                  cx="50%"
                  cy="50%"
                  innerRadius={60}
                  outerRadius={90}
                  paddingAngle={5}
                  dataKey="value"
                >
                  {occupancyData.map((entry, index) => (
                    <Cell key={`cell-${index}`} fill={entry.color} />
                  ))}
                </Pie>
                <Tooltip
                  contentStyle={{
                    background: 'rgba(15, 23, 42, 0.9)',
                    border: '1px solid rgba(255,255,255,0.1)',
                    borderRadius: '12px',
                    color: '#fff',
                  }}
                />
              </RechartsPie>
            </ResponsiveContainer>
            <div className="pie-legend">
              {occupancyData.map((item) => (
                <div key={item.nameKey} className="pie-legend-item">
                  <span className="pie-dot" style={{ background: item.color }} />
                  <span>{t(item.nameKey)}</span>
                  <span className="pie-value">{item.value}%</span>
                </div>
              ))}
            </div>
          </div>
        </motion.div>

        {/* Agent Status */}
        <motion.div className="glass-card agents-card" variants={itemVariants}>
          <div className="card-header">
            <h3><Bot size={20} /> {t('dashboard.charts.agentsStatus')}</h3>
            <span className="badge-success">{t('dashboard.charts.agentsAllActive')}</span>
          </div>
          <div className="agents-list">
            {Object.entries(agents).map(([id, agent]) => (
              <AgentStatusItem key={id} id={id} agent={agent} t={t} />
            ))}
          </div>
        </motion.div>

        {/* Rooms Grid */}
        <motion.div className="glass-card rooms-card" variants={itemVariants}>
          <div className="card-header">
            <h3><Building2 size={20} /> {t('dashboard.rooms.title')}</h3>
            <span className="room-count">{t('dashboard.rooms.count', { count: rooms.length })}</span>
          </div>
          <div className="rooms-grid">
            {rooms.slice(0, 6).map((room) => (
              <RoomCard key={room.id} room={room} onViewDetails={() => setSelectedRoom(room)} t={t} />
            ))}
          </div>
        </motion.div>

        {/* Agent Performance Chart */}
        <motion.div className="glass-card performance-chart" variants={itemVariants}>
          <div className="card-header">
            <h3><Target size={20} /> {t('dashboard.charts.agentsPerformance')}</h3>
          </div>
          <div className="chart-container">
            <ResponsiveContainer width="100%" height={220}>
              <BarChart data={agentPerformance} layout="vertical">
                <CartesianGrid strokeDasharray="3 3" stroke="rgba(255,255,255,0.1)" />
                <XAxis type="number" stroke="#64748b" />
                <YAxis type="category" dataKey="name" stroke="#64748b" width={80} />
                <Tooltip
                  contentStyle={{
                    background: 'rgba(15, 23, 42, 0.9)',
                    border: '1px solid rgba(255,255,255,0.1)',
                    borderRadius: '12px',
                    color: '#fff',
                  }}
                />
                <Bar dataKey="tasks" fill="#3b82f6" radius={[0, 4, 4, 0]} name={t('dashboard.charts.tasks')} />
                <Bar dataKey="success" fill="#10b981" radius={[0, 4, 4, 0]} name={t('dashboard.charts.successes')} />
              </BarChart>
            </ResponsiveContainer>
          </div>
        </motion.div>
      </div>

      {/* Daily Report Modal */}
      <AnimatePresence>
        {dailyReport && (
          <motion.div
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            exit={{ opacity: 0 }}
            className="report-modal-backdrop"
            onClick={() => setDailyReport(null)}
          >
            <motion.div
              initial={{ opacity: 0, scale: 0.9, y: 20 }}
              animate={{ opacity: 1, scale: 1, y: 0 }}
              exit={{ opacity: 0, scale: 0.9, y: 20 }}
              className="report-modal glass-card"
              onClick={(e) => e.stopPropagation()}
            >
              <div className="report-header">
                <h2><Sparkles size={24} /> {t('dashboard.report.title')} - {dailyReport.date}</h2>
                <button onClick={() => setDailyReport(null)} className="close-btn">Ã—</button>
              </div>

              <div className="report-content">
                <div className="report-stats">
                  <div className="report-stat">
                    <span className="stat-value text-gradient">{dailyReport.roi_metrics?.hours_saved || 0}</span>
                    <span className="stat-label">{t('dashboard.report.hoursSaved')}</span>
                  </div>
                  <div className="report-stat">
                    <span className="stat-value" style={{ color: '#10b981' }}>{dailyReport.roi_metrics?.cleanings_triggered || 0}</span>
                    <span className="stat-label">{t('dashboard.report.cleaningsTriggered')}</span>
                  </div>
                  <div className="report-stat">
                    <span className="stat-value" style={{ color: '#3b82f6' }}>{dailyReport.roi_metrics?.leads_captured || 0}</span>
                    <span className="stat-label">{t('dashboard.report.leadsCaptured')}</span>
                  </div>
                </div>

                <div className="report-section">
                  <h4>{t('dashboard.report.recommendationsTitle')}</h4>
                  <ul className="recommendations-list">
                    {dailyReport.recommendations.map((rec, i) => (
                      <li key={i}><CheckCircle2 size={16} /> {rec}</li>
                    ))}
                  </ul>
                </div>

                <div className="report-section">
                  <h4>{t('dashboard.report.agentSummaryTitle')}</h4>
                  <div className="agent-summary">
                    {dailyReport.agent_performance?.map((agent) => (
                      <div key={agent.type} className="agent-summary-item">
                        <span className="agent-name">{agent.name}</span>
                        <span className="agent-tasks">{agent.tasks_today} {t('dashboard.charts.tasks')}</span>
                        <span className={`agent-status-badge ${agent.status}`}>{agent.status}</span>
                      </div>
                    ))}
                  </div>
                </div>
              </div>

              <div className="report-footer">
                <p className="automation-message">
                  <Zap size={16} />
                  {t('dashboard.report.automationMessage', { rate: dailyReport.savings?.automation_rate || 0 })}
                </p>
              </div>
            </motion.div>
          </motion.div>
        )}
      </AnimatePresence>

      {/* Room Details Modal */}
      <AnimatePresence>
        {selectedRoom && (
          <motion.div
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            exit={{ opacity: 0 }}
            className="report-modal-backdrop"
            onClick={() => setSelectedRoom(null)}
          >
            <motion.div
              initial={{ opacity: 0, scale: 0.95, y: 20 }}
              animate={{ opacity: 1, scale: 1, y: 0 }}
              exit={{ opacity: 0, scale: 0.95, y: 20 }}
              className="report-modal glass-card room-details-modal"
              onClick={(e) => e.stopPropagation()}
            >
              <div className="report-header">
                <h2>
                  <Building2 size={22} />
                  {t('dashboard.rooms.roomLabel', { number: selectedRoom.id })}
                </h2>
                <button onClick={() => setSelectedRoom(null)} className="close-btn">Ã—</button>
              </div>

              <div className="report-content">
                <div className="room-details-grid">
                  <div className="room-detail-item">
                    <span className="detail-label">{t('dashboard.rooms.title')}</span>
                    <span className="detail-value">{selectedRoom.name}</span>
                  </div>
                  <div className="room-detail-item">
                    <span className="detail-label">{t('dashboard.charts.roomStatus')}</span>
                    <span className="detail-value">{t(`dashboard.rooms.status.${selectedRoom.status}`)}</span>
                  </div>
                  <div className="room-detail-item">
                    <span className="detail-label">{t('roles.guest')}</span>
                    <span className="detail-value">{selectedRoom.guest || '-'}</span>
                  </div>
                  <div className="room-detail-item">
                    <span className="detail-label">{t('dashboard.rooms.checkout')}</span>
                    <span className="detail-value">{selectedRoom.checkOut || '-'}</span>
                  </div>
                </div>
              </div>

              <div className="report-footer room-details-actions">
                <button
                  className="btn-secondary"
                  onClick={() => setSelectedRoom(null)}
                >
                  {t('common.close')}
                </button>
                <button
                  className="btn-primary"
                  disabled={roomActionLoading}
                  onClick={async () => {
                    setRoomActionLoading(true);
                    try {
                      await updateRoomStatus(selectedRoom.id, { status: 'cleaning' });
                      updateRoom(selectedRoom.id, { status: 'cleaning' });
                    } catch (error) {
                      console.error('Failed to trigger cleaning:', error);
                    } finally {
                      setRoomActionLoading(false);
                      setSelectedRoom(null);
                    }
                  }}
                >
                  <Brush size={18} />
                  {t('dashboard.rooms.sendCleaner')}
                </button>
              </div>
            </motion.div>
          </motion.div>
        )}
      </AnimatePresence>
    </motion.div>
  );
};

// Stat Card Component
const StatCard = ({ title, value, trend, icon: Icon, color, trendLabel, t }) => {
  const translate = t || ((key) => key);
  const isPositive = trend >= 0;

  return (
    <motion.div
      whileHover={{ y: -4, scale: 1.02 }}
      className={`stat-card glass-card stat-${color}`}
    >
      <div className="stat-icon">
        <Icon size={24} />
      </div>
      <div className="stat-content">
        <span className="stat-label">{title}</span>
        <span className="stat-value">{value}</span>
        <div className={`stat-trend ${isPositive ? 'positive' : 'negative'}`}>
          {isPositive ? <ArrowUpRight size={16} /> : <ArrowDownRight size={16} />}
          <span>{Math.abs(trend)}%</span>
          <span className="trend-label">{trendLabel || translate('dashboard.trendLabelDefault')}</span>
        </div>
      </div>
    </motion.div>
  );
};

// Agent Status Item
const AgentStatusItem = ({ id, agent, t }) => {
  const translate = t || ((key) => key);
  const agentNames = {
    scraper: { name: 'Scraper Agent', icon: 'ğŸ”' },
    marketing: { name: 'Marketing Agent', icon: 'ğŸ“£' },
    creative: { name: 'Creative Agent', icon: 'ğŸ¨' },
    sales: { name: 'Sales Manager', icon: 'ğŸ’¼' },
    operations: { name: 'Operations Agent', icon: 'âš™ï¸' },
  };

  const info = agentNames[id] || { name: id, icon: 'ğŸ¤–' };

  return (
    <motion.div
      whileHover={{ x: 4 }}
      className="agent-item"
    >
      <div className="agent-info">
        <span className="agent-icon">{info.icon}</span>
        <span className="agent-name">{info.name}</span>
      </div>
      <div className="agent-meta">
        <span className="agent-tasks">{agent.taskCount} {translate('dashboard.charts.tasks')}</span>
        <span className={`status-badge ${agent.status}`}>
          {agent.status === 'online'
            ? translate('status.online')
            : agent.status === 'busy'
              ? translate('status.busy')
              : translate('status.offline')}
        </span>
      </div>
    </motion.div>
  );
};

// Room Card Component
const RoomCard = ({ room, onViewDetails, t }) => {
  const translate = t || ((key) => key);
  const statusColors = {
    occupied: '#10b981',
    available: '#3b82f6',
    cleaning: '#f59e0b',
    maintenance: '#ef4444',
  };

  const statusLabels = {
    occupied: translate('dashboard.rooms.status.occupied'),
    available: translate('dashboard.rooms.status.available'),
    cleaning: translate('dashboard.rooms.status.cleaning'),
    maintenance: translate('dashboard.rooms.status.maintenance'),
  };

  return (
    <motion.div
      whileHover={{ scale: 1.03 }}
      className="room-card"
      style={{
        backgroundImage: `linear-gradient(to top, rgba(0,0,0,0.8), transparent), url(${room.image})`,
      }}
    >
      <div className="room-status" style={{ background: statusColors[room.status] }}>
        {statusLabels[room.status]}
      </div>
      <div className="room-info">
        <span className="room-number">{translate('dashboard.rooms.roomLabel', { number: room.id })}</span>
        <span className="room-name">{room.name}</span>
        {room.guest && <span className="room-guest">{room.guest}</span>}
        <button className="room-details-btn" onClick={onViewDetails}>
          {translate('common.viewDetails')}
        </button>
      </div>
    </motion.div>
  );
};

export default EnterpriseDashboard;
