import { useMemo } from 'react';
import { translations } from '../utils/constants';

/**
 * Custom hook for translations
 * @param {string} lang - Language code ('he', 'en', 'el')
 * @returns {object} Translation object with all translated strings
 */
export const useTranslations = (lang = 'he') => {
  const t = useMemo(() => {
    return translations[lang] || translations['he'];
  }, [lang]);

  return t;
};

export default useTranslations;
