import React, { useState, useEffect } from 'react';
import { Bot, Send, TrendingUp, Users, Bed, Layout, Bell, Settings } from 'lucide-react';
import { BarChart, Bar, XAxis, YAxis, Tooltip, ResponsiveContainer, CartesianGrid, AreaChart, Area } from 'recharts';
import { agents } from './agentData';

function App() {
  const [userInput, setUserInput] = useState("");
  const [aiResponse, setAiResponse] = useState("המערכת מנותחת וממתינה להוראות שלך, אדוני.");
  const [isClient, setIsClient] = useState(false);

  useEffect(() => { setIsClient(true); }, []);

  const askAI = async () => {
    if (!userInput) return;
    setAiResponse("מנתח נתונים בזמן אמת...");
    try {
      const response = await fetch('http://127.0.0.1:5000/ask', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ message: userInput }),
      });
      const data = await response.json();
      setAiResponse(data.reply || data.message);
      setUserInput("");
    } catch (e) { setAiResponse("שגיאה בחיבור לליבת ה-AI. וודא ששרת ה-Python רץ."); }
  };

  return (
    <div style={{ padding: '0', margin: '0', fontFamily: 'Inter, system-ui, sans-serif', direction: 'rtl', backgroundColor: '#020617', color: '#f8fafc', minHeight: '100vh', display: 'flex' }}>
      
      {/* Sidebar - תחושת אפליקציה מקצועית */}
      <div style={{ width: '80px', backgroundColor: '#0f172a', borderLeft: '1px solid #1e293b', display: 'flex', flexDirection: 'column', alignItems: 'center', padding: '20px 0', gap: '30px' }}>
        <div style={{ background: 'linear-gradient(135deg, #3b82f6 0%, #2563eb 100%)', padding: '12px', borderRadius: '16px', boxShadow: '0 4px 12px rgba(37, 99, 235, 0.3)' }}><Layout color="white" /></div>
        <Bell size={24} color="#64748b" />
        <Settings size={24} color="#64748b" />
      </div>

      {/* Main Content */}
      <div style={{ flex: 1, padding: '40px', overflowY: 'auto' }}>
        
        {/* Header */}
        <header style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '40px' }}>
          <div>
            <h1 style={{ fontSize: '32px', fontWeight: '800', margin: '0', letterSpacing: '-0.025em' }}>LuxuryStay <span style={{ color: '#3b82f6' }}>AI Assistant</span></h1>
            <p style={{ color: '#64748b', margin: '5px 0 0 0' }}>ברוך הבא, מנהל המערכת. הנה תמונת המצב של המלון שלך.</p>
          </div>
          <div style={{ display: 'flex', alignItems: 'center', gap: '10px', background: 'rgba(34, 197, 94, 0.1)', color: '#4ade80', padding: '8px 20px', borderRadius: '30px', border: '1px solid rgba(34, 197, 94, 0.2)', fontWeight: '600', fontSize: '14px' }}>
            <span style={{ height: '8px', width: '8px', backgroundColor: '#22c55e', borderRadius: '50%', boxShadow: '0 0 10px #22c55e' }}></span>
            מערכת AI מקוונת
          </div>
        </header>

        {/* Stats Grid */}
        <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(300px, 1fr))', gap: '25px', marginBottom: '40px' }}>
          <StatBox icon={<TrendingUp color="#3b82f6"/>} label="תפוסה נוכחית" value="88%" trend="+5% מהחודש שעבר" />
          <StatBox icon={<Users color="#8b5cf6"/>} label="משימות סוכנים" value="124" trend="הספק גבוה" />
          <StatBox icon={<Bed color="#ec4899"/>} label="חדרים מוכנים" value="32/40" trend="בניקיון: 8" />
        </div>

        <div style={{ display: 'grid', gridTemplateColumns: '1fr 1.5fr', gap: '30px' }}>
          
          {/* AI Consultation Card */}
          <div style={{ background: '#0f172a', border: '1px solid #1e293b', padding: '30px', borderRadius: '32px', boxShadow: '0 20px 25px -5px rgba(0,0,0,0.1)' }}>
            <h3 style={{ display: 'flex', alignItems: 'center', gap: '12px', marginTop: 0, fontSize: '20px' }}>
              <div style={{ padding: '8px', background: 'rgba(59, 130, 246, 0.1)', borderRadius: '10px' }}><Bot color="#3b82f6" /></div>
              התייעצות חכמה
            </h3>
            <div style={{ height: '280px', background: '#020617', padding: '25px', borderRadius: '24px', marginBottom: '25px', borderRight: '4px solid #3b82f6', lineHeight: '1.7', color: '#cbd5e1', overflowY: 'auto', fontSize: '16px' }}>
              {aiResponse}
            </div>
            <div style={{ display: 'flex', gap: '12px' }}>
              <input 
                value={userInput} 
                onChange={(e)=>setUserInput(e.target.value)} 
                onKeyPress={(e) => e.key === 'Enter' && askAI()}
                placeholder="כתוב שאלה ל-AI (למשל: נתח את ביצועי דני)..." 
                style={{ flex: 1, background: '#1e293b', border: 'none', color: '#fff', padding: '18px', borderRadius: '18px', outline: 'none', fontSize: '15px' }}
              />
              <button onClick={askAI} style={{ background: '#3b82f6', color: '#fff', border: 'none', width: '55px', borderRadius: '18px', cursor: 'pointer', display: 'flex', justifyContent: 'center', alignItems: 'center', transition: '0.2s' }}>
                <Send size={22}/>
              </button>
            </div>
          </div>

          {/* Analytics Card */}
          <div style={{ background: '#0f172a', border: '1px solid #1e293b', padding: '30px', borderRadius: '32px', boxShadow: '0 20px 25px -5px rgba(0,0,0,0.1)' }}>
            <h3 style={{ marginTop: 0, marginBottom: '25px', fontSize: '20px' }}>ביצועי צוות בזמן אמת</h3>
            <div style={{ height: '350px', width: '100%' }}>
              {isClient && (
                <ResponsiveContainer width="100%" height="100%">
                  <BarChart data={agents}>
                    <defs>
                      <linearGradient id="barGradient" x1="0" y1="0" x2="0" y2="1">
                        <stop offset="0%" stopColor="#3b82f6" stopOpacity={1}/>
                        <stop offset="100%" stopColor="#2563eb" stopOpacity={0.8}/>
                      </linearGradient>
                    </defs>
                    <CartesianGrid strokeDasharray="3 3" vertical={false} stroke="#1e293b" />
                    <XAxis dataKey="name" stroke="#64748b" axisLine={false} tickLine={false} tick={{fontSize: 12}} dy={10} />
                    <YAxis stroke="#64748b" axisLine={false} tickLine={false} tick={{fontSize: 12}} />
                    <Tooltip cursor={{fill: 'rgba(255,255,255,0.05)'}} contentStyle={{ background: '#0f172a', border: '1px solid #1e293b', borderRadius: '12px', color: '#fff' }} />
                    <Bar dataKey="tasks" fill="url(#barGradient)" radius={[8, 8, 0, 0]} barSize={45} />
                  </BarChart>
                </ResponsiveContainer>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

function StatBox({ icon, label, value, trend }) {
  return (
    <div style={{ background: '#0f172a', border: '1px solid #1e293b', padding: '25px', borderRadius: '28px', transition: '0.3s' }}>
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'flex-start', marginBottom: '15px' }}>
        <div style={{ background: 'rgba(255,255,255,0.03)', padding: '12px', borderRadius: '14px' }}>{icon}</div>
        <span style={{ color: '#4ade80', fontSize: '13px', fontWeight: '600', background: 'rgba(74, 222, 128, 0.1)', padding: '4px 10px', borderRadius: '20px' }}>{trend}</span>
      </div>
      <div style={{ fontSize: '15px', color: '#94a3b8', marginBottom: '5px' }}>{label}</div>
      <div style={{ fontSize: '28px', fontWeight: '800', color: '#f8fafc' }}>{value}</div>
    </div>
  );
}

export default App;
