import React, { useEffect } from 'react';
import { CheckCircle2, Languages, Wrench, Sparkles, Brush } from 'lucide-react';
import useStore from '../../store/useStore';
import { setWorkerLanguage } from '../../services/api';
import './FieldView.css';

const MARKET = process.env.REACT_APP_MARKET || 'US';

const MARKET_LANGUAGES = {
  US: [
    { code: 'en', label: 'English' },
    { code: 'es', label: 'Spanish' },
  ],
  IL: [
    { code: 'he', label: 'Hebrew' },
    { code: 'th', label: 'Thai' },
    { code: 'hi', label: 'Hindi' },
  ],
};

const TASK_ICONS = {
  Cleaning: Brush,
  Maintenance: Wrench,
  'VIP Treats': Sparkles,
};

const FieldView = () => {
  const { fieldTasks, completeFieldTask, fieldLanguage, setFieldLanguage } = useStore();
  const languages = MARKET_LANGUAGES[MARKET] || MARKET_LANGUAGES.US;

  useEffect(() => {
    const syncWorkerLanguage = async () => {
      try {
        await setWorkerLanguage(fieldLanguage);
      } catch (error) {
        console.error('Failed to sync worker language:', error);
      }
    };
    syncWorkerLanguage();
  }, [fieldLanguage]);

  return (
    <div className="field-view">
      <div className="field-header">
        <div className="field-title">
          <h2>Field Ops</h2>
          <span className="field-subtitle">Mobile-first task feed</span>
        </div>
        <div className="field-lang-toggle">
          <Languages size={16} />
          <div className="field-lang-options">
            {languages.map((lang) => (
              <button
                key={lang.code}
                className={`field-lang-btn ${fieldLanguage === lang.code ? 'active' : ''}`}
                onClick={() => setFieldLanguage(lang.code)}
              >
                {lang.label}
              </button>
            ))}
          </div>
        </div>
      </div>

      <div className="field-task-list">
        {fieldTasks.map((task) => {
          const Icon = TASK_ICONS[task.title] || CheckCircle2;
          return (
            <div key={task.id} className={`field-task-card ${task.status}`}>
              <div className="task-main">
                <div className="task-icon">
                  <Icon size={18} />
                </div>
                <div className="task-info">
                  <span className="task-title">{task.title}</span>
                  <span className="task-meta">Room {task.room} Â· {task.priority}</span>
                </div>
              </div>
              <button
                className="task-complete-btn"
                onClick={() => completeFieldTask(task.id)}
                disabled={task.status === 'completed'}
              >
                {task.status === 'completed' ? 'Completed' : 'Mark Complete'}
              </button>
            </div>
          );
        })}
      </div>
    </div>
  );
};

export default FieldView;
