import React, { useState, useEffect } from 'react';
import useTranslations from './hooks/useTranslations';
import Sidebar from './components/layout/Sidebar';
import TopBar from './components/layout/TopBar';
import EnterpriseDashboard from './components/dashboard/EnterpriseDashboard';
import LeadsCRM from './components/crm/LeadsCRM';
import MayaChat from './components/maya/MayaChat';
import FieldView from './components/features/FieldView';
import useStore from './store/useStore';
import { getDemoAuthToken } from './services/api';
import { wsService } from './services/websocket';
import './styles/tailwind.css';
import './App.css';

// Placeholder components for other views
const RoomsView = () => {
  const { t } = useTranslations();
  return (
  <div className="view-placeholder glass-card">
    <h2>{t('placeholders.roomsTitle')}</h2>
    <p>{t('placeholders.roomsDesc')}</p>
  </div>
  );
};

const AnalyticsView = () => {
  const { t } = useTranslations();
  return (
  <div className="view-placeholder glass-card">
    <h2>{t('placeholders.analyticsTitle')}</h2>
    <p>{t('placeholders.analyticsDesc')}</p>
  </div>
  );
};

const AgentsView = () => {
  const { t } = useTranslations();
  const { agents } = useStore();

  return (
    <div className="agents-view">
      <h2 className="view-title text-gradient">{t('agentsView.title')}</h2>
      <div className="agents-grid">
        {Object.entries(agents).map(([id, agent]) => (
          <div
            key={id}
            className="agent-detail-card glass-card"
          >
            <div className="agent-header">
              <span className="agent-emoji">
                {id === 'scraper' && 'üîç'}
                {id === 'marketing' && 'üì£'}
                {id === 'creative' && 'üé®'}
                {id === 'sales' && 'üíº'}
                {id === 'operations' && '‚öôÔ∏è'}
              </span>
              <div>
                <h3>{id.charAt(0).toUpperCase() + id.slice(1)} Agent</h3>
                <span className={`status-pill ${agent.status}`}>
                  {agent.status === 'online'
                    ? t('agentsView.statusOnline')
                    : agent.status === 'busy'
                      ? t('agentsView.statusBusy')
                      : t('agentsView.statusOffline')}
                </span>
              </div>
            </div>
            <div className="agent-stats">
              <div className="agent-stat">
                <span className="value">{agent.taskCount}</span>
                <span className="label">{t('agentsView.tasks')}</span>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

const SettingsView = () => {
  const { t } = useTranslations();
  return (
  <div className="view-placeholder glass-card">
    <h2>{t('placeholders.settingsTitle')}</h2>
    <p>{t('placeholders.settingsDesc')}</p>
  </div>
  );
};

function App() {
  const { lang, sidebarOpen, activeTenantId, authToken, setAuthToken, role } = useStore();
  const { i18n } = useTranslations();
  const [activeView, setActiveView] = useState('dashboard');
  const isRTL = lang === 'he';

  useEffect(() => {
    // Connect to WebSocket on mount
    wsService.connect();

    return () => {
      wsService.disconnect();
    };
  }, []);

  useEffect(() => {
    const ensureAuth = async () => {
      if (authToken) return;
      try {
        const result = await getDemoAuthToken(activeTenantId);
        setAuthToken(result.token);
      } catch (error) {
        console.error('Failed to obtain demo token:', error);
      }
    };
    ensureAuth();
  }, [activeTenantId, authToken, setAuthToken]);

  useEffect(() => {
    i18n.changeLanguage(lang);
    if (typeof document !== 'undefined') {
      document.documentElement.dir = isRTL ? 'rtl' : 'ltr';
      document.documentElement.lang = lang;
    }
  }, [i18n, isRTL, lang]);

  useEffect(() => {
    const roleViews = {
      host: ['dashboard', 'crm', 'analytics'],
      operator: ['dashboard', 'rooms', 'analytics', 'agents', 'settings'],
      field: ['field'],
    };
    const allowed = roleViews[role] || ['dashboard'];
    if (!allowed.includes(activeView)) {
      setActiveView(allowed[0]);
    }
  }, [role, activeView]);

  const renderView = () => {
    switch (activeView) {
      case 'dashboard':
        return <EnterpriseDashboard />;
      case 'crm':
        return <LeadsCRM />;
      case 'rooms':
        return <RoomsView />;
      case 'analytics':
        return <AnalyticsView />;
      case 'agents':
        return <AgentsView />;
      case 'settings':
        return <SettingsView />;
      case 'field':
        return <FieldView />;
      default:
        return <EnterpriseDashboard />;
    }
  };

  return (
    <div 
      className={`app-enterprise ${sidebarOpen ? 'sidebar-open' : 'sidebar-collapsed'}`}
      dir={isRTL ? 'rtl' : 'ltr'}
    >
      {/* Sidebar */}
      <Sidebar activeView={activeView} setActiveView={setActiveView} />

      {/* Main Content */}
      <main className="main-content">
        <TopBar />
        <div className="view-container" key={activeView}>
          {renderView()}
        </div>
      </main>

      {/* Maya AI Chat */}
      <MayaChat />

      {/* Background Effects */}
      <div className="bg-effects">
        <div className="bg-gradient-1" />
        <div className="bg-gradient-2" />
        <div className="bg-grid" />
      </div>
    </div>
  );
}

export default App;
