import React, { useEffect, useState } from 'react';
import { CheckCircle2, Languages, Wrench, Sparkles, Brush } from 'lucide-react';
import useStore from '../../store/useStore';
import { setWorkerLanguage, clockInStaff, getStaffTasks, updateStaffTaskStatus } from '../../services/api';
import './FieldView.css';

const MARKET_LANGUAGES = {
  US: [
    { code: 'en', label: 'English' },
    { code: 'es', label: 'Spanish' },
  ],
  IL: [
    { code: 'he', label: 'Hebrew' },
    { code: 'th', label: 'Thai' },
    { code: 'hi', label: 'Hindi' },
  ],
};

const TASK_ICONS = {
  Cleaning: Brush,
  Maintenance: Wrench,
  'VIP Treats': Sparkles,
};

const FieldView = () => {
  const { fieldLanguage, setFieldLanguage, market, staffProfile, setStaffProfile } = useStore();
  const marketKey = market === 'IL' ? 'IL' : 'US';
  const languages = MARKET_LANGUAGES[marketKey] || MARKET_LANGUAGES.US;
  const [staffIdInput, setStaffIdInput] = useState(staffProfile.staffId || '');
  const [staffNameInput, setStaffNameInput] = useState(staffProfile.name || '');
  const [staffPhoneInput, setStaffPhoneInput] = useState(staffProfile.phone || '');
  const [tasks, setTasks] = useState([]);
  const [loadingTasks, setLoadingTasks] = useState(false);

  useEffect(() => {
    const syncWorkerLanguage = async () => {
      try {
        await setWorkerLanguage(fieldLanguage);
      } catch (error) {
        console.error('Failed to sync worker language:', error);
      }
    };
    syncWorkerLanguage();
  }, [fieldLanguage]);

  useEffect(() => {
    const loadTasks = async () => {
      if (!staffProfile.staffId) return;
      setLoadingTasks(true);
      try {
        const data = await getStaffTasks(staffProfile.staffId);
        setTasks(data);
      } catch (error) {
        console.error('Failed to load tasks:', error);
      } finally {
        setLoadingTasks(false);
      }
    };
    loadTasks();
  }, [staffProfile.staffId]);

  const handleClockIn = async () => {
    if (!staffIdInput) return;
    try {
      const result = await clockInStaff({
        staff_id: staffIdInput,
        name: staffNameInput,
        phone: staffPhoneInput,
      });
      setStaffProfile({ staffId: result.id, name: result.name, phone: result.phone });
      const data = await getStaffTasks(result.id);
      setTasks(data);
    } catch (error) {
      console.error('Clock-in failed:', error);
    }
  };

  const handleTaskUpdate = async (taskId, status) => {
    try {
      await updateStaffTaskStatus(taskId, status);
      const data = await getStaffTasks(staffProfile.staffId);
      setTasks(data);
    } catch (error) {
      console.error('Failed to update task:', error);
    }
  };

  return (
    <div className="field-view">
      <div className="field-header">
        <div className="field-title">
          <h2>Field Ops</h2>
          <span className="field-subtitle">Mobile-first task feed</span>
        </div>
        <div className="field-lang-toggle">
          <Languages size={16} />
          <div className="field-lang-options">
            {languages.map((lang) => (
              <button
                key={lang.code}
                className={`field-lang-btn ${fieldLanguage === lang.code ? 'active' : ''}`}
                onClick={() => setFieldLanguage(lang.code)}
              >
                {lang.label}
              </button>
            ))}
          </div>
        </div>
      </div>

      <div className="field-task-list">
        <div className="field-clockin glass-card">
          <h3>Clock In</h3>
          <div className="clockin-form">
            <input
              type="text"
              placeholder="Scan QR / Staff ID"
              value={staffIdInput}
              onChange={(e) => setStaffIdInput(e.target.value)}
            />
            <input
              type="text"
              placeholder="Name (optional)"
              value={staffNameInput}
              onChange={(e) => setStaffNameInput(e.target.value)}
            />
            <input
              type="tel"
              placeholder="Phone (optional)"
              value={staffPhoneInput}
              onChange={(e) => setStaffPhoneInput(e.target.value)}
            />
            <button className="btn-primary" onClick={handleClockIn}>
              Clock In
            </button>
          </div>
        </div>

        {loadingTasks && <div className="field-loading">Loading tasks...</div>}
        {tasks.map((task) => {
          const Icon = TASK_ICONS[task.task_type] || CheckCircle2;
          return (
            <div key={task.id} className={`field-task-card ${task.status}`}>
              <div className="task-main">
                <div className="task-icon">
                  <Icon size={18} />
                </div>
                <div className="task-info">
                  <span className="task-title">{task.task_type}</span>
                  <span className="task-meta">{task.room}</span>
                </div>
              </div>
              <div className="task-actions">
                <button
                  className="task-action-btn"
                  onClick={() => handleTaskUpdate(task.id, 'on_my_way')}
                  disabled={task.status === 'finished'}
                >
                  On My Way
                </button>
                <button
                  className="task-action-btn"
                  onClick={() => handleTaskUpdate(task.id, 'started')}
                  disabled={task.status === 'finished'}
                >
                  Start Task
                </button>
                <button
                  className="task-action-btn primary"
                  onClick={() => handleTaskUpdate(task.id, 'finished')}
                  disabled={task.status === 'finished'}
                >
                  Finish
                </button>
              </div>
            </div>
          );
        })}
      </div>
    </div>
  );
};

export default FieldView;
