import React, { useState, useRef, useEffect } from 'react';
import {
  MessageCircle, X, Send, User, Plus, Mic,
  Loader2, Minimize2, Maximize2,
  Search, FileText, Image, Zap, Droplets,
} from 'lucide-react';
import useTranslations from '../../hooks/useTranslations';
import useStore from '../../store/useStore';
import { maya } from '../../services/agentOrchestrator';
import './MayaChat.css';

const MAYA_AVATAR_URL = 'https://api.dicebear.com/7.x/personas/svg?seed=MayaManager&backgroundColor=25D366';

const MayaChat = () => {
  const mayaChatOpen = useStore((s) => s.mayaChatOpen);
  const [toast, setToast] = useState(null);
  const toggleMayaChat = useStore((s) => s.toggleMayaChat);
  const {
    mayaMessages,
    addMayaMessage,
    mayaIsTyping,
    setMayaTyping,
    addNotification,
    stats,
  } = useStore();
  const { t, i18n } = useTranslations();
  const lang = useStore((s) => s.lang);
  const isRTL = lang === 'he';

  const [input, setInput] = useState('');
  const [isMinimized, setIsMinimized] = useState(false);
  const messagesEndRef = useRef(null);
  const inputRef = useRef(null);
  const proactiveSentRef = useRef(false);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  useEffect(() => {
    scrollToBottom();
  }, [mayaMessages]);

  useEffect(() => {
    if (mayaChatOpen && inputRef.current) {
      inputRef.current.focus();
    }
  }, [mayaChatOpen]);

  useEffect(() => {
    const onTaskCreated = (e) => {
      const task = e?.detail?.task;
      if (!task) return;
      const staffName = (task.staff_name || task.staffName || '◊ß◊ï◊ë◊ô').trim() || '◊ß◊ï◊ë◊ô';
      const emoji = /alma|◊¢◊ú◊û◊î/i.test(staffName) ? 'üßπ' : /avi|◊ê◊ë◊ô/i.test(staffName) ? '‚ö°' : 'üõ†Ô∏è';
      setToast({ staffName, emoji });
      setTimeout(() => setToast(null), 3500);
    };
    window.addEventListener('maya-task-created', onTaskCreated);
    return () => window.removeEventListener('maya-task-created', onTaskCreated);
  }, []);

  useEffect(() => {
    if (!mayaChatOpen || proactiveSentRef.current) return;
    if (stats?.occupancy?.current >= 87) {
      proactiveSentRef.current = true;
      addMayaMessage({
        role: 'assistant',
        content: t('mayaChat.proactiveOccupancy'),
      });
    }
  }, [mayaChatOpen, stats, addMayaMessage]);

  const handleSend = async () => {
    if (!input.trim() || mayaIsTyping) return;

    const userMessage = input.trim();
    setInput('');

    // Add user message
    addMayaMessage({
      role: 'user',
      content: userMessage,
    });

    // Set typing state
    setMayaTyping(true);

    try {
      // Process with Maya orchestrator
      const result = await maya.processCommand(userMessage);

      // Add Maya's response (ensure content is always a string to avoid [object Object] crash)
      const msg = result?.message ?? result?.displayMessage ?? t('common.taskCompleted');
      addMayaMessage({
        role: 'assistant',
        content: typeof msg === 'string' ? msg : (msg?.content ?? msg?.text ?? JSON.stringify(msg)),
        data: result,
      });

      // Add notification if applicable
      if (result.success) {
        const notifMsg = result?.message ?? result?.displayMessage;
        addNotification({
          type: 'success',
          title: 'Maya',
          message: typeof notifMsg === 'string' ? notifMsg : (notifMsg ? String(notifMsg) : ''),
        });
      }
      const taskAdded = result.taskCreated || result.action === 'add_task' || !!result.task;
      if (taskAdded) {
        addNotification({
          type: 'info',
          title: t('notifications.agentUpdate'),
          message: t('notifications.agentTaskCreated', { defaultValue: 'A new task was created and assigned to staff.' }),
        });
        window.dispatchEvent(new CustomEvent('maya-task-created', { detail: { task: result.task || result.task_data } }));
      }
    } catch (error) {
      addMayaMessage({
        role: 'assistant',
        content: '◊°◊ú◊ô◊ó◊î, ◊ê◊†◊ô ◊û◊™◊ó◊ë◊®◊™ ◊û◊ó◊ì◊©...',
        isError: true,
      });
    } finally {
      setMayaTyping(false);
    }
  };

  const handleQuickAction = (command) => {
    setInput(command);
    setTimeout(() => handleSend(), 100);
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSend();
    }
  };

  const quickActions = [
    { id: 'scan', icon: Search, label: t('mayaChat.quickActions.scan'), command: 'scan new Airbnb leads' },
    { id: 'report', icon: FileText, label: t('mayaChat.quickActions.report'), command: 'generate daily report' },
    { id: 'clean', icon: Droplets, label: t('mayaChat.quickActions.cleaning'), command: 'Tell the cleaner to fix the room' },
    { id: 'post', icon: Image, label: t('mayaChat.quickActions.post'), command: 'create an Instagram marketing post' },
    { id: 'auto', icon: Zap, label: t('mayaChat.quickActions.automation'), command: 'enable automations' },
  ];

  const timeLocale = i18n.language === 'he' ? 'he-IL' : i18n.language === 'el' ? 'el-GR' : 'en-US';

  return (
    <div className="maya-container">
      {/* iOS-style toast - Maya assigned task */}
      {toast && (
        <div className="maya-toast-ios">
          ◊û◊©◊ô◊û◊î ◊ó◊ì◊©◊î ◊ú{toast.staffName} ◊†◊©◊ú◊ó◊î ◊ë◊î◊¶◊ú◊ó◊î! {toast.emoji}
        </div>
      )}
      {mayaChatOpen && (
        <div className="maya-chat-wrapper" dir={isRTL ? 'rtl' : 'ltr'}>
          <div className="maya-iphone-frame">
            {/* Dynamic Island with avatar and online dot */}
            <div className="maya-dynamic-island-notch">
              <div className={`maya-avatar maya-dynamic-avatar ${mayaIsTyping ? 'maya-avatar-thinking' : ''}`}>
                <img src={MAYA_AVATAR_URL} alt="Maya" className="maya-avatar-img" />
                <span className="status-dot status-dot-online" />
              </div>
            </div>
            <div className="maya-chat">
            {/* Top bar - title and actions */}
            <div className="maya-header maya-dark-header">
              <div className="maya-header-text">
                <h3>{t('mayaChat.title')}</h3>
                <span className="maya-status">
                  <span className="status-dot status-dot-online" />
                  {t('mayaChat.role')}
                </span>
              </div>
              <div className="maya-header-actions">
                <button
                  onClick={() => setIsMinimized(!isMinimized)}
                  className="maya-header-btn"
                >
                  {isMinimized ? <Maximize2 size={18} /> : <Minimize2 size={18} />}
                </button>
                <button type="button" onClick={() => toggleMayaChat?.()} className="maya-header-btn">
                  <X size={18} />
                </button>
              </div>
            </div>

            {!isMinimized && (
              <>
                {/* Quick Actions */}
                <div className="maya-quick-actions">
                  {quickActions.map((action) => (
                    <button
                      key={action.id}
                      onClick={() => handleQuickAction(action.command)}
                      className="quick-action-btn"
                    >
                      <action.icon size={16} />
                      <span>{action.label}</span>
                    </button>
                  ))}
                </div>

                {/* Messages */}
                <div className="maya-messages">
                  {mayaMessages.map((message) => (
                    <div
                      key={message.id}
                      className={`maya-message ${message.role}`}
                    >
                      <div className="message-avatar">
                        {message.role === 'assistant' ? (
                          <img src={MAYA_AVATAR_URL} alt="Maya" className="message-avatar-img" />
                        ) : (
                          <User size={16} />
                        )}
                      </div>
                      <div className={`message-content ${message.isError ? 'error' : ''}`}>
                        <p>{typeof message.content === 'string' ? message.content : (message.content?.content ?? message.content?.text ?? String(message.content ?? ''))}</p>
                        {message.data && message.data.leads && (
                          <div className="message-data">
                            <span className="data-label">{t('mayaChat.leadsFoundLabel')}</span>
                            <span className="data-value">{message.data.leads.length}</span>
                          </div>
                        )}
                        <span className="message-time">
                          {new Date(message.timestamp).toLocaleTimeString(timeLocale, {
                            hour: '2-digit',
                            minute: '2-digit',
                          })}
                        </span>
                      </div>
                    </div>
                  ))}

                  {mayaIsTyping && (
                    <div className="maya-message assistant">
                      <div className="message-avatar">
                        <img src={MAYA_AVATAR_URL} alt="Maya" className="message-avatar-img" />
                      </div>
                      <div className="message-content typing">
                        <div className="typing-indicator">
                          <span />
                          <span />
                          <span />
                        </div>
                      </div>
                    </div>
                  )}
                  <div ref={messagesEndRef} />
                </div>

                {/* Dark mode input: gray capsule, + and green send */}
                <div className="maya-input-container maya-dark-input">
                  <div className="maya-input-wrapper">
                    <button type="button" className="maya-input-add-btn" title="Add">
                      <Plus size={24} />
                    </button>
                    <input
                      ref={inputRef}
                      type="text"
                      value={input}
                      onChange={(e) => setInput(e.target.value)}
                      onKeyPress={handleKeyPress}
                      placeholder={t('mayaChat.askPlaceholder')}
                      className="maya-input"
                      disabled={mayaIsTyping}
                    />
                    <button
                      type="button"
                      onClick={handleSend}
                      disabled={!input.trim() || mayaIsTyping}
                      className="maya-send-btn maya-whatsapp-send"
                      aria-label="Send"
                    >
                      {mayaIsTyping ? (
                        <Loader2 size={22} className="animate-spin" />
                      ) : (
                        <Send size={22} />
                      )}
                    </button>
                  </div>
                </div>
              </>
            )}
          </div>
          </div>
          {/* QR placeholder - no library to avoid crashes */}
          <div className="maya-qr-placeholder">
            <span>◊°◊®◊ï◊ß ◊ú◊†◊ô◊î◊ï◊ú ◊û◊î◊ê◊ô◊ô◊§◊ï◊ü</span>
          </div>
        </div>
      )}

      {/* Floating Button */}
      <button
        type="button"
        onClick={() => toggleMayaChat?.()}
        className="maya-button"
        aria-label="Toggle Maya Chat"
      >
        {mayaChatOpen ? (
          <div key="close">
            <X size={28} />
          </div>
        ) : (
          <div key="open">
            <MessageCircle size={28} />
          </div>
        )}
        <div className="maya-button-ring" />
      </button>
    </div>
  );
};

export default MayaChat;
