import React, { useState, useEffect } from 'react';
import { CheckCircle, Clock, DollarSign, Activity, Home, Users, Phone } from 'lucide-react';
import {
  BarChart,
  Bar,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  ResponsiveContainer,
  PieChart,
  Pie,
  Cell,
  Legend,
} from 'recharts';
import { getDashboardSummary, getStatsSummary, getPropertyTasks } from '../../services/api';
import StaffGrid from './StaffGrid';
import AirbnbImporter from './AirbnbImporter';
import GuestChatFeed from '../guest/GuestChatFeed';
import PropertyCreatorModal from './PropertyCreatorModal';

const TASK_CHART_COLORS = ['#3b82f6', '#10b981'];
const PIE_COLORS = ['#3b82f6', '#10b981', '#f59e0b', '#8b5cf6', '#ec4899'];

const TaskTimeline = ({ tasks, loading }) => {
  const mapStatus = (s) => {
    if (!s) return 'pending';
    if (s === 'Done' || s === 'done' || s === 'Completed' || s === 'completed') return 'completed';
    return 'pending';
  };

  const formatTime = (dueAt) => {
    if (!dueAt) return '—';
    try {
      const d = new Date(dueAt);
      return d.toLocaleTimeString('he-IL', { hour: '2-digit', minute: '2-digit' });
    } catch {
      return typeof dueAt === 'string' && dueAt.length >= 16 ? dueAt.slice(11, 16) : '—';
    }
  };

  const list = Array.isArray(tasks) ? tasks.slice(0, 8) : [];

  return (
    <div className="p-6 bg-white dark:bg-gray-800 rounded-3xl shadow-sm border border-gray-100 dark:border-gray-700">
      <div className="flex justify-between items-center mb-6">
        <h3 className="text-xl font-bold text-gray-800 dark:text-gray-100">ציר זמן תפעולי</h3>
        <span className="px-3 py-1 bg-green-50 dark:bg-green-900/30 text-green-600 dark:text-green-400 text-xs font-semibold rounded-full">LIVE</span>
      </div>
      {loading ? (
        <div className="py-8 text-center text-gray-400 dark:text-gray-500">טוען...</div>
      ) : list.length === 0 ? (
        <div className="py-8 text-center text-gray-400 dark:text-gray-500">אין משימות כרגע</div>
      ) : (
        <div className="space-y-6">
          {list.map((task, index) => {
            const status = mapStatus(task.status);
            const displayName = task.property_name || task.room || 'נכס';
            const timeStr = formatTime(task.due_at || task.created_at);
            return (
              <div key={task.id} className="relative flex gap-4">
                {index !== list.length - 1 && (
                  <div className="absolute top-8 right-4 w-0.5 h-12 bg-gray-100 dark:bg-gray-600" aria-hidden />
                )}
                <div
                  className={`z-10 w-8 h-8 rounded-full flex items-center justify-center ${
                    status === 'completed'
                      ? 'bg-green-100 dark:bg-green-900/50 text-green-600 dark:text-green-400'
                      : 'bg-blue-100 dark:bg-blue-900/50 text-blue-600 dark:text-blue-400'
                  }`}
                >
                  {status === 'completed' ? <CheckCircle size={16} /> : <Clock size={16} />}
                </div>
                <div className="flex-1">
                  <div className="flex justify-between">
                    <p className="text-sm font-bold text-gray-800 dark:text-gray-200">{displayName}</p>
                    <span className="text-xs text-gray-400 dark:text-gray-500">{timeStr}</span>
                  </div>
                  <p className="text-xs text-gray-500 dark:text-gray-400">
                    {task.staff_name || '—'}
                  </p>
                </div>
              </div>
            );
          })}
        </div>
      )}
    </div>
  );
};

const KPICard = ({ title, value, icon: Icon, color }) => (
  <div className="bg-white dark:bg-gray-800 p-6 rounded-3xl border border-gray-100 dark:border-gray-700 shadow-sm flex items-center gap-4">
    <div className={`w-12 h-12 rounded-2xl flex items-center justify-center ${color}`}>
      <Icon size={24} />
    </div>
    <div>
      <p className="text-xs text-gray-500 dark:text-gray-400 font-medium">{title}</p>
      <h4 className="text-2xl font-black text-gray-800 dark:text-gray-100">{value}</h4>
    </div>
  </div>
);

const QuickActionStaff = ({ topStaff }) => {
  const callPhone = (phone) => {
    if (!phone) return;
    window.location.href = `tel:${phone.replace(/\D/g, '')}`;
  };
  if (!topStaff?.length) return null;
  return (
    <div className="p-6 bg-white dark:bg-gray-800 rounded-3xl border border-gray-100 dark:border-gray-700 shadow-sm">
      <h3 className="text-lg font-bold text-gray-800 dark:text-gray-100 mb-4 flex items-center gap-2">
        <Phone size={20} className="text-blue-500" />
        קשר מהיר
      </h3>
      <div className="space-y-3">
        {topStaff.map((s, i) => (
          <div
            key={i}
            className="flex justify-between items-center p-3 rounded-xl bg-gray-50 dark:bg-gray-700/50 hover:bg-gray-100 dark:hover:bg-gray-700 transition-colors"
          >
            <span className="font-medium text-gray-800 dark:text-gray-200">{s.name}</span>
            <a
              href={`tel:${(s.phone || '').replace(/\D/g, '')}`}
              onClick={(e) => { e.preventDefault(); callPhone(s.phone); }}
              className="text-blue-600 dark:text-blue-400 font-medium text-sm hover:underline"
            >
              {s.phone}
            </a>
          </div>
        ))}
      </div>
    </div>
  );
};

export default function PremiumDashboard() {
  const [summary, setSummary] = useState(null);
  const [stats, setStats] = useState(null);
  const [tasks, setTasks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [importSuccess, setImportSuccess] = useState(0);
  const [showPropertyModal, setShowPropertyModal] = useState(false);

  useEffect(() => {
    let mounted = true;
    const load = async () => {
      setLoading(true);
      try {
        const [s, st, t] = await Promise.all([
          getDashboardSummary(),
          getStatsSummary(),
          getPropertyTasks(),
        ]);
        if (mounted) {
          setSummary(s);
          setStats(st);
          setTasks(Array.isArray(t) ? t : []);
        }
      } catch (e) {
        if (mounted) {
          setSummary({ revenue: '0₪', active_tasks_count: 0, status: 'Unavailable' });
          setStats({ total_properties: 0, tasks_by_status: { Pending: 0, Done: 0 }, staff_workload: {}, total_capacity: 0, top_staff: [] });
          setTasks([]);
        }
      } finally {
        if (mounted) setLoading(false);
      }
    };
    load();
    return () => { mounted = false; };
  }, [importSuccess]);

  const tasksByStatusData = stats?.tasks_by_status
    ? [
        { name: 'ממתינות', value: stats.tasks_by_status.Pending || 0, fill: TASK_CHART_COLORS[0] },
        { name: 'הושלמו', value: stats.tasks_by_status.Done || 0, fill: TASK_CHART_COLORS[1] },
      ]
    : [];
  const staffWorkloadData = stats?.staff_workload
    ? Object.entries(stats.staff_workload).map(([name, count], i) => ({
        name: name.length > 10 ? name.slice(0, 8) + '…' : name,
        fullName: name,
        משימות: count,
        fill: PIE_COLORS[i % PIE_COLORS.length],
      }))
    : [];

  return (
    <div className="min-h-screen bg-gray-50 dark:bg-gray-900 p-8" dir="rtl">
      <div className="flex justify-between items-end mb-10">
        <div>
          <h1 className="text-3xl font-black text-gray-900 dark:text-white">בוקר טוב, מנהל</h1>
          <p className="text-gray-500 dark:text-gray-400">כל הנכסים שלך תחת שליטה. הנה מה שקורה היום.</p>
        </div>
        <button
          type="button"
          onClick={() => setShowPropertyModal(true)}
          className="bg-black dark:bg-white text-white dark:text-gray-900 px-6 py-3 rounded-2xl font-bold hover:opacity-90 transition-all"
        >
          + הוסף נכס חדש
        </button>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-10">
        <KPICard
          title="הכנסות החודש"
          value={summary?.revenue ?? '₪0'}
          icon={DollarSign}
          color="bg-emerald-50 dark:bg-emerald-900/30 text-emerald-600 dark:text-emerald-400"
        />
        <KPICard
          title="נכסים"
          value={String(stats?.total_properties ?? 0)}
          icon={Home}
          color="bg-blue-50 dark:bg-blue-900/30 text-blue-600 dark:text-blue-400"
        />
        <KPICard
          title="קיבולת כוללת"
          value={String(stats?.total_capacity ?? 0)}
          icon={Users}
          color="bg-violet-50 dark:bg-violet-900/30 text-violet-600 dark:text-violet-400"
        />
        <KPICard
          title="משימות פעילות"
          value={String(stats?.tasks_by_status?.Pending ?? 0)}
          icon={Activity}
          color="bg-amber-50 dark:bg-amber-900/30 text-amber-600 dark:text-amber-400"
        />
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
        <div className="lg:col-span-2 space-y-8">
          <AirbnbImporter onSuccess={() => setImportSuccess((n) => n + 1)} />
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            <div className="bg-white dark:bg-gray-800 rounded-3xl border border-gray-100 dark:border-gray-700 shadow-sm p-6 min-h-[280px]">
              <h3 className="text-lg font-bold text-gray-800 dark:text-gray-100 mb-4">משימות לפי סטטוס</h3>
              {tasksByStatusData.some((d) => d.value > 0) ? (
                <ResponsiveContainer width="100%" height={200}>
                  <BarChart data={tasksByStatusData} layout="vertical" margin={{ top: 5, right: 20, left: 0, bottom: 5 }}>
                    <CartesianGrid strokeDasharray="3 3" stroke="#e5e7eb" />
                    <XAxis type="number" stroke="#6b7280" />
                    <YAxis type="category" dataKey="name" width={70} stroke="#6b7280" />
                    <Tooltip
                      contentStyle={{ backgroundColor: 'rgba(255,255,255,0.95)', border: '1px solid #e5e7eb', borderRadius: 8 }}
                      formatter={(value) => [value, 'משימות']}
                    />
                    <Bar dataKey="value" name="משימות" radius={[0, 4, 4, 0]} />
                  </BarChart>
                </ResponsiveContainer>
              ) : (
                <div className="h-[200px] flex items-center justify-center text-gray-400 dark:text-gray-500">אין משימות</div>
              )}
            </div>
            <div className="bg-white dark:bg-gray-800 rounded-3xl border border-gray-100 dark:border-gray-700 shadow-sm p-6 min-h-[280px]">
              <h3 className="text-lg font-bold text-gray-800 dark:text-gray-100 mb-4">עומס עובדים</h3>
              {staffWorkloadData.length > 0 ? (
                <ResponsiveContainer width="100%" height={200}>
                  <PieChart>
                    <Pie
                      data={staffWorkloadData}
                      dataKey="משימות"
                      nameKey="fullName"
                      cx="50%"
                      cy="50%"
                      outerRadius={70}
                      label={({ fullName, משימות }) => `${fullName}: ${משימות}`}
                    >
                      {staffWorkloadData.map((entry, i) => (
                        <Cell key={i} fill={entry.fill} />
                      ))}
                    </Pie>
                    <Tooltip formatter={(v, n, props) => [`${props.payload.fullName}: ${v} משימות`, '']} />
                    <Legend />
                  </PieChart>
                </ResponsiveContainer>
              ) : (
                <div className="h-[200px] flex items-center justify-center text-gray-400 dark:text-gray-500">אין נתונים</div>
              )}
            </div>
          </div>
          <StaffGrid />
        </div>
        <div className="space-y-8">
          <TaskTimeline tasks={tasks} loading={loading} />
          <QuickActionStaff topStaff={stats?.top_staff} />
          <GuestChatFeed />
        </div>
      </div>

      <PropertyCreatorModal
        isOpen={showPropertyModal}
        onClose={() => setShowPropertyModal(false)}
        onSuccess={() => setImportSuccess((n) => n + 1)}
      />
    </div>
  );
}
