import React, { useState, useEffect } from 'react';
import {
  ArrowRight, Users, Plus, Trash2, Sparkles, UserPlus,
} from 'lucide-react';
import {
  getPropertyStaff,
  addPropertyStaff,
  removePropertyStaff,
  updateProperty,
} from '../../services/api';
import './PropertyManagementDashboard.css';

const ROLES = ['Staff', '◊û◊†◊î◊ú', '◊û◊†◊ß◊î', '◊û◊™◊ó◊ñ◊ß', '◊ì◊ú◊§◊ß', 'Security', 'Concierge'];

export default function PropertyManagementDashboard({ property, onBack }) {
  const [staff, setStaff] = useState([]);
  const [loading, setLoading] = useState(true);
  const [newName, setNewName] = useState('');
  const [newRole, setNewRole] = useState('Staff');
  const [adding, setAdding] = useState(false);
  const [aiAutomation, setAiAutomation] = useState(Boolean(property?.ai_automation_enabled));

  useEffect(() => {
    if (property?.id) {
      setAiAutomation(Boolean(property.ai_automation_enabled));
      loadStaff();
    }
  }, [property?.id, property?.ai_automation_enabled]);

  const loadStaff = async () => {
    if (!property?.id) return;
    setLoading(true);
    try {
      const list = await getPropertyStaff(property.id);
      setStaff(Array.isArray(list) ? list : []);
    } catch {
      setStaff([]);
    } finally {
      setLoading(false);
    }
  };

  const handleAddEmployee = async () => {
    const name = (newName || '').trim();
    if (!name || !property?.id) return;
    setAdding(true);
    try {
      await addPropertyStaff(property.id, { name, role: newRole });
      setNewName('');
      setNewRole('Staff');
      loadStaff();
    } catch (e) {
      window.alert(e?.message || '◊©◊í◊ô◊ê◊î ◊ë◊î◊ï◊°◊§◊™ ◊¢◊ï◊ë◊ì');
    } finally {
      setAdding(false);
    }
  };

  const handleRemoveEmployee = async (staffId) => {
    if (!window.confirm('◊ú◊î◊°◊ô◊® ◊¢◊ï◊ë◊ì ◊û◊î◊†◊õ◊°?')) return;
    try {
      await removePropertyStaff(property.id, staffId);
      loadStaff();
    } catch (e) {
      window.alert(e?.message || '◊©◊í◊ô◊ê◊î ◊ë◊î◊°◊®◊™ ◊¢◊ï◊ë◊ì');
    }
  };

  const handleAiToggle = async (enabled) => {
    if (!property?.id) return;
    setAiAutomation(enabled);
    try {
      await updateProperty(property.id, { ai_automation_enabled: enabled });
    } catch {
      setAiAutomation(!enabled);
      window.alert('◊©◊í◊ô◊ê◊î ◊ë◊¢◊ì◊õ◊ï◊ü ◊ê◊ï◊ò◊ï◊û◊¶◊ô◊î');
    }
  };

  if (!property) return null;

  return (
    <div className="property-management-dashboard p-8 bg-[#FBFBFB] min-h-screen" dir="rtl">
      <button
        type="button"
        onClick={() => onBack && onBack()}
        className="flex items-center gap-2 text-gray-600 hover:text-gray-900 mb-8 font-bold transition-colors"
      >
        <ArrowRight size={20} />
        ◊ó◊ñ◊®◊î ◊ú◊†◊õ◊°◊ô◊ù
      </button>

      <div className="flex flex-wrap gap-8 mb-10">
        <div className="w-32 h-24 rounded-2xl overflow-hidden bg-gray-100 shrink-0">
          {property.mainImage ? (
            <img src={property.mainImage} alt="" className="w-full h-full object-cover" />
          ) : (
            <div className="w-full h-full flex items-center justify-center text-2xl">üè®</div>
          )}
        </div>
        <div>
          <h1 className="property-management-title text-2xl font-black text-gray-900 mb-1">
            {property.name}
          </h1>
          <p className="text-gray-500 text-sm">◊†◊î◊ú ◊¢◊ï◊ë◊ì◊ô◊ù ◊ï◊ê◊ï◊ò◊ï◊û◊¶◊ô◊î</p>
        </div>
      </div>

      {/* AI Automation Toggle */}
      <div className="ai-automation-card bg-white rounded-2xl p-6 shadow-sm border border-gray-100 mb-8">
        <div className="flex items-center justify-between">
          <div className="flex items-center gap-3">
            <div className="w-12 h-12 rounded-xl bg-amber-100 flex items-center justify-center">
              <Sparkles size={24} className="text-amber-600" />
            </div>
            <div>
              <h3 className="font-bold text-gray-900">◊ê◊ï◊ò◊ï◊û◊¶◊ô◊ô◊™ AI</h3>
              <p className="text-sm text-gray-500">◊ó◊ô◊ë◊ï◊® ◊ú÷æAI Assistant ◊û◊î◊°◊®◊í◊ú</p>
            </div>
          </div>
          <button
            type="button"
            role="switch"
            aria-checked={aiAutomation}
            onClick={() => handleAiToggle(!aiAutomation)}
            className={`relative w-14 h-8 rounded-full transition-colors ${
              aiAutomation ? 'bg-amber-500' : 'bg-gray-200'
            }`}
          >
            <span
              className={`absolute top-1 w-6 h-6 rounded-full bg-white shadow transition-transform ${
                aiAutomation ? 'right-1' : 'right-7'
              }`}
            />
          </button>
        </div>
        {aiAutomation && (
          <p className="mt-4 text-sm text-amber-700 bg-amber-50 rounded-xl p-3">
            ‚úì ◊û◊ó◊ï◊ë◊® ◊ú÷æAI Assistant ‚Äì ◊û◊ê◊ô◊î ◊û◊ò◊§◊ú◊™ ◊ë◊î◊ñ◊û◊†◊ï◊™ ◊ï◊î◊ï◊ì◊¢◊ï◊™ ◊ê◊ï◊ò◊ï◊û◊ò◊ô◊™.
          </p>
        )}
      </div>

      {/* Employees */}
      <div className="property-staff-card bg-white rounded-2xl p-6 shadow-sm border border-gray-100">
        <h3 className="font-bold text-gray-900 mb-4 flex items-center gap-2">
          <Users size={20} />
          ◊¢◊ï◊ë◊ì◊ô ◊î◊†◊õ◊°
        </h3>

        <div className="add-employee-form flex flex-wrap gap-3 mb-6">
          <input
            type="text"
            placeholder="◊©◊ù ◊î◊¢◊ï◊ë◊ì"
            value={newName}
            onChange={(e) => setNewName(e.target.value)}
            onKeyDown={(e) => e.key === 'Enter' && handleAddEmployee()}
            className="px-4 py-2.5 rounded-xl border border-gray-200 text-gray-900"
            style={{ minWidth: 140 }}
          />
          <select
            value={newRole}
            onChange={(e) => setNewRole(e.target.value)}
            className="px-4 py-2.5 rounded-xl border border-gray-200 text-gray-900"
          >
            {ROLES.map((r) => (
              <option key={r} value={r}>{r}</option>
            ))}
          </select>
          <button
            type="button"
            onClick={handleAddEmployee}
            disabled={!newName.trim() || adding}
            className="flex items-center gap-2 px-4 py-2.5 rounded-xl bg-gray-900 text-white font-bold hover:bg-gray-800 disabled:opacity-50 disabled:cursor-not-allowed transition-colors"
          >
            <UserPlus size={18} />
            {adding ? '◊û◊ï◊°◊ô◊£...' : '◊î◊ï◊°◊£ ◊¢◊ï◊ë◊ì'}
          </button>
        </div>

        {loading ? (
