import React from 'react';
import { Bot, UserCircle, Sparkles } from 'lucide-react';
import Button from '../ui/Button';
import { AI_ASSISTANT_URL } from '../../utils/constants';
import './Header.css';

/**
 * Header component with language switcher, role selector, and AI Assistant button
 * @param {object} props
 * @param {string} props.lang - Current language
 * @param {function} props.setLang - Language setter
 * @param {string} props.role - Current role
 * @param {function} props.setRole - Role setter
 * @param {object} props.t - Translations object
 */
const Header = ({ lang, setLang, role, setRole, t }) => {
  const handleAIAssistantClick = () => {
    window.open(AI_ASSISTANT_URL, '_blank', 'noopener,noreferrer');
  };

  const isRTL = lang === 'he';

  return (
    <header className="app-header">
      <div className="header-content">
        {/* Language Switcher */}
        <div className="header-section">
          <button
            onClick={() => setLang('he')}
            className={`lang-btn ${lang === 'he' ? 'active' : ''}`}
            aria-label="Switch to Hebrew"
          >
            ðŸ‡®ðŸ‡± HE
          </button>
          <button
            onClick={() => setLang('en')}
            className={`lang-btn ${lang === 'en' ? 'active' : ''}`}
            aria-label="Switch to English"
          >
            ðŸ‡ºðŸ‡¸ EN
          </button>
          <button
            onClick={() => setLang('el')}
            className={`lang-btn ${lang === 'el' ? 'active' : ''}`}
            aria-label="Switch to Greek"
          >
            ðŸ‡¬ðŸ‡· EL
          </button>
        </div>

        {/* AI Assistant Button - Prominent */}
        <div className="header-section header-center">
          <Button
            variant="primary"
            size="lg"
            onClick={handleAIAssistantClick}
            className="ai-assistant-btn"
          >
            <Sparkles size={20} />
            {t.aiAssistant}
          </Button>
        </div>

        {/* Role Selector */}
        <div className="header-section header-right">
          <div className="role-selector">
            <UserCircle size={20} />
            <select
              value={role}
              onChange={(e) => setRole(e.target.value)}
              className="role-select"
            >
              <option value="owner">{t.owner}</option>
              <option value="staff">{t.staff}</option>
              <option value="guest">{t.guest}</option>
            </select>
          </div>
        </div>
      </div>
    </header>
  );
};

export default Header;
