import React, { useState, useEffect } from 'react';
import { CheckCircle, Clock, Wrench, DollarSign, Activity } from 'lucide-react';
import { getDashboardSummary, getTasks } from '../../services/api';
import StaffGrid from './StaffGrid';
import AirbnbImporter from './AirbnbImporter';

const TaskTimeline = ({ tasks, loading }) => {
  const mapStatus = (s) => {
    if (!s) return 'pending';
    if (['finished', 'completed'].includes(s)) return 'completed';
    if (['on_my_way', 'started', 'in_progress', 'assigned'].includes(s)) return 'in_progress';
    return 'pending';
  };

  const formatTime = (dueAt) => {
    if (!dueAt) return '—';
    try {
      const d = new Date(dueAt);
      return d.toLocaleTimeString('he-IL', { hour: '2-digit', minute: '2-digit' });
    } catch {
      return dueAt.slice(11, 16) || '—';
    }
  };

  const typeLabel = (t) => (t === 'cleaning' || t === 'Cleaning' ? 'ניקיון' : 'תחזוקה');

  const list = Array.isArray(tasks) ? tasks.slice(0, 8) : [];

  return (
    <div className="p-6 bg-white rounded-3xl shadow-sm border border-gray-100">
      <div className="flex justify-between items-center mb-6">
        <h3 className="text-xl font-bold text-gray-800">ציר זמן תפעולי</h3>
        <span className="px-3 py-1 bg-green-50 text-green-600 text-xs font-semibold rounded-full">LIVE</span>
      </div>
      {loading ? (
        <div className="py-8 text-center text-gray-400">טוען...</div>
      ) : list.length === 0 ? (
        <div className="py-8 text-center text-gray-400">אין משימות כרגע</div>
      ) : (
        <div className="space-y-6">
          {list.map((task, index) => {
            const status = mapStatus(task.status);
            return (
              <div key={task.id} className="relative flex gap-4">
                {index !== list.length - 1 && (
                  <div className="absolute top-8 right-4 w-0.5 h-12 bg-gray-100" aria-hidden />
                )}
                <div
                  className={`z-10 w-8 h-8 rounded-full flex items-center justify-center ${
                    status === 'completed'
                      ? 'bg-green-100 text-green-600'
                      : status === 'in_progress'
                        ? 'bg-blue-100 text-blue-600'
                        : 'bg-gray-100 text-gray-400'
                  }`}
                >
                  {status === 'completed' ? <CheckCircle size={16} /> : <Clock size={16} />}
                </div>
                <div className="flex-1">
                  <div className="flex justify-between">
                    <p className="text-sm font-bold text-gray-800">{task.room || 'חדר'}</p>
                    <span className="text-xs text-gray-400">{formatTime(task.due_at)}</span>
                  </div>
                  <p className="text-xs text-gray-500">
                    {typeLabel(task.task_type)} • {task.staff_name || '—'}
                  </p>
                </div>
              </div>
            );
          })}
        </div>
      )}
    </div>
  );
};

const KPICard = ({ title, value, icon: Icon, color }) => (
  <div className="bg-white p-6 rounded-3xl border border-gray-100 shadow-sm flex items-center gap-4">
    <div className={`w-12 h-12 rounded-2xl flex items-center justify-center ${color}`}>
      <Icon size={24} />
    </div>
    <div>
      <p className="text-xs text-gray-500 font-medium">{title}</p>
      <h4 className="text-2xl font-black text-gray-800">{value}</h4>
    </div>
  </div>
);

export default function PremiumDashboard() {
  const [summary, setSummary] = useState(null);
  const [tasks, setTasks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [importSuccess, setImportSuccess] = useState(0);

  useEffect(() => {
    let mounted = true;
    const load = async () => {
      setLoading(true);
      try {
        const [s, t] = await Promise.all([getDashboardSummary(), getTasks()]);
        if (mounted) {
          setSummary(s);
          setTasks(Array.isArray(t) ? t : []);
        }
      } catch (e) {
        if (mounted) {
          setSummary({ revenue: '0₪', active_tasks_count: 0, status: 'Unavailable' });
          setTasks([]);
        }
      } finally {
        if (mounted) setLoading(false);
      }
    };
    load();
    return () => { mounted = false; };
  }, []);

  const openTasks = summary?.active_tasks_count ?? 0;
  const issuesCount = summary?.open_issues ?? 0;

  return (
    <div className="min-h-screen bg-gray-50 p-8" dir="rtl">
      <div className="flex justify-between items-end mb-10">
        <div>
          <h1 className="text-3xl font-black text-gray-900">בוקר טוב, מנהל</h1>
          <p className="text-gray-500">כל הנכסים שלך תחת שליטה. הנה מה שקורה היום.</p>
        </div>
        <button
          type="button"
          className="bg-black text-white px-6 py-3 rounded-2xl font-bold hover:bg-gray-800 transition-all"
        >
          + הוסף נכס חדש
        </button>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-10">
        <KPICard
          title="הכנסות החודש"
          value={summary?.revenue ?? '₪0'}
          icon={DollarSign}
          color="bg-emerald-50 text-emerald-600"
        />
        <KPICard
          title="משימות פעילות"
          value={String(openTasks)}
          icon={Activity}
          color="bg-blue-50 text-blue-600"
        />
        <KPICard
          title="תקלות פתוחות"
          value={String(issuesCount)}
          icon={Wrench}
          color="bg-orange-50 text-orange-600"
        />
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
        <div className="lg:col-span-2 space-y-8">
          <AirbnbImporter onSuccess={() => setImportSuccess((n) => n + 1)} />
          <div className="bg-white h-64 rounded-3xl border border-gray-100 shadow-sm flex items-center justify-center text-gray-400">
            [כרטיס נכס פרמיום - תמונת Airbnb גדולה]
          </div>
          <StaffGrid />
        </div>
        <TaskTimeline tasks={tasks} loading={loading} />
      </div>
    </div>
  );
}
