import React, { useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import {
  Bell, X, CheckCircle2, AlertTriangle, Info,
  Zap, MessageCircle, User, Trash2
} from 'lucide-react';
import useStore from '../../store/useStore';
import { wsService } from '../../services/websocket';
import './NotificationCenter.css';

const NotificationCenter = () => {
  const {
    notifications,
    unreadCount,
    notificationsPanelOpen,
    toggleNotifications,
    addNotification,
    markNotificationRead,
    clearNotifications,
  } = useStore();

  // Subscribe to WebSocket events
  useEffect(() => {
    // Connect to WebSocket
    wsService.connect();

    // Subscribe to room requests
    const unsubRoom = wsService.on('room_request', (data) => {
      addNotification({
        type: data.priority === 'high' ? 'warning' : 'info',
        title: 'בקשה מחדר',
        message: data.message,
        data,
      });
    });

    // Subscribe to agent updates
    const unsubAgent = wsService.on('agent_update', (data) => {
      addNotification({
        type: 'success',
        title: 'עדכון סוכן',
        message: data.message,
        data,
      });
    });

    // Subscribe to new leads
    const unsubLead = wsService.on('new_lead', (data) => {
      addNotification({
        type: data.priority === 'hot' ? 'warning' : 'info',
        title: 'ליד חדש!',
        message: data.message,
        data,
      });
    });

    return () => {
      unsubRoom();
      unsubAgent();
      unsubLead();
      wsService.disconnect();
    };
  }, [addNotification]);

  const getIcon = (type) => {
    switch (type) {
      case 'success':
        return <CheckCircle2 size={20} />;
      case 'warning':
        return <AlertTriangle size={20} />;
      case 'error':
        return <X size={20} />;
      default:
        return <Info size={20} />;
    }
  };

  const getTimeAgo = (timestamp) => {
    const now = new Date();
    const then = new Date(timestamp);
    const diffMs = now - then;
    const diffMins = Math.floor(diffMs / 60000);
    const diffHours = Math.floor(diffMs / 3600000);

    if (diffMins < 1) return 'עכשיו';
    if (diffMins < 60) return `לפני ${diffMins} דקות`;
    if (diffHours < 24) return `לפני ${diffHours} שעות`;
    return then.toLocaleDateString('he-IL');
  };

  return (
    <>
      {/* Notification Bell */}
      <motion.button
        whileHover={{ scale: 1.05 }}
        whileTap={{ scale: 0.95 }}
        onClick={toggleNotifications}
        className="notification-bell"
      >
        <Bell size={22} />
        <AnimatePresence>
          {unreadCount > 0 && (
            <motion.span
              initial={{ scale: 0 }}
              animate={{ scale: 1 }}
              exit={{ scale: 0 }}
              className="notification-badge"
            >
              {unreadCount > 99 ? '99+' : unreadCount}
            </motion.span>
          )}
        </AnimatePresence>
      </motion.button>

      {/* Notification Panel */}
      <AnimatePresence>
        {notificationsPanelOpen && (
          <>
            <motion.div
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              exit={{ opacity: 0 }}
              className="notification-backdrop"
              onClick={toggleNotifications}
            />
            <motion.div
              initial={{ opacity: 0, x: 100 }}
              animate={{ opacity: 1, x: 0 }}
              exit={{ opacity: 0, x: 100 }}
              transition={{ type: 'spring', damping: 25 }}
              className="notification-panel glass-dark"
            >
              <div className="panel-header">
                <h3>
                  <Bell size={20} />
                  התראות
                  {unreadCount > 0 && (
                    <span className="unread-count">{unreadCount} חדשות</span>
                  )}
                </h3>
                <div className="panel-actions">
                  {notifications.length > 0 && (
                    <button onClick={clearNotifications} className="clear-btn">
                      <Trash2 size={16} />
                      נקה הכל
                    </button>
                  )}
                  <button onClick={toggleNotifications} className="close-panel-btn">
                    <X size={20} />
                  </button>
                </div>
              </div>

              <div className="notifications-list">
                {notifications.length === 0 ? (
                  <div className="no-notifications">
                    <Bell size={48} />
                    <p>אין התראות חדשות</p>
                  </div>
                ) : (
                  <AnimatePresence>
                    {notifications.map((notification) => (
                      <motion.div
                        key={notification.id}
                        initial={{ opacity: 0, y: -10 }}
                        animate={{ opacity: 1, y: 0 }}
                        exit={{ opacity: 0, x: 100 }}
                        onClick={() => markNotificationRead(notification.id)}
                        className={`notification-item ${notification.type} ${
                          notification.read ? 'read' : 'unread'
                        }`}
                      >
                        <div className={`notification-icon ${notification.type}`}>
                          {getIcon(notification.type)}
                        </div>
                        <div className="notification-content">
                          <div className="notification-header">
                            <span className="notification-title">
                              {notification.title}
                            </span>
                            <span className="notification-time">
                              {getTimeAgo(notification.timestamp)}
                            </span>
                          </div>
                          <p className="notification-message">{notification.message}</p>
                        </div>
                        {!notification.read && <div className="unread-dot" />}
                      </motion.div>
                    ))}
                  </AnimatePresence>
                )}
              </div>

              <div className="panel-footer">
                <div className="connection-status">
                  <span className="status-indicator online" />
                  <span>מחובר לשרת</span>
                </div>
              </div>
            </motion.div>
          </>
        )}
      </AnimatePresence>
    </>
  );
};

export default NotificationCenter;
