import React from 'react';
import { motion } from 'framer-motion';
import {
  LayoutDashboard, Users, MessageCircle, Settings,
  Building2, BarChart3, Shield, Zap, ChevronLeft,
  ChevronRight, Sparkles, ExternalLink, Home
} from 'lucide-react';
import useTranslations from '../../hooks/useTranslations';
import useStore from '../../store/useStore';
import { AI_ASSISTANT_URL } from '../../utils/constants';
import './Sidebar.css';

const menuItems = [
  { id: 'dashboard', icon: LayoutDashboard, labelKey: 'nav.dashboard' },
  { id: 'crm', icon: Users, labelKey: 'nav.crm' },
  { id: 'rooms', icon: Building2, labelKey: 'nav.rooms' },
  { id: 'analytics', icon: BarChart3, labelKey: 'nav.analytics' },
  { id: 'agents', icon: Zap, labelKey: 'nav.agents' },
  { id: 'settings', icon: Settings, labelKey: 'nav.settings' },
  { id: 'field', icon: MessageCircle, labelKey: 'nav.field' },
];

const Sidebar = ({ activeView, setActiveView }) => {
  const { sidebarOpen, toggleSidebar, lang, role } = useStore();
  const isRTL = lang === 'he';
  const { t } = useTranslations();

  const roleNav = {
    host: ['dashboard', 'crm', 'analytics'],
    operator: ['dashboard', 'rooms', 'analytics', 'agents', 'settings'],
    field: ['field'],
  };

  const visibleItems = menuItems.filter((item) =>
    (roleNav[role] || []).includes(item.id)
  );

  const handleAIAssistantClick = () => {
    window.open(AI_ASSISTANT_URL, '_blank', 'noopener,noreferrer');
  };

  return (
    <motion.aside
      initial={false}
      animate={{ width: sidebarOpen ? 260 : 80 }}
      className={`sidebar glass-dark ${sidebarOpen ? 'open' : 'collapsed'}`}
    >
      {/* Logo */}
      <div className="sidebar-logo">
        <div className="logo-icon">
          <div className="logo-icon-stack">
            <Home size={26} />
            <Sparkles size={14} className="logo-sparkle" />
          </div>
        </div>
        <motion.span
          initial={false}
          animate={{ opacity: sidebarOpen ? 1 : 0, width: sidebarOpen ? 'auto' : 0 }}
          className="logo-text"
        >
          {t('branding.name')}
        </motion.span>
      </div>

      {/* Navigation */}
      <nav className="sidebar-nav">
        {visibleItems.map((item) => (
          <motion.button
            key={item.id}
            whileHover={{ x: isRTL ? -4 : 4 }}
            whileTap={{ scale: 0.98 }}
            onClick={() => setActiveView(item.id)}
            className={`nav-item ${activeView === item.id ? 'active' : ''}`}
          >
            <item.icon size={22} />
            <motion.span
              initial={false}
              animate={{ opacity: sidebarOpen ? 1 : 0, width: sidebarOpen ? 'auto' : 0 }}
              className="nav-label"
            >
              {t(item.labelKey)}
            </motion.span>
            {activeView === item.id && (
              <div className="active-indicator" />
            )}
          </motion.button>
        ))}
      </nav>

      {/* AI Assistant Button */}
      <div className="sidebar-action">
        <motion.button
          whileHover={{ scale: 1.02 }}
          whileTap={{ scale: 0.98 }}
          onClick={handleAIAssistantClick}
          className="ai-assistant-sidebar-btn"
        >
          <Sparkles size={20} />
          <motion.span
            initial={false}
            animate={{ opacity: sidebarOpen ? 1 : 0, width: sidebarOpen ? 'auto' : 0 }}
          >
            {t('sidebar.aiAssistant')}
          </motion.span>
          {sidebarOpen && <ExternalLink size={14} />}
        </motion.button>
      </div>

      {/* Toggle Button */}
      <motion.button
        whileHover={{ scale: 1.1 }}
        whileTap={{ scale: 0.9 }}
        onClick={toggleSidebar}
        className="sidebar-toggle"
      >
        {sidebarOpen ? (
          isRTL ? <ChevronRight size={18} /> : <ChevronLeft size={18} />
        ) : (
          isRTL ? <ChevronLeft size={18} /> : <ChevronRight size={18} />
        )}
      </motion.button>

      {/* Security Badge */}
      <div className="sidebar-footer">
        <div className="security-badge">
          <Shield size={16} />
          <motion.span
            initial={false}
            animate={{ opacity: sidebarOpen ? 1 : 0, width: sidebarOpen ? 'auto' : 0 }}
          >
            {t('sidebar.zeroTrust')}
          </motion.span>
        </div>
      </div>
    </motion.aside>
  );
};

export default Sidebar;
