        </motion.div>

        {/* Rooms Grid */}
        <motion.div className="glass-card rooms-card" variants={itemVariants}>
          <div className="card-header">
            <h3><Building2 size={20} /> {t('dashboard.rooms.title')}</h3>
            <span className="room-count">{t('dashboard.rooms.count', { count: safeRooms.length })}</span>
          </div>
