            {safeRooms.slice(0, 6).map((room) => (
              <RoomCard
                key={room.id}
                room={room}
                onViewDetails={() => setSelectedRoom(room)}
                onWhatsApp={async () => {
                  try {
                    await whatsappService.notifyCleaningRequest(room.id, lang);
                  } catch (error) {
                    console.error('Failed to send WhatsApp request:', error);
                  }
                }}
                onToggleStatus={async () => {
                  const nextStatus = getNextStatus(room.status);
                  setRoomStatusLoading(room.id);
                  try {
                    await updateRoomStatus(room.id, { status: nextStatus });
                    updateRoom(room.id, { status: nextStatus });
                  } catch (error) {
                    console.error('Failed to update room status:', error);
                  } finally {
                    setRoomStatusLoading(null);
                  }
                }}
                statusLoading={roomStatusLoading === room.id}
                t={t}
              />
            ))}
          </div>
        </motion.div>

        {/* Agent Performance Chart */}
        <motion.div className="glass-card performance-chart" variants={itemVariants}>
          <div className="card-header">
            <h3><Target size={20} /> {t('dashboard.charts.agentsPerformance')}</h3>
          </div>
          <div className="chart-container">
            <ResponsiveContainer width="100%" height={220}>
              <BarChart data={agentPerformance} layout="vertical">
                <CartesianGrid strokeDasharray="3 3" stroke="rgba(255,255,255,0.1)" />
                <XAxis type="number" stroke="#64748b" />
                <YAxis type="category" dataKey="name" stroke="#64748b" width={80} />
                <Tooltip
                  contentStyle={{
                    background: 'rgba(15, 23, 42, 0.9)',
                    border: '1px solid rgba(255,255,255,0.1)',
                    borderRadius: '12px',
                    color: '#fff',
                  }}
                />
                <Bar dataKey="tasks" fill="#3b82f6" radius={[0, 4, 4, 0]} name={t('dashboard.charts.tasks')} />
                <Bar dataKey="success" fill="#10b981" radius={[0, 4, 4, 0]} name={t('dashboard.charts.successes')} />
              </BarChart>
            </ResponsiveContainer>
          </div>
        </motion.div>
      </div>

      {/* Daily Report Modal */}
      <ReportModal
        report={reportData}
        isOpen={isReportOpen}
        onClose={() => setReportDataLocal(null)}
        t={t}
      />

      {/* Room Details Modal */}
      {selectedRoom && (
        <div
          className="report-modal-backdrop"
          onClick={() => setSelectedRoom(null)}
        >
          <div
            className="report-modal glass-card room-details-modal"
            onClick={(e) => e.stopPropagation()}
          >
            <div className="report-header">
              <h2>
                <Building2 size={22} />
                {t('dashboard.rooms.roomLabel', { number: selectedRoom.id })}
              </h2>
              <button onClick={() => setSelectedRoom(null)} className="close-btn">×</button>
            </div>

            <div className="report-content">
              <div className="room-details-grid">
                <div className="room-detail-item">
                  <span className="detail-label">{t('dashboard.rooms.title')}</span>
                  <span className="detail-value">{selectedRoom.name}</span>
                </div>
                <div className="room-detail-item">
                  <span className="detail-label">{t('dashboard.charts.roomStatus')}</span>
                  <span className="detail-value">{t(`dashboard.rooms.status.${selectedRoom.status}`)}</span>
                </div>
                <div className="room-detail-item">
                  <span className="detail-label">{t('roles.guest')}</span>
                  <span className="detail-value">{selectedRoom.guest || '-'}</span>
                </div>
                <div className="room-detail-item">
                  <span className="detail-label">{t('dashboard.rooms.checkout')}</span>
                  <span className="detail-value">{selectedRoom.checkOut || '-'}</span>
                </div>
              </div>
            </div>

            <div className="report-footer room-details-actions">
              <button
                className="btn-secondary"
                onClick={() => setSelectedRoom(null)}
              >
                {t('common.close')}
              </button>
              <button
                className="btn-primary"
                disabled={roomActionLoading}
                onClick={async () => {
                  setRoomActionLoading(true);
                  try {
                    await updateRoomStatus(selectedRoom.id, { status: 'cleaning' });
                    updateRoom(selectedRoom.id, { status: 'cleaning' });
                  } catch (error) {
                    console.error('Failed to trigger cleaning:', error);
                  } finally {
                    setRoomActionLoading(false);
                    setSelectedRoom(null);
                  }
                }}
              >
                <Brush size={18} />
                {t('dashboard.rooms.sendCleaner')}
              </button>
            </div>
          </div>
        </div>
      )}
    </motion.div>
  );
};

// Stat Card Component
const normalizeReport = (report) => {
  if (!report || typeof report !== 'object') {
    return null;
  }

  const leadsCaptured = report.roi_metrics?.leads_captured ?? 0;
  const cleaningsTriggered = report.roi_metrics?.cleanings_triggered ?? 0;
  const interactionCount = Number.isFinite(report.automation_interactions)
    ? report.automation_interactions
    : leadsCaptured + cleaningsTriggered;
  const humanHoursSaved = Math.round(((interactionCount * 10) / 60) * 10) / 10;

  return {
    date: report.date || new Date().toISOString().split('T')[0],
    roi_metrics: {
      hours_saved: report.roi_metrics?.hours_saved ?? 0,
      cleanings_triggered: cleaningsTriggered,
      leads_captured: leadsCaptured,
      human_hours_saved: humanHoursSaved,
    },
    recommendations: Array.isArray(report.recommendations)
      ? report.recommendations.filter(Boolean).map((rec) => String(rec))
      : [],
    agent_performance: Array.isArray(report.agent_performance) ? report.agent_performance : [],
    savings: {
      automation_rate: report.savings?.automation_rate ?? 0,
    },
  };
};

const buildManagementAlerts = ({ leads, notifications, t }) => {
  const now = Date.now();
  const toTime = (value) => {
    const ts = new Date(value).getTime();
    if (!Number.isFinite(ts)) return t('dashboard.alerts.justNow');
    const diffMin = Math.floor((now - ts) / 60000);
    if (diffMin < 1) return t('dashboard.alerts.justNow');
    if (diffMin < 60) return t('dashboard.alerts.minutesAgo', { count: diffMin });
    const diffHours = Math.floor(diffMin / 60);
    return t('dashboard.alerts.hoursAgo', { count: diffHours });
  };

  const leadNoReply = leads
    .filter((lead) => lead.status === 'contacted' && lead.createdAt)
    .filter((lead) => now - new Date(lead.createdAt).getTime() > 2 * 60 * 60 * 1000)
    .map((lead) => ({
      id: `lead-no-reply-${lead.id}`,
      level: 'high',
      title: t('dashboard.alerts.leadNoReply', { name: lead.contact || lead.name }),
      subtitle: lead.name,
      time: toTime(lead.createdAt),
    }));

  const bookingAlerts = leads
    .filter((lead) => ['booked', 'converted'].includes(lead.status))
    .map((lead) => ({
      id: `booking-${lead.id}`,
      level: 'medium',
      title: t('dashboard.alerts.bookingConfirmed'),
      subtitle: lead.name,
      time: toTime(lead.createdAt),
    }));

  const notificationAlerts = notifications
    .filter((n) => n.type === 'warning' || n.type === 'success')
    .slice(0, 3)
    .map((n) => ({
      id: `notification-${n.id}`,
      level: n.type === 'warning' ? 'high' : 'low',
      title: n.title || t('dashboard.alerts.notification'),
      subtitle: n.message || '',
      time: toTime(n.timestamp),
    }));

  return [...leadNoReply, ...bookingAlerts, ...notificationAlerts].slice(0, 5);
};

const ReportModal = ({ report, isOpen, onClose, t }) => {
  const safeReport = report || normalizeReport({});
  if (typeof document === 'undefined') return null;

  return createPortal(
    <div
      className={`report-modal-backdrop ${isOpen ? 'is-open' : 'is-closed'}`}
      onClick={isOpen ? onClose : undefined}
      aria-hidden={!isOpen}
    >
      <div className="report-modal glass-card" onClick={(e) => e.stopPropagation()}>
        <div className="report-header">
          <h2><Sparkles size={24} /> {t('dashboard.report.title')} - {safeReport.date}</h2>
          <button onClick={onClose} className="close-btn">×</button>
        </div>

        <div className="report-content">
          <div className="report-stats">
            <div className="report-stat">
              <span className="stat-value text-gradient">{safeReport.roi_metrics.hours_saved}</span>
              <span className="stat-label">{t('dashboard.report.hoursSaved')}</span>
            </div>
            <div className="report-stat">
              <span className="stat-value" style={{ color: '#10b981' }}>{safeReport.roi_metrics.cleanings_triggered}</span>
              <span className="stat-label">{t('dashboard.report.cleaningsTriggered')}</span>
            </div>
            <div className="report-stat">
              <span className="stat-value" style={{ color: '#3b82f6' }}>{safeReport.roi_metrics.leads_captured}</span>
              <span className="stat-label">{t('dashboard.report.leadsCaptured')}</span>
            </div>
          </div>

          <div className="report-section">
            <h4>{t('dashboard.report.recommendationsTitle')}</h4>
            <ul className="recommendations-list">
              {safeReport.recommendations.map((rec, i) => (
                <li key={i}><CheckCircle2 size={16} /> {rec}</li>
              ))}
            </ul>
          </div>

          <div className="report-section">
            <h4>{t('dashboard.report.agentSummaryTitle')}</h4>
            <div className="agent-summary">
              {safeReport.agent_performance.map((agent, idx) => (
                <div key={agent.type || agent.name || idx} className="agent-summary-item">
                  <span className="agent-name">{agent.name}</span>
                  <span className="agent-tasks">{agent.tasks_today} {t('dashboard.charts.tasks')}</span>
                  <span className={`agent-status-badge ${agent.status}`}>{agent.status}</span>
                </div>
              ))}
            </div>
          </div>
        </div>

        <div className="report-footer">
          <p className="automation-message">
            <Zap size={16} />
            {t('dashboard.report.automationMessage', { rate: safeReport.savings.automation_rate })}
          </p>
        </div>
      </div>
    </div>,
    document.body
  );
};

const StatCard = ({ title, value, subtitle, trend, icon: Icon, color, image, trendLabel, t }) => {
  const translate = t || ((key) => key);
  const isPositive = trend >= 0;

  return (
    <motion.div
      whileHover={{ y: -4, scale: 1.02 }}
      className={`stat-card glass-card stat-${color}`}
      style={{
        backgroundImage: image
          ? `linear-gradient(130deg, rgba(15,23,42,0.85), rgba(15,23,42,0.4)), url(${image})`
          : undefined,
      }}
    >
      <div className="stat-overlay" />
      <div className="stat-header">
        <div className="stat-icon">
          <Icon size={24} />
        </div>
        <div className={`stat-trend ${isPositive ? 'positive' : 'negative'}`}>
          {isPositive ? <ArrowUpRight size={16} /> : <ArrowDownRight size={16} />}
          <span>{Math.abs(trend)}%</span>
        </div>
      </div>
      <div className="stat-content">
        <span className="stat-label">{title}</span>
        <span className="stat-value">{value}</span>
        <span className="stat-subtitle">{subtitle || trendLabel || translate('dashboard.trendLabelDefault')}</span>
      </div>
    </motion.div>
  );
};

// Agent Status Item
const AgentStatusItem = ({ id, agent, t }) => {
  const translate = t || ((key) => key);
  const agentNames = {
    scraper: { name: 'Scraper Agent', icon: '🔍' },
    marketing: { name: 'Marketing Agent', icon: '📣' },
    creative: { name: 'Creative Agent', icon: '🎨' },
    sales: { name: 'Sales Manager', icon: '💼' },
    operations: { name: 'Operations Agent', icon: '⚙️' },
  };

  const info = agentNames[id] || { name: id, icon: '🤖' };

  return (
    <motion.div
      whileHover={{ x: 4 }}
      className="agent-item"
    >
      <div className="agent-info">
        <span className="agent-icon">{info.icon}</span>
        <span className="agent-name">{info.name}</span>
      </div>
      <div className="agent-meta">
        <span className="agent-tasks">{agent.taskCount} {translate('dashboard.charts.tasks')}</span>
        <span className={`status-badge ${agent.status}`}>
          {agent.status === 'online'
            ? translate('status.online')
            : agent.status === 'busy'
              ? translate('status.busy')
              : translate('status.offline')}
        </span>
      </div>
    </motion.div>
  );
};

// Room Card Component
const RoomCard = ({ room, onViewDetails, onWhatsApp, onToggleStatus, statusLoading, t }) => {
  const translate = t || ((key) => key);
  const statusColors = {
    occupied: '#10b981',
    available: '#3b82f6',
    cleaning: '#f59e0b',
    maintenance: '#ef4444',
  };

  const statusLabels = {
    occupied: translate('dashboard.rooms.status.occupied'),
    available: translate('dashboard.rooms.status.available'),
    cleaning: translate('dashboard.rooms.status.cleaning'),
    maintenance: translate('dashboard.rooms.status.maintenance'),
  };

  const statusIcon = room.status === 'available' ? ToggleRight : ToggleLeft;

  return (
    <motion.div
      whileHover={{ scale: 1.02 }}
      className="room-card airbnb-room"
    >
      <div
        className="room-image"
        style={{
          backgroundImage: `linear-gradient(180deg, rgba(2,6,23,0.2), rgba(2,6,23,0.85)), url(${room.image})`,
        }}
      >
        <div className="room-status-pill" style={{ background: statusColors[room.status] }}>
          {statusLabels[room.status]}
        </div>
      </div>
      <div className="room-body">
        <div className="room-meta">
          <span className="room-number">{translate('dashboard.rooms.roomLabel', { number: room.id })}</span>
          <span className="room-name">{room.name}</span>
          {room.guest && <span className="room-guest">{room.guest}</span>}
        </div>
        <div className="room-actions">
          <span className="room-actions-label">{translate('dashboard.rooms.ownerActions')}</span>
          <button className="room-action-btn whatsapp" onClick={onWhatsApp}>
            <MessageCircle size={16} />
            {translate('dashboard.rooms.sendCleaner')}
          </button>
          <button className="room-action-btn toggle" onClick={onToggleStatus} disabled={statusLoading}>
            {React.createElement(statusIcon, { size: 16 })}
            {statusLoading ? translate('common.loading') : translate('dashboard.rooms.status.toggle')}
          </button>
          <button className="room-action-btn secondary" onClick={onViewDetails}>
            <ExternalLink size={16} />
            {translate('common.viewDetails')}
          </button>
        </div>
      </div>
    </motion.div>
  );
};

export default EnterpriseDashboard;
