import React, { useEffect, useState, useCallback } from 'react';
import { CheckCircle2, Flag, Loader2, BedDouble, User, Clock, AlertCircle } from 'lucide-react';
import { API_URL } from '../../utils/apiClient';
import './WorkerView.css';

const MAYA_AVATAR = 'https://api.dicebear.com/7.x/personas/svg?seed=MayaManager&backgroundColor=25D366';

const STATUS_LABELS = {
  Pending: { label: '◊û◊û◊™◊ô◊ü', color: '#f59e0b' },
  Accepted: { label: '◊î◊™◊ß◊ë◊ú◊î', color: '#3b82f6' },
  Done: { label: '◊î◊ï◊©◊ú◊û◊î', color: '#22c55e' },
  pending: { label: '◊û◊û◊™◊ô◊ü', color: '#f59e0b' },
  assigned: { label: '◊©◊ï◊ô◊ö', color: '#3b82f6' },
};

const WorkerView = () => {
  const [task, setTask] = useState(null);
  const [loading, setLoading] = useState(true);
  const [actionLoading, setActionLoading] = useState(false);
  const [status, setStatus] = useState(null);
  const [error, setError] = useState(null);
  const [workerName, setWorkerName] = useState('');
  const [nameSubmitted, setNameSubmitted] = useState(false);

  const params = new URLSearchParams(window.location.search);
  const taskId = params.get('task_id') || params.get('taskId') || params.get('id');
  const workerParam = params.get('worker') || params.get('staff');

  useEffect(() => {
    if (workerParam) {
      setWorkerName(workerParam);
      setNameSubmitted(true);
    }
  }, [workerParam]);

  const fetchTask = useCallback(async () => {
    if (!taskId) {
      setError('◊ú◊ê ◊°◊ï◊§◊ß ◊û◊°◊§◊® ◊û◊©◊ô◊û◊î. ◊ê◊†◊ê ◊§◊†◊î ◊ú◊û◊†◊î◊ú.');
      setLoading(false);
      return;
    }
    setLoading(true);
    try {
      const res = await fetch(`${API_URL}/api/property-tasks/${taskId}`);
      const data = await res.json();
      if (res.ok && data.task) {
        setTask(data.task);
        setStatus(data.task.status);
      } else {
        setError(data.error || '◊î◊û◊©◊ô◊û◊î ◊ú◊ê ◊†◊û◊¶◊ê◊î.');
      }
    } catch {
      setError('◊©◊í◊ô◊ê◊™ ◊ó◊ô◊ë◊ï◊® ◊ú◊©◊®◊™.');
    } finally {
      setLoading(false);
    }
  }, [taskId]);

  useEffect(() => {
    fetchTask();
  }, [fetchTask]);

  const updateStatus = async (newStatus) => {
    if (!taskId || actionLoading) return;
    setActionLoading(true);
    try {
      const res = await fetch(`${API_URL}/api/property-tasks/${taskId}`, {
        method: 'PATCH',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ status: newStatus }),
      });
      const data = await res.json();
      if (res.ok) {
        setStatus(data.task?.status || newStatus);
        await fetchTask();
      } else {
