/**
 * WorkerView ‚Äî Mobile-first worker portal
 * Route:  /worker/:workerName   (e.g. /worker/levikobi)
 * Shows all pending tasks assigned to this worker.
 * PATCH /api/property-tasks/:id  { status: 'completed' }
 */
import React, { useState, useEffect, useCallback, useRef } from 'react';
import { API_URL } from '../utils/apiClient';

/* ‚îÄ‚îÄ‚îÄ colour tokens ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ */
const C = {
  green:   '#25D366',
  darkGreen:'#075E54',
  red:     '#ef4444',
  amber:   '#f59e0b',
  blue:    '#3b82f6',
  bg:      '#0f1e2e',
  card:    '#ffffff',
  textDim: '#94a3b8',
};

/* ‚îÄ‚îÄ‚îÄ helpers ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ */
const REFRESH_INTERVAL = 30_000;   // 30 s auto-refresh

function getWorkerName() {
  // /worker/levikobi ‚Üí "levikobi"
  const parts = window.location.pathname.split('/').filter(Boolean);
  return parts[1] ? decodeURIComponent(parts[1]) : null;
}

function fmtTime(iso) {
  if (!iso) return '';
  try {
    return new Date(iso).toLocaleTimeString('he-IL', { hour: '2-digit', minute: '2-digit' });
  } catch { return ''; }
}
function fmtDate(iso) {
  if (!iso) return '';
  try {
    return new Date(iso).toLocaleDateString('he-IL', { day: '2-digit', month: '2-digit', year: '2-digit' });
  } catch { return ''; }
}

function isPending(s = '') {
  return ['pending', 'Pending', 'assigned', 'Assigned'].includes(s);
}

/* ‚îÄ‚îÄ‚îÄ Toast ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ */
function Toast({ msg, onClose }) {
  useEffect(() => {
    const t = setTimeout(onClose, 2800);
    return () => clearTimeout(t);
  }, [onClose]);

  return (
    <div style={{
      position: 'fixed', bottom: 28, left: '50%', transform: 'translateX(-50%)',
      background: C.green, color: '#fff', borderRadius: 30,
      padding: '12px 24px', fontWeight: 700, fontSize: 15,
      boxShadow: '0 6px 28px rgba(0,0,0,0.35)',
      zIndex: 9999, whiteSpace: 'nowrap',
      animation: 'wvSlideUp .3s ease',
    }}>
      {msg}
    </div>
  );
}

/* ‚îÄ‚îÄ‚îÄ TaskCard ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ */
function TaskCard({ task, workerName, onDone }) {
  const [loading, setLoading] = useState(false);
  const [done,    setDone]    = useState(false);

  const room    = task.property_name || task.room || `◊ó◊ì◊® ${task.property_id || '?'}`;
  const desc    = task.description  || task.content || task.task_type || '◊û◊©◊ô◊û◊î';
  const staff   = task.staff_name   || workerName || '';
  const isPend  = isPending(task.status) && !done;

  const markDone = async () => {
    if (loading || done) return;
    setLoading(true);
    try {
      const res = await fetch(`${API_URL}/api/property-tasks/${task.id}`, {
        method:  'PATCH',
        headers: { 'Content-Type': 'application/json' },
        body:    JSON.stringify({ status: 'completed' }),
      });
      if (res.ok) {
        setDone(true);
        onDone(task.id);
      } else {
        const d = await res.json().catch(() => ({}));
        alert('◊©◊í◊ô◊ê◊î: ' + (d.error || res.status));
      }
    } catch {
      alert('◊©◊í◊ô◊ê◊™ ◊ó◊ô◊ë◊ï◊® ‚Äî ◊†◊°◊î ◊©◊ï◊ë');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={{
      background: done ? '#f0fdf4' : C.card,
      border: done ? `2px solid ${C.green}` : '2px solid #e2e8f0',
      borderRadius: 20,
      marginBottom: 16,
      overflow: 'hidden',
      boxShadow: '0 4px 20px rgba(0,0,0,0.12)',
      transition: 'border-color .3s',
    }}>
      {/* Card header bar */}
      <div style={{
        background: done ? C.green : C.darkGreen,
        padding: '10px 16px',
        display: 'flex', alignItems: 'center', justifyContent: 'space-between',
      }}>
        <span style={{
          background: 'rgba(255,255,255,0.2)', color: '#fff',
          fontSize: 11, fontWeight: 700, padding: '3px 10px', borderRadius: 20,
          letterSpacing: '0.05em',
        }}>
          {done ? '‚úÖ ◊î◊ï◊©◊ú◊ù' : '◊û◊û◊™◊ô◊ü ◊ú◊ò◊ô◊§◊ï◊ú'}
        </span>
        <span style={{ color: 'rgba(255,255,255,0.75)', fontSize: 11 }}>
          {fmtDate(task.created_at)} ¬∑ {fmtTime(task.created_at)}
        </span>
      </div>

      {/* Card body */}
      <div style={{ padding: '16px 18px 4px', direction: 'rtl' }}>

        {/* Room ‚Äî large */}
        <div style={{ display: 'flex', alignItems: 'center', gap: 10, marginBottom: 12 }}>
          <div style={{
            width: 46, height: 46, borderRadius: 12,
            background: `${C.darkGreen}18`,
            display: 'flex', alignItems: 'center', justifyContent: 'center',
            fontSize: 22, flexShrink: 0,
          }}>üõè</div>
          <div>
            <div style={{ fontSize: 11, color: C.textDim, fontWeight: 600, textTransform: 'uppercase', letterSpacing: '0.06em' }}>
              ◊ó◊ì◊® / ◊†◊õ◊°
            </div>
            <div style={{ fontSize: 22, fontWeight: 800, color: '#0f172a', lineHeight: 1.15 }}>
              {room}
            </div>
          </div>
        </div>

        {/* Task description */}
        <div style={{
          background: '#f8fafc', borderRadius: 12, padding: '10px 14px',
          marginBottom: 10, fontSize: 14, color: '#1e293b', lineHeight: 1.55,
        }}>
          {desc}
        </div>

        {/* Staff row */}
        {staff && (
          <div style={{ fontSize: 12, color: C.textDim, marginBottom: 14 }}>
            üë§ ◊©◊ï◊ô◊ö ◊ú: <strong style={{ color: '#374151' }}>{staff}</strong>
          </div>
        )}
      </div>

      {/* Action area */}
      <div style={{ padding: '4px 18px 18px', direction: 'rtl' }}>
        {done ? (
          <div style={{
            background: '#dcfce7', borderRadius: 14,
            padding: '12px 16px', textAlign: 'center',
            color: '#15803d', fontWeight: 700, fontSize: 15,
          }}>
            ‚úÖ ◊û◊©◊ô◊û◊î ◊î◊ï◊©◊ú◊û◊î ‚Äî ◊õ◊ú ◊î◊õ◊ë◊ï◊ì!
          </div>
        ) : (
          <button
            onClick={markDone}
            disabled={loading}
            style={{
              width: '100%',
              padding: '15px 0',
              background: loading ? '#86efac' : `linear-gradient(135deg, ${C.green}, #16a34a)`,
              border: 'none', borderRadius: 16,
              color: '#fff', fontWeight: 800, fontSize: 16,
              cursor: loading ? 'wait' : 'pointer',
              display: 'flex', alignItems: 'center', justifyContent: 'center', gap: 8,
              boxShadow: '0 4px 18px rgba(37,211,102,0.4)',
              transition: 'transform .15s, box-shadow .15s',
              letterSpacing: '0.03em',
            }}
            onMouseDown={(e) => { e.currentTarget.style.transform = 'scale(0.97)'; }}
            onMouseUp={(e)   => { e.currentTarget.style.transform = 'scale(1)'; }}
          >
            {loading
              ? <>‚è≥ ◊©◊ï◊û◊®...</>
              : <>üèÅ ◊°◊û◊ü ◊õ◊î◊ï◊©◊ú◊ù</>}
          </button>
        )}
      </div>
    </div>
  );
}

/* ‚îÄ‚îÄ‚îÄ Main component ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ */
export default function WorkerView() {
  const workerName = getWorkerName() || '◊¢◊ï◊ë◊ì';

  const [tasks,    setTasks]   = useState([]);
  const [loading,  setLoading] = useState(true);
  const [spinning, setSpin]    = useState(false);
  const [toast,    setToast]   = useState(null);
  const [lastSync, setLastSync]= useState(null);
  const timerRef               = useRef(null);

  /* ‚îÄ‚îÄ fetch ‚îÄ‚îÄ */
  const fetchTasks = useCallback(async (silent = false) => {
    if (!silent) setLoading(true);
    setSpin(true);
    try {
      const res  = await fetch(`${API_URL}/api/property-tasks`);
      if (!res.ok) throw new Error(res.status);
      const data = await res.json();
      const all  = Array.isArray(data) ? data : [];
      // Only pending/assigned (not done/completed)
      setTasks(all.filter((t) => isPending(t.status)));
      setLastSync(new Date());
    } catch {
      /* keep stale data */
    } finally {
      setLoading(false);
      setSpin(false);
    }
  }, []);

  useEffect(() => {
    fetchTasks();
    timerRef.current = setInterval(() => fetchTasks(true), REFRESH_INTERVAL);
    return () => clearInterval(timerRef.current);
  }, [fetchTasks]);

  /* ‚îÄ‚îÄ after worker marks done ‚îÄ‚îÄ */
  const handleDone = useCallback((id) => {
    setToast('‚úÖ ◊û◊©◊ô◊û◊î ◊î◊ï◊©◊ú◊û◊î ◊ë◊î◊¶◊ú◊ó◊î!');
    // remove after a short delay so the card stays visible with ‚úÖ
    setTimeout(() => {
      setTasks((prev) => prev.filter((t) => t.id !== id));
    }, 2500);
  }, []);

  const pendingCount = tasks.length;

  /* ‚îÄ‚îÄ render ‚îÄ‚îÄ */
  return (
    <>
      {/* Keyframe injected once */}
      <style>{`
        @keyframes wvSlideUp {
          from { opacity:0; transform:translateX(-50%) translateY(12px); }
          to   { opacity:1; transform:translateX(-50%) translateY(0); }
        }
        @keyframes wvSpin {
          to { transform: rotate(360deg); }
        }
        body { margin:0; background:${C.bg}; }
      `}</style>

      <div style={{
        minHeight: '100vh',
        background: `linear-gradient(160deg, ${C.bg} 0%, #1a2f4a 55%, #0f3460 100%)`,
        fontFamily: "-apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, sans-serif",
        paddingBottom: 40,
      }}>

        {/* ‚îÄ‚îÄ App bar ‚îÄ‚îÄ */}
        <div style={{
          background: C.darkGreen,
          padding: '14px 18px',
          display: 'flex', alignItems: 'center', gap: 12,
          boxShadow: '0 2px 16px rgba(0,0,0,0.3)',
          position: 'sticky', top: 0, zIndex: 100,
        }}>
          <img
            src="https://api.dicebear.com/7.x/personas/svg?seed=MayaManager&backgroundColor=25D366"
            alt="Maya"
            style={{ width: 42, height: 42, borderRadius: '50%', border: '2px solid rgba(255,255,255,0.35)', flexShrink: 0 }}
          />
          <div style={{ flex: 1 }}>
            <div style={{ color: '#fff', fontWeight: 700, fontSize: 15 }}>Maya Hotel AI</div>
            <div style={{ color: 'rgba(255,255,255,0.65)', fontSize: 12 }}>
              ◊©◊ú◊ï◊ù {workerName} &nbsp;¬∑&nbsp;
              {pendingCount > 0
                ? <span style={{ color: '#fde68a' }}>{pendingCount} ◊û◊©◊ô◊û◊ï◊™ ◊§◊™◊ï◊ó◊ï◊™</span>
                : <span style={{ color: '#86efac' }}>◊î◊õ◊ú ◊†◊ß◊ô ‚úì</span>}
            </div>
          </div>
          {/* Refresh button */}
          <button
            onClick={() => fetchTasks()}
            title="◊®◊¢◊†◊ü"
            style={{
              background: 'rgba(255,255,255,0.15)', border: 'none', color: '#fff',
              width: 38, height: 38, borderRadius: '50%', cursor: 'pointer',
              display: 'flex', alignItems: 'center', justifyContent: 'center', fontSize: 18,
            }}
          >
            <span style={{ display: 'inline-block', animation: spinning ? 'wvSpin 0.7s linear infinite' : 'none' }}>‚Üª</span>
          </button>
        </div>

        {/* ‚îÄ‚îÄ Last sync ‚îÄ‚îÄ */}
        {lastSync && (
          <div style={{
            textAlign: 'center', color: 'rgba(255,255,255,0.3)',
            fontSize: 11, padding: '6px 0 0',
          }}>
            ◊¢◊ï◊ì◊õ◊ü: {lastSync.toLocaleTimeString('he-IL', { hour: '2-digit', minute: '2-digit' })}
            &nbsp;¬∑ ◊û◊™◊®◊¢◊†◊ü ◊ê◊ï◊ò◊ï◊û◊ò◊ô◊™ ◊õ◊ú 30 ◊©◊†◊ô◊ï◊™
          </div>
        )}

        {/* ‚îÄ‚îÄ Task list ‚îÄ‚îÄ */}
        <div style={{ maxWidth: 480, margin: '0 auto', padding: '16px 16px 0' }}>
          {loading ? (
            <div style={{ textAlign: 'center', color: 'rgba(255,255,255,0.55)', paddingTop: 80, fontSize: 15 }}>
              ‚è≥ ◊ò◊ï◊¢◊ü ◊û◊©◊ô◊û◊ï◊™...
            </div>
          ) : tasks.length === 0 ? (
            <div style={{
              textAlign: 'center', padding: '60px 20px',
              color: 'rgba(255,255,255,0.45)',
            }}>
              <div style={{ fontSize: 52, marginBottom: 12 }}>üéâ</div>
              <div style={{ fontSize: 17, fontWeight: 600 }}>◊ê◊ô◊ü ◊û◊©◊ô◊û◊ï◊™ ◊§◊™◊ï◊ó◊ï◊™</div>
              <div style={{ fontSize: 13, marginTop: 6 }}>◊õ◊ú ◊î◊õ◊ë◊ï◊ì ‚Äî ◊î◊õ◊ú ◊û◊ò◊ï◊§◊ú!</div>
              <button
                onClick={() => fetchTasks()}
                style={{
                  marginTop: 24, background: C.green, border: 'none', borderRadius: 14,
                  color: '#fff', fontWeight: 700, padding: '12px 28px',
                  cursor: 'pointer', fontSize: 14,
                }}
              >
                ◊®◊¢◊†◊ü
              </button>
            </div>
          ) : (
            tasks.map((t) => (
              <TaskCard
                key={t.id}
                task={t}
                workerName={workerName}
                onDone={handleDone}
              />
            ))
          )}
        </div>

        <p style={{ textAlign: 'center', color: 'rgba(255,255,255,0.2)', fontSize: 11, marginTop: 24 }}>
          ◊û◊ï◊§◊¢◊ú ◊¢"◊ô Maya AI ¬∑ Arizona Hotel
        </p>
      </div>

      {/* ‚îÄ‚îÄ Toast ‚îÄ‚îÄ */}
      {toast && <Toast msg={toast} onClose={() => setToast(null)} />}
    </>
  );
}
