import React, { useState } from 'react';
import { X, Phone, Mail, Calendar, UserCheck, Clock, Copy, Check, MessageCircle } from 'lucide-react';
import Button from '../ui/Button';
import './LeadDetailsModal.css';

/**
 * Modal for viewing and managing lead details
 * Includes WhatsApp/Email message copy functionality
 */
const LeadDetailsModal = ({ lead, lang, onClose, onStatusUpdate }) => {
  const [copied, setCopied] = useState(null);
  const [selectedStatus, setSelectedStatus] = useState(lead.status);

  // Status options
  const statusOptions = [
    { value: 'new', label: lang === 'he' ? 'חדש' : 'New', color: 'primary' },
    { value: 'contacted', label: lang === 'he' ? 'נוצר קשר' : 'Contacted', color: 'accent' },
    { value: 'qualified', label: lang === 'he' ? 'מתאים' : 'Qualified', color: 'secondary' },
    { value: 'booked', label: lang === 'he' ? 'הוזמן' : 'Booked', color: 'secondary' },
    { value: 'lost', label: lang === 'he' ? 'אבוד' : 'Lost', color: 'danger' },
  ];

  // Format date
  const formatDate = (dateStr) => {
    if (!dateStr) return '-';
    return new Date(dateStr).toLocaleDateString(lang === 'he' ? 'he-IL' : 'en-US', {
      year: 'numeric',
      month: 'short',
      day: 'numeric'
    });
  };

  // Generate WhatsApp message
  const generateWhatsAppMessage = () => {
    const hotelName = 'Villa Mykonos';
    const greeting = lang === 'he' ? 'שלום' : 'Hello';
    const intro = lang === 'he'
      ? `${greeting} ${lead.name},\n\nתודה על פנייתך ל-${hotelName}!`
      : `${greeting} ${lead.name},\n\nThank you for your inquiry at ${hotelName}!`;

    let dateInfo = '';
    if (lead.checkin && lead.checkout) {
      dateInfo = lang === 'he'
        ? `\n\nראינו שאתם מתעניינים בתאריכים:\n${formatDate(lead.checkin)} - ${formatDate(lead.checkout)}`
        : `\n\nWe noticed you're interested in:\n${formatDate(lead.checkin)} - ${formatDate(lead.checkout)}`;
    }

    let guestsInfo = '';
    if (lead.guests) {
      guestsInfo = lang === 'he'
        ? `\nעבור ${lead.guests} אורחים.`
        : `\nFor ${lead.guests} guests.`;
    }

    const closing = lang === 'he'
      ? '\n\nנשמח לעזור לכם להזמין את החופשה המושלמת!\nאיך נוכל לסייע?'
      : '\n\nWe would be happy to help you book your perfect vacation!\nHow can we assist you?';

    return `${intro}${dateInfo}${guestsInfo}${closing}`;
  };

  // Generate Email message
  const generateEmailMessage = () => {
    const hotelName = 'Villa Mykonos';
    const subject = lang === 'he'
      ? `בקשת הזמנה - ${hotelName}`
      : `Booking Inquiry - ${hotelName}`;

    const body = generateWhatsAppMessage();

    return { subject, body };
  };

  // Copy to clipboard
  const copyToClipboard = async (text, type) => {
    try {
      await navigator.clipboard.writeText(text);
      setCopied(type);
      setTimeout(() => setCopied(null), 2000);
    } catch (err) {
      console.error('Failed to copy:', err);
    }
  };

  // Handle status change
  const handleStatusChange = (newStatus) => {
    setSelectedStatus(newStatus);
    onStatusUpdate(lead.id, newStatus);
  };

  // Open WhatsApp
  const openWhatsApp = () => {
    if (lead.phone) {
      const message = encodeURIComponent(generateWhatsAppMessage());
      const phone = lead.phone.replace(/[^\d+]/g, '');
      window.open(`https://wa.me/${phone}?text=${message}`, '_blank');
    }
  };

  // Open Email
  const openEmail = () => {
    if (lead.email) {
      const { subject, body } = generateEmailMessage();
      const mailtoUrl = `mailto:${lead.email}?subject=${encodeURIComponent(subject)}&body=${encodeURIComponent(body)}`;
      window.location.href = mailtoUrl;
    }
  };

  return (
    <div className="lead-modal-overlay" onClick={onClose}>
      <div className="lead-modal" onClick={(e) => e.stopPropagation()}>
        {/* Header */}
        <div className="lead-modal-header">
          <div className="lead-modal-title">
            <h2>{lead.name}</h2>
            <span className={`score-badge score-${lead.score >= 70 ? 'high' : lead.score >= 40 ? 'medium' : 'low'}`}>
              {lang === 'he' ? 'ציון' : 'Score'}: {lead.score}
            </span>
          </div>
          <button className="lead-modal-close" onClick={onClose}>
            <X size={24} />
          </button>
        </div>

        {/* Content */}
        <div className="lead-modal-content">
          {/* Contact Info */}
          <div className="lead-section">
            <h3>{lang === 'he' ? 'פרטי קשר' : 'Contact Info'}</h3>
            <div className="lead-info-grid">
              {lead.phone && (
                <div className="lead-info-item">
                  <Phone size={18} />
                  <span>{lead.phone}</span>
                </div>
              )}
              {lead.email && (
                <div className="lead-info-item">
                  <Mail size={18} />
                  <span>{lead.email}</span>
                </div>
              )}
            </div>
          </div>

          {/* Booking Details */}
          <div className="lead-section">
            <h3>{lang === 'he' ? 'פרטי הזמנה' : 'Booking Details'}</h3>
            <div className="lead-info-grid">
              {(lead.checkin || lead.checkout) && (
                <div className="lead-info-item">
                  <Calendar size={18} />
                  <span>
                    {formatDate(lead.checkin)} - {formatDate(lead.checkout)}
                  </span>
                </div>
              )}
              {lead.guests && (
                <div className="lead-info-item">
                  <UserCheck size={18} />
                  <span>{lead.guests} {lang === 'he' ? 'אורחים' : 'guests'}</span>
                </div>
              )}
              <div className="lead-info-item">
                <Clock size={18} />
                <span>
                  {lang === 'he' ? 'נוצר:' : 'Created:'} {formatDate(lead.created_at)}
                </span>
              </div>
            </div>
          </div>

          {/* Status Update */}
          <div className="lead-section">
            <h3>{lang === 'he' ? 'סטטוס' : 'Status'}</h3>
            <div className="status-buttons">
              {statusOptions.map(option => (
                <button
                  key={option.value}
                  className={`status-btn status-btn-${option.color} ${selectedStatus === option.value ? 'active' : ''}`}
                  onClick={() => handleStatusChange(option.value)}
                >
                  {option.label}
                </button>
              ))}
            </div>
          </div>

          {/* Quick Actions */}
          <div className="lead-section">
            <h3>{lang === 'he' ? 'פעולות מהירות' : 'Quick Actions'}</h3>
            <div className="quick-actions">
              {lead.phone && (
                <Button variant="secondary" onClick={openWhatsApp}>
                  <MessageCircle size={18} />
                  WhatsApp
                </Button>
              )}
              {lead.email && (
                <Button variant="primary" onClick={openEmail}>
                  <Mail size={18} />
                  Email
                </Button>
              )}
            </div>
          </div>

          {/* Message Templates */}
          <div className="lead-section">
            <h3>{lang === 'he' ? 'העתק הודעה' : 'Copy Message'}</h3>
            <div className="message-templates">
              {/* WhatsApp Message */}
              <div className="message-template">
                <div className="template-header">
                  <MessageCircle size={16} />
                  <span>WhatsApp</span>
                  <button
                    className="copy-btn"
                    onClick={() => copyToClipboard(generateWhatsAppMessage(), 'whatsapp')}
                  >
                    {copied === 'whatsapp' ? <Check size={16} /> : <Copy size={16} />}
                    {copied === 'whatsapp'
                      ? (lang === 'he' ? 'הועתק!' : 'Copied!')
                      : (lang === 'he' ? 'העתק' : 'Copy')
                    }
                  </button>
                </div>
                <pre className="message-preview">{generateWhatsAppMessage()}</pre>
              </div>

              {/* Email Message */}
              <div className="message-template">
                <div className="template-header">
                  <Mail size={16} />
                  <span>Email</span>
                  <button
                    className="copy-btn"
                    onClick={() => {
                      const { body } = generateEmailMessage();
                      copyToClipboard(body, 'email');
                    }}
                  >
                    {copied === 'email' ? <Check size={16} /> : <Copy size={16} />}
                    {copied === 'email'
                      ? (lang === 'he' ? 'הועתק!' : 'Copied!')
                      : (lang === 'he' ? 'העתק' : 'Copy')
                    }
                  </button>
                </div>
                <pre className="message-preview">{generateEmailMessage().body}</pre>
              </div>
            </div>
          </div>
        </div>

        {/* Footer */}
        <div className="lead-modal-footer">
          <span className="lead-source">
            {lang === 'he' ? 'מקור:' : 'Source:'} {lead.source}
          </span>
          <Button variant="ghost" onClick={onClose}>
            {lang === 'he' ? 'סגור' : 'Close'}
          </Button>
        </div>
      </div>
    </div>
  );
};

export default LeadDetailsModal;
