import React, { useState } from 'react';
import { Bot, Clock, Droplets, CheckCircle, LogIn, LogOut, AlertTriangle, Building2 } from 'lucide-react';

const initialPortfolio = [
  {
    name: 'וילה קיסריה',
    rooms: [
      { id: 'K1', name: 'סוויטת ים', status: 'פנוי', cleaning: 'נקי', priority: 'נמוך' },
      { id: 'K2', name: 'חדר גן', status: 'תפוס', cleaning: 'נקי', priority: 'נמוך' },
    ]
  },
  {
    name: 'פנטהאוז תל אביב',
    rooms: [
      { id: 'T1', name: 'סוויטה ראשית', status: 'פנוי', cleaning: 'דרוש ניקיון', priority: 'גבוה' },
    ]
  },
  {
    name: 'סוויטת ירושלים',
    rooms: [
      { id: 'J1', name: 'חדר אבן', status: 'פנוי', cleaning: 'נקי', priority: 'נמוך' },
    ]
  }
];

function App() {
  const [portfolio, setPortfolio] = useState(initialPortfolio);
  const [aiMessage, setAiMessage] = useState("מערכת התיעדוף פועלת. ממתין ליציאת אורחים...");

  const handleAction = async (roomIndex, propIndex, action) => {
    let newPortfolio = JSON.parse(JSON.stringify(portfolio));
    let property = newPortfolio[propIndex];
    let room = property.rooms[roomIndex];

    if (action === 'checkin') {
      room.status = 'תפוס';
      setAiMessage(`צ'ק-אין בוצע ב${room.name}. המערכת עוקבת אחרי זמן השהייה.`);
    } else if (action === 'checkout') {
      room.status = 'פנוי';
      room.cleaning = 'דרוש ניקיון';
      room.priority = 'גבוה מאוד';
      
      try {
        await fetch('http://127.0.0.1:5000/trigger_checkout', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ property: property.name, room: room.name }),
        });
        setAiMessage(`התראת AI: אורח יצא מ${room.name}. שלחתי הודעה למנקה עם תיעדוף גבוה.`);
      } catch (e) {
        setAiMessage("התראה נשלחה (הערה: וודא ששרת ה-Python דולק).");
      }
    }
    setPortfolio(newPortfolio);
  };

  return (
    <div style={{ padding: '30px', fontFamily: 'Inter, sans-serif', direction: 'rtl', backgroundColor: '#020617', color: '#f8fafc', minHeight: '100vh' }}>
      
      <header style={{ marginBottom: '30px', borderBottom: '1px solid #1e293b', paddingBottom: '15px' }}>
        <h1 style={{ fontSize: '26px', fontWeight: '800' }}>מערכת תיעדוף חכמה <span style={{ color: '#3b82f6' }}>LuxuryStay</span></h1>
      </header>

      <div style={{ background: 'rgba(59, 130, 246, 0.1)', border: '1px solid #3b82f6', padding: '15px', borderRadius: '15px', marginBottom: '30px', display: 'flex', alignItems: 'center', gap: '15px' }}>
        <Bot color="#3b82f6" />
        <span style={{ fontSize: '15px' }}>{aiMessage}</span>
      </div>

      <div style={{ display: 'flex', flexDirection: 'column', gap: '25px' }}>
        {portfolio.map((property, propIdx) => (
          <div key={property.name} style={{ background: '#0f172a', padding: '20px', borderRadius: '20px', border: '1px solid #1e293b' }}>
            <h2 style={{ fontSize: '20px', marginBottom: '15px', color: '#3b82f6', display: 'flex', alignItems: 'center', gap: '10px' }}>
              <Building2 size={20}/> {property.name}
            </h2>
            
            <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(300px, 1fr))', gap: '15px' }}>
              {property.rooms.map((room, roomIdx) => (
                <div key={room.id} style={{ background: '#020617', padding: '20px', borderRadius: '15px', border: '1px solid #1e293b' }}>
                  <div style={{ display: 'flex', justifyContent: 'space-between', marginBottom: '15px' }}>
                    <span style={{ fontWeight: 'bold' }}>{room.name}</span>
                    {room.priority === 'גבוה מאוד' && <span style={{ color: '#ef4444', fontSize: '11px', fontWeight: 'bold' }}>דחוף</span>}
                  </div>

                  <div style={{ display: 'flex', gap: '10px', marginBottom: '15px' }}>
                    <div style={{ flex: 1, background: '#111827', padding: '10px', borderRadius: '8px', fontSize: '12px' }}>
                      סטטוס: <span style={{ color: room.status === 'תפוס' ? '#ef4444' : '#4ade80' }}>{room.status}</span>
                    </div>
                    <div style={{ flex: 1, background: '#111827', padding: '10px', borderRadius: '8px', fontSize: '12px' }}>
                      ניקיון: <span style={{ color: room.cleaning === 'דרוש ניקיון' ? '#fbbf24' : '#3b82f6' }}>{room.cleaning}</span>
                    </div>
                  </div>

                  <div style={{ display: 'flex', gap: '8px' }}>
                    {room.status === 'פנוי' && room.cleaning === 'נקי' && (
                      <button onClick={() => handleAction(roomIdx, propIdx, 'checkin')} style={{ flex: 1, background: '#3b82f6', border: 'none', color: '#fff', padding: '10px', borderRadius: '8px', cursor: 'pointer' }}>
                        כניסת אורח
                      </button>
                    )}
                    {room.status === 'תפוס' && (
                      <button onClick={() => handleAction(roomIdx, propIdx, 'checkout')} style={{ flex: 1, background: '#ef4444', border: 'none', color: '#fff', padding: '10px', borderRadius: '8px', cursor: 'pointer' }}>
                        יציאת אורח
                      </button>
                    )}
                    {room.cleaning === 'דרוש ניקיון' && (
                      <div style={{ flex: 1, textAlign: 'center', color: '#fbbf24', fontSize: '12px' }}>
                         התראה נשלחה למנקה...
                      </div>
                    )}
                  </div>
                </div>
              ))}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
