import React from 'react';
import { motion } from 'framer-motion';
import { Menu, Globe, UserCircle, LogOut, Moon, Sun } from 'lucide-react';
import useStore from '../../store/useStore';
import NotificationCenter from '../notifications/NotificationCenter';
import './TopBar.css';

const TopBar = () => {
  const { lang, setLang, role, sidebarOpen, toggleSidebar } = useStore();

  const languages = [
    { code: 'he', label: ' 注专转', flag: '' },
    { code: 'en', label: '吼 English', flag: '吼' },
    { code: 'el', label: ' 位位畏谓喂魏维', flag: '' },
  ];

  const roleLabels = {
    he: { owner: '', staff: '爪转', guest: '专' },
    en: { owner: 'Owner', staff: 'Staff', guest: 'Guest' },
    el: { owner: '未喂慰魏萎畏', staff: '慰喂魏', guest: '喂魏苇畏' },
  };

  return (
    <header className="top-bar glass">
      <div className="top-bar-start">
        <motion.button
          whileHover={{ scale: 1.05 }}
          whileTap={{ scale: 0.95 }}
          onClick={toggleSidebar}
          className="menu-btn"
        >
          <Menu size={22} />
        </motion.button>
        <div className="breadcrumb">
          <span className="breadcrumb-item">Enterprise</span>
          <span className="breadcrumb-separator">/</span>
          <span className="breadcrumb-item active">Dashboard</span>
        </div>
      </div>

      <div className="top-bar-end">
        {/* Language Selector */}
        <div className="lang-selector">
          {languages.map((l) => (
            <motion.button
              key={l.code}
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
              onClick={() => setLang(l.code)}
              className={`lang-btn ${lang === l.code ? 'active' : ''}`}
            >
              {l.flag}
            </motion.button>
          ))}
        </div>

        {/* Notifications */}
        <NotificationCenter />

        {/* User Menu */}
        <div className="user-menu">
          <div className="user-avatar">
            <UserCircle size={24} />
          </div>
          <div className="user-info">
            <span className="user-name"> 注专转</span>
            <span className="user-role">{roleLabels[lang]?.[role] || role}</span>
          </div>
        </div>
      </div>
    </header>
  );
};

export default TopBar;
