import React from 'react';
import { Building2 } from 'lucide-react';
import Card from '../ui/Card';
import Button from '../ui/Button';
import { sendAIAction } from '../../services/api';
import './OwnerView.css';

/**
 * Owner view component
 */
const OwnerView = ({ aiLog, onShowSummary, t }) => {
  const handleAIAction = async (type, room) => {
    const timestamp = new Date().toLocaleTimeString();
    try {
      await sendAIAction(type, room, 'owner', 'he');
    } catch (error) {
      console.error('Error sending AI action:', error);
    }
  };

  return (
    <div className="owner-view fade-in">
      <div className="owner-actions">
        <Button variant="secondary" onClick={onShowSummary}>
          ðŸ“Š {t.summary}
        </Button>
      </div>

      <Card variant="outlined" className="ai-log-card">
        <div className="card-header">
          <h4 className="card-title">{t.logTitle}</h4>
        </div>
        <div className="card-body">
          {aiLog.map((log, i) => (
            <div key={i} className="log-entry">
              {log}
            </div>
          ))}
        </div>
      </Card>

      <Card className="room-management-card">
        <div className="card-header">
          <h3 className="room-title">
            <Building2 size={24} />
            ×•×™×œ×” ×§×™×¡×¨×™×” / Villa Mykonos
          </h3>
        </div>
        <div className="card-body">
          <div className="room-item">
            <span className="room-name">{t.suite} 101</span>
            <div className="room-actions">
              <Button
                variant="danger"
                size="sm"
                onClick={() => handleAIAction('checkout', '101')}
              >
                {t.exit}
              </Button>
              <Button
                variant="accent"
                size="sm"
                onClick={() => handleAIAction('towels', '101')}
              >
                {t.towels}
              </Button>
            </div>
          </div>
        </div>
      </Card>
    </div>
  );
};

export default OwnerView;
