import React, { useState } from 'react';
import { Bot, Building2, Utensils, Bell, LogOut, Activity, Globe, UserCircle } from 'lucide-react';

const translations = {
  he: {
    title: "◊†◊ô◊î◊ï◊ú LuxuryStay AI",
    summary: "◊ì◊ï\"◊ó ◊°◊ô◊õ◊ï◊ù",
    online: "◊û◊¢◊®◊õ◊™ ◊û◊ß◊ï◊ï◊†◊™",
    owner: "◊û◊†◊î◊ú", staff: "◊¶◊ï◊ï◊™ ◊¢◊ï◊ë◊ì◊ô◊ù", guest: "◊ê◊ï◊®◊ó",
    exit: "◊ô◊¶◊ô◊ê◊î", towels: "◊û◊í◊ë◊ï◊™", rest: "◊û◊°◊¢◊ì◊î",
    logTitle: "◊ô◊ï◊û◊ü ◊§◊¢◊ï◊ú◊ï◊™ AI",
    saved: "◊†◊©◊û◊® ◊ë◊ñ◊ô◊õ◊®◊ï◊ü",
    changeRole: "◊î◊ó◊ú◊£ ◊™◊§◊ß◊ô◊ì"
  },
  en: {
    title: "LuxuryStay AI Management",
    summary: "Summary Report",
    online: "System Online",
    owner: "Owner", staff: "Staff", guest: "Guest",
    exit: "Checkout", towels: "Towels", rest: "Restaurant",
    logTitle: "AI Activity Log",
    saved: "Saved to Memory",
    changeRole: "Switch Role"
  },
  el: {
    title: "ŒîŒπŒ±œáŒµŒØœÅŒπœÉŒ∑ LuxuryStay AI",
    summary: "ŒëŒΩŒ±œÜŒøœÅŒ¨",
    online: "Œ£œçœÉœÑŒ∑ŒºŒ± Online",
    owner: "ŒôŒ¥ŒπŒøŒ∫œÑŒÆœÑŒ∑œÇ", staff: "Œ†œÅŒøœÉœâœÄŒπŒ∫œå", guest: "ŒïœÄŒπœÉŒ∫Œ≠œÄœÑŒ∑œÇ",
    exit: "ŒëœÄŒøœáœéœÅŒ∑œÉŒ∑", towels: "Œ†ŒµœÑœÉŒ≠œÑŒµœÇ", rest: "ŒïœÉœÑŒπŒ±œÑœåœÅŒπŒø",
    logTitle: "ŒëœÅœáŒµŒØŒø AI",
    saved: "ŒëœÄŒøŒ∏Œ∑Œ∫ŒµœçœÑŒ∑Œ∫Œµ",
    changeRole: "ŒëŒªŒªŒ±Œ≥ŒÆ Œ°œåŒªŒøœÖ"
  }
};

function App() {
  const [lang, setLang] = useState('he');
  const [role, setRole] = useState('owner'); // 'owner', 'staff', 'guest'
  const [aiLog, setAiLog] = useState(["System Ready..."]);
  const [showSummary, setShowSummary] = useState(false);
  
  const t = translations[lang];

  const sendToAI = async (type, room) => {
    const timestamp = new Date().toLocaleTimeString();
    setAiLog(prev => [`[${timestamp}] ${type} request at ${room}`, ...prev.slice(0, 3)]);
    // ◊õ◊ê◊ü ◊î◊ß◊®◊ô◊ê◊î ◊ú-Python
    try { await fetch('http://127.0.0.1:5000/ai_action', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ type, room, role, lang }),
    }); } catch(e) {}
  };

  return (
    <div style={{ padding: '20px', backgroundColor: '#020617', color: 'white', minHeight: '100vh', direction: lang === 'he' ? 'rtl' : 'ltr', fontFamily: 'Segoe UI' }}>
      
      {/* Top Bar: Languages & Roles */}
      <div style={{ display: 'flex', justifyContent: 'space-between', marginBottom: '20px', background: '#1e293b', padding: '10px', borderRadius: '10px' }}>
        <div style={{ display: 'flex', gap: '10px' }}>
          <button onClick={() => setLang('he')} style={langBtnStyle(lang === 'he')}>üáÆüá± HE</button>
          <button onClick={() => setLang('en')} style={langBtnStyle(lang === 'en')}>üá∫üá∏ EN</button>
          <button onClick={() => setLang('el')} style={langBtnStyle(lang === 'el')}>üá¨üá∑ EL</button>
        </div>
        <div style={{ display: 'flex', alignItems: 'center', gap: '10px' }}>
          <UserCircle size={20} />
          <select value={role} onChange={(e) => setRole(e.target.value)} style={{ background: '#0f172a', color: '#3b82f6', border: '1px solid #3b82f6', borderRadius: '5px', padding: '5px' }}>
            <option value="owner">{t.owner}</option>
            <option value="staff">{t.staff}</option>
            <option value="guest">{t.guest}</option>
          </select>
        </div>
      </div>

      <h1 style={{ color: '#3b82f6', textAlign: 'center' }}><Bot size={30} /> {t.title}</h1>

      {/* View Logic based on Role */}
      {role === 'owner' && (
        <div style={{ animation: 'fadeIn 0.5s' }}>
           <div style={{ display: 'flex', justifyContent: 'center', gap: '10px', marginBottom: '20px' }}>
