import React, { useState, useEffect, useCallback, useRef } from 'react';
import { CalendarCheck, Phone, Building2, User, CheckCircle, MessageCircle } from 'lucide-react';
import { toWhatsAppPhone } from '../../utils/phone';
import { getPropertyTasks, updatePropertyTaskStatus } from '../../services/api';
import TaskListErrorBoundary from '../common/TaskListErrorBoundary';
import './TaskCalendar.css';

/** Always return a string for rendering - prevents [object Object] crash */
const safeStr = (val, fallback = '') => {
  if (val == null) return fallback;
  if (typeof val === 'string') return val;
  if (typeof val === 'object') return safeStr(val.content ?? val.title ?? val.text, fallback);
  return String(val);
};

export default function TaskCalendar() {
  const [tasks, setTasks] = useState([]);
  const [loading, setLoading] = useState(true);
  const loadRef = useRef(0);

  useEffect(() => {
    let cancelled = false;
    const loadId = ++loadRef.current;
    setLoading(true);
    getPropertyTasks()
      .then((list) => {
        if (!cancelled && loadId === loadRef.current) {
          setTasks(Array.isArray(list) ? list : []);
        }
      })
      .catch(() => {
        if (!cancelled && loadId === loadRef.current) setTasks([]);
      })
      .finally(() => {
        if (!cancelled && loadId === loadRef.current) setLoading(false);
      });
    return () => {
      cancelled = true;
    };
  }, []);

  useEffect(() => {
    const onTaskCreated = () => {
      getPropertyTasks().then((list) => {
        setTasks(Array.isArray(list) ? list : []);
      });
    };
    window.addEventListener('maya-task-created', onTaskCreated);
    return () => window.removeEventListener('maya-task-created', onTaskCreated);
  }, []);

  const handleToggleStatus = useCallback(async (taskId, newStatus) => {
    if (!taskId) return;
    try {
      await updatePropertyTaskStatus(taskId, newStatus);
      setTasks((prev) =>
        prev.map((t) => (t.id === taskId ? { ...t, status: newStatus } : t))
      );
    } catch (e) {
      window.alert(e?.message || 'Failed to update');
    }
  }, []);

  const isDone = (task) => (task?.status || '').toLowerCase() === 'done';

  const formatDate = (str) => {
    if (!str) return '—';
    try {
      const d = new Date(str);
      return d.toLocaleDateString('he-IL', {
        weekday: 'short',
        day: 'numeric',
        month: 'short',
        hour: '2-digit',
        minute: '2-digit',
      });
    } catch {
      return str;
    }
  };

  return (
    <div className="task-calendar p-10 bg-[#FBFBFB] min-h-screen" dir="rtl">
      <div className="flex items-center gap-3 mb-10">
        <div className="w-14 h-14 rounded-2xl bg-amber-100 flex items-center justify-center">
          <CalendarCheck size={28} className="text-amber-600" />
        </div>
        <div>
          <h1 className="text-3xl font-black text-gray-900">לוח משימות</h1>
          <p className="text-gray-500 mt-1">משימות שנוצרו על ידי מאיה – ניקיון, תחזוקה ושירות</p>
        </div>
      </div>

      <TaskListErrorBoundary>
      {loading ? (
        <p className="text-gray-400 py-12 text-center">טוען משימות...</p>
      ) : (tasks ?? []).length === 0 ? (
        <div className="task-calendar-empty bg-white rounded-2xl p-12 text-center border border-gray-100">
          <CalendarCheck size={48} className="text-gray-300 mx-auto mb-4" />
          <p className="text-gray-500 font-bold">אין משימות כרגע</p>
          <p className="text-sm text-gray-400 mt-1">כשמאיה תשלח הודעה לעובד, המשימה תופיע כאן</p>
        </div>
      ) : (
        <div className="task-calendar-grid">
          {(tasks ?? []).map((t) => (
            <div
              key={t.id}
              className={`task-card ${isDone(t) ? 'task-done' : 'task-pending'}`}
            >
              <div className="task-card-header">
                <span className={`task-status-badge ${isDone(t) ? 'done' : 'pending'}`}>
                  {isDone(t) ? 'הושלם' : 'ממתין'}
                </span>
                <span className="task-date">{formatDate(t.created_at)}</span>
              </div>
              <p className="task-description">{typeof t.description === 'string' ? t.description : (t.title || t.content || '')}</p>
              {t.property_context && (
                <p className="text-xs text-gray-500 mt-0.5">{t.property_context}</p>
              )}
              <div className="task-meta">
                {t.property_name && (
                  <div className="task-meta-row">
                    <Building2 size={16} />
                    <span>{String(t.property_name)}</span>
                  </div>
                )}
                {t.staff_name && (
                  <div className="task-meta-row flex items-center gap-2">
                    <User size={16} />
                    <span>{String(t.staff_name)}</span>
                    {t.staff_phone && (
                      <>
                        <a href={`tel:${String(t.staff_phone).replace(/\D/g, '')}`} className="task-phone-link" title="Call">
                          <Phone size={14} />
                        </a>
                        <a
                          href={`https://wa.me/${toWhatsAppPhone(t.staff_phone)}?text=${encodeURIComponent(`Hi ${t.staff_name}, regarding the task: ${(t.description || t.title || '').slice(0, 80)}. Please handle when possible.`)}`}
                          target="_blank"
                          rel="noopener noreferrer"
                          className="text-green-600 hover:text-green-700"
                          title="WhatsApp"
                        >
                          <MessageCircle size={16} />
                        </a>
                      </>
                    )}
                  </div>
                )}
                {t.staff_phone && !t.staff_name && (
                  <div className="task-meta-row task-phone">
                    <Phone size={16} />
                    <a href={`tel:${String(t.staff_phone)}`} className="task-phone-link">{String(t.staff_phone)}</a>
                    <a href={`https://wa.me/${toWhatsAppPhone(t.staff_phone)}?text=${encodeURIComponent(`Hi, regarding the task: ${(t.description || t.title || '').slice(0, 80)}. Please handle when possible.`)}`} target="_blank" rel="noopener noreferrer" className="text-green-600 hover:text-green-700" title="WhatsApp">
                      <MessageCircle size={16} />
                    </a>
                  </div>
                )}
              </div>
              <button
                type="button"
                onClick={() => handleToggleStatus(t.id, isDone(t) ? 'Pending' : 'Done')}
                className={isDone(t) ? 'task-revert-btn' : 'task-done-btn'}
              >
                {isDone(t) ? (
                  <>חזרה לממתין</>
                ) : (
                  <><CheckCircle size={18} /> סמן כהושלם</>
                )}
              </button>
            </div>
          ))}
        </div>
      )}
      </TaskListErrorBoundary>
    </div>
  );
}
