import React, { useState, useEffect, useCallback, useRef } from 'react';
import { CalendarCheck, Phone, CheckCircle, MessageCircle, FileText } from 'lucide-react';
import { toWhatsAppPhone } from '../../utils/phone';
import { getPropertyTasks, updatePropertyTaskStatus } from '../../services/api';
import TaskListErrorBoundary from '../common/TaskListErrorBoundary';
import useStore from '../../store/useStore';
import { maya } from '../../services/agentOrchestrator';
import './TaskCalendar.css';

/** Always return a string for rendering - prevents [object Object] crash */
const safeStr = (val, fallback = '') => {
  if (val == null) return fallback;
  if (typeof val === 'string') return val;
  if (typeof val === 'object') return safeStr(val.content ?? val.title ?? val.text, fallback);
  return String(val);
};

/** Build WhatsApp message by staff: Kobi = task + property, Alma = cleaning, Avi = general */
const getWhatsAppMessage = (t) => {
  const content = safeStr(t.description ?? t.title ?? t.content).slice(0, 120);
  const property = safeStr(t.property_name ?? t.propertyName);
  const staff = ((t.staff_name ?? t.staffName) || '').toString().toLowerCase();
  if (staff.includes('alma') || staff.includes('◊¢◊ú◊û◊î')) {
    return `◊î◊ô◊ô ◊¢◊ú◊û◊î, ◊ô◊© ◊ú◊ö ◊†◊ô◊ß◊ô◊ï◊ü: ${content}`;
  }
  if (staff.includes('kobi') || staff.includes('◊ß◊ï◊ë◊ô')) {
    return property ? `◊î◊ô◊ô ◊ß◊ï◊ë◊ô, ◊ô◊© ◊ú◊ö ◊û◊©◊ô◊û◊î: ${content} ◊ë${property}` : `◊î◊ô◊ô ◊ß◊ï◊ë◊ô, ◊ô◊© ◊ú◊ö ◊û◊©◊ô◊û◊î: ${content}`;
  }
  if (staff.includes('avi') || staff.includes('◊ê◊ë◊ô')) {
    return property ? `◊î◊ô◊ô ◊ê◊ë◊ô, ◊ô◊© ◊ú◊ö ◊û◊©◊ô◊û◊î: ${content} ◊ë${property}` : `◊î◊ô◊ô ◊ê◊ë◊ô, ◊ô◊© ◊ú◊ö ◊û◊©◊ô◊û◊î: ${content}`;
  }
  return `◊î◊ô◊ô ${safeStr(t.staff_name ?? t.staffName) || '◊©◊ù'}, ◊ô◊© ◊ú◊ö ◊û◊©◊ô◊û◊î: ${content}`;
};

export default function TaskCalendar() {
  const [tasks, setTasks] = useState([]);
  const [loading, setLoading] = useState(true);
  const loadRef = useRef(0);

  useEffect(() => {
    let cancelled = false;
    const loadId = ++loadRef.current;
    setLoading(true);
    getPropertyTasks()
      .then((list) => {
        if (!cancelled && loadId === loadRef.current) {
          setTasks(Array.isArray(list) ? list : []);
        }
      })
      .catch(() => {
        if (!cancelled && loadId === loadRef.current) setTasks([]);
      })
      .finally(() => {
        if (!cancelled && loadId === loadRef.current) setLoading(false);
      });
    return () => {
      cancelled = true;
    };
  }, []);

  useEffect(() => {
    const onTaskCreated = () => {
      getPropertyTasks().then((list) => {
        setTasks(Array.isArray(list) ? list : []);
      });
    };
    window.addEventListener('maya-task-created', onTaskCreated);
    return () => window.removeEventListener('maya-task-created', onTaskCreated);
  }, []);

  const [togglingId, setTogglingId] = useState(null);
  const handleToggleStatus = useCallback(async (taskId, newStatus) => {
    if (!taskId) return;
    setTogglingId(taskId);
    let prevStatus;
    setTasks((prev) => {
      const t = prev.find((x) => x.id === taskId);
      prevStatus = t?.status;
      return prev.map((x) => (x.id === taskId ? { ...x, status: newStatus } : x));
    });
    try {
      await updatePropertyTaskStatus(taskId, newStatus);
    } catch (e) {
      setTasks((prev) =>
        prev.map((x) => (x.id === taskId ? { ...x, status: prevStatus } : x))
      );
      window.alert(e?.message || 'Failed to update');
    } finally {
      setTogglingId(null);
    }
  }, []);

  const isDone = (task) => (task?.status || '').toLowerCase() === 'done';

  const formatDate = (str) => {
    if (!str) return '‚Äî';
    try {
      const d = new Date(str);
      return d.toLocaleDateString('he-IL', {
        weekday: 'short',
        day: 'numeric',
        month: 'short',
        hour: '2-digit',
        minute: '2-digit',
      });
    } catch {
      return str;
    }
  };

  return (
    <div className="task-calendar p-10 bg-[#FBFBFB] min-h-screen" dir="rtl">
      <div className="flex items-center gap-3 mb-10">
        <div className="w-14 h-14 rounded-2xl bg-amber-100 flex items-center justify-center">
          <CalendarCheck size={28} className="text-amber-600" />
        </div>
        <div>
          <h1 className="text-3xl font-black text-gray-900">◊ú◊ï◊ó ◊û◊©◊ô◊û◊ï◊™</h1>
          <p className="text-gray-500 mt-1">◊û◊©◊ô◊û◊ï◊™ ◊©◊†◊ï◊¶◊®◊ï ◊¢◊ú ◊ô◊ì◊ô ◊û◊ê◊ô◊î ‚Äì ◊†◊ô◊ß◊ô◊ï◊ü, ◊™◊ó◊ñ◊ï◊ß◊î ◊ï◊©◊ô◊®◊ï◊™</p>
        </div>
      </div>

      <TaskListErrorBoundary>
      {loading ? (
        <p className="text-gray-400 py-12 text-center">◊ò◊ï◊¢◊ü ◊û◊©◊ô◊û◊ï◊™...</p>
      ) : (tasks ?? []).length === 0 ? (
        <div className="task-calendar-empty bg-white rounded-2xl p-12 text-center border border-gray-100">
          <CalendarCheck size={48} className="text-gray-300 mx-auto mb-4" />
          <p className="text-gray-500 font-bold">◊ê◊ô◊ü ◊û◊©◊ô◊û◊ï◊™ ◊õ◊®◊í◊¢</p>
          <p className="text-sm text-gray-400 mt-1">◊õ◊©◊û◊ê◊ô◊î ◊™◊©◊ú◊ó ◊î◊ï◊ì◊¢◊î ◊ú◊¢◊ï◊ë◊ì, ◊î◊û◊©◊ô◊û◊î ◊™◊ï◊§◊ô◊¢ ◊õ◊ê◊ü</p>
        </div>
      ) : (
        <div className="task-calendar-grid">
          {(tasks ?? []).map((t, i) => (
            <div
              key={t?.id != null && typeof t.id !== 'object' ? String(t.id) : `task-${i}`}
              className={`task-card ${isDone(t) ? 'task-done' : 'task-pending'}`}
            >
              <div className="task-card-header">
                <span className={`task-status-badge ${isDone(t) ? 'done' : 'pending'}`}>
                  {isDone(t) ? '◊î◊ï◊©◊ú◊ù' : '◊û◊û◊™◊ô◊ü'}
                </span>
                <span className="task-date">{formatDate(t.created_at)}</span>
              </div>
              <p className="task-description">{safeStr(t.description) || safeStr(t.title) || safeStr(t.content) || ''}</p>
              {safeStr(t.property_context) && (
                <p className="text-xs text-gray-500 mt-0.5">{safeStr(t.property_context)}</p>
              )}
              <div className="task-meta">
                {(t.property_name ?? t.propertyName) && (
                  <div className="task-meta-row">
                    <span className="task-meta-icon" aria-hidden>üè†</span>
                    <span>{safeStr(t.property_name ?? t.propertyName)}</span>
                  </div>
                )}
                {(t.staff_name ?? t.staffName) && (
                  <div className="task-meta-row flex items-center gap-2">
                    <span className="task-meta-icon" aria-hidden>üë§</span>
                    <span>{safeStr(t.staff_name ?? t.staffName)}</span>
                    {(t.staff_phone ?? t.staffPhone) && (
                      <>
                        <a href={`tel:${safeStr(t.staff_phone ?? t.staffPhone).replace(/\D/g, '')}`} className="task-phone-link" title="Call">
                          <Phone size={14} />
                        </a>
                        <a
                          href={`https://wa.me/${toWhatsAppPhone(t.staff_phone ?? t.staffPhone)}?text=${encodeURIComponent(getWhatsAppMessage(t))}`}
                          target="_blank"
                          rel="noopener noreferrer"
                          className="task-whatsapp-btn"
                          title="◊©◊ú◊ô◊ó◊™ ◊ï◊ï◊ê◊ò◊°◊ê◊§"
                        >
                          <MessageCircle size={18} />
                        </a>
                      </>
                    )}
                  </div>
                )}
                {(t.staff_phone ?? t.staffPhone) && !(t.staff_name ?? t.staffName) && (
                  <div className="task-meta-row task-phone">
                    <Phone size={16} />
                    <a href={`tel:${safeStr(t.staff_phone ?? t.staffPhone).replace(/\D/g, '')}`} className="task-phone-link">{safeStr(t.staff_phone ?? t.staffPhone)}</a>
                    <a href={`https://wa.me/${toWhatsAppPhone(t.staff_phone ?? t.staffPhone)}?text=${encodeURIComponent(getWhatsAppMessage(t))}`} target="_blank" rel="noopener noreferrer" className="task-whatsapp-btn" title="◊©◊ú◊ô◊ó◊™ ◊ï◊ï◊ê◊ò◊°◊ê◊§">
                      <MessageCircle size={16} />
                    </a>
                  </div>
                )}
              </div>
              <button
                type="button"
                disabled={togglingId === t.id}
                onClick={() => handleToggleStatus(t.id, isDone(t) ? 'Pending' : 'Done')}
                className={`task-status-toggle ${isDone(t) ? 'revert' : 'done'} ${togglingId === t.id ? 'loading' : ''}`}
                title={isDone(t) ? '◊ó◊ñ◊®◊î ◊ú◊û◊û◊™◊ô◊ü' : '◊°◊û◊ü ◊õ◊î◊ï◊©◊ú◊ù'}
              >
                {togglingId === t.id ? (
                  <span className="task-toggle-spinner" />
                ) : (
                  <CheckCircle size={20} strokeWidth={2.5} />
                )}
              </button>
            </div>
          ))}
        </div>
      )}
      </TaskListErrorBoundary>
    </div>
  );
}
