import React, { useEffect, useState, useCallback } from 'react';
import {
  CheckCircle2, Loader2, BedDouble, User, Clock,
  AlertCircle, Phone, MessageCircle, CalendarDays,
} from 'lucide-react';
import { API_URL } from '../../utils/apiClient';
import './WorkerView.css';

const MAYA_AVATAR = 'https://api.dicebear.com/7.x/personas/svg?seed=MayaManager&backgroundColor=25D366';

const STATUS_CONFIG = {
  Pending:   { label: '◊û◊û◊™◊ô◊ü ◊ú◊ò◊ô◊§◊ï◊ú', color: '#f59e0b', bg: '#fef3c7' },
  pending:   { label: '◊û◊û◊™◊ô◊ü ◊ú◊ò◊ô◊§◊ï◊ú', color: '#f59e0b', bg: '#fef3c7' },
  Accepted:  { label: '◊ë◊ò◊ô◊§◊ï◊ú',        color: '#3b82f6', bg: '#dbeafe' },
  accepted:  { label: '◊ë◊ò◊ô◊§◊ï◊ú',        color: '#3b82f6', bg: '#dbeafe' },
  assigned:  { label: '◊©◊ï◊ô◊ö',           color: '#8b5cf6', bg: '#ede9fe' },
  Done:      { label: '◊î◊ï◊©◊ú◊ù ‚úì',        color: '#16a34a', bg: '#dcfce7' },
  done:      { label: '◊î◊ï◊©◊ú◊ù ‚úì',        color: '#16a34a', bg: '#dcfce7' },
  Completed: { label: '◊î◊ï◊©◊ú◊ù ‚úì',        color: '#16a34a', bg: '#dcfce7' },
};

const WorkerView = () => {
  const [task, setTask]               = useState(null);
  const [loading, setLoading]         = useState(true);
  const [actionLoading, setAction]    = useState(false);
  const [status, setStatus]           = useState(null);
  const [error, setError]             = useState(null);
  const [workerName, setWorkerName]   = useState('');
  const [nameOk, setNameOk]           = useState(false);
  const [successAnim, setSuccessAnim] = useState(false);

  const params      = new URLSearchParams(window.location.search);
  const taskId      = params.get('task_id') || params.get('taskId') || params.get('id');
  const workerParam = params.get('worker') || params.get('staff');
  const guestPhone  = params.get('guest_phone') || params.get('phone') || task?.guest_phone || '';

  useEffect(() => {
    if (workerParam) { setWorkerName(workerParam); setNameOk(true); }
  }, [workerParam]);

  const fetchTask = useCallback(async () => {
    if (!taskId) { setError('◊ú◊ê ◊°◊ï◊§◊ß ◊û◊°◊§◊® ◊û◊©◊ô◊û◊î. ◊§◊†◊î ◊ú◊û◊†◊î◊ú.'); setLoading(false); return; }
    setLoading(true);
    try {
      const res  = await fetch(`${API_URL}/api/property-tasks/${taskId}`);
      const data = await res.json();
      if (res.ok && data.task) { setTask(data.task); setStatus(data.task.status); }
      else setError(data.error || '◊î◊û◊©◊ô◊û◊î ◊ú◊ê ◊†◊û◊¶◊ê◊î.');
    } catch { setError('◊©◊í◊ô◊ê◊™ ◊ó◊ô◊ë◊ï◊® ◊ú◊©◊®◊™.'); }
    finally   { setLoading(false); }
  }, [taskId]);

  useEffect(() => { fetchTask(); }, [fetchTask]);

  const updateStatus = async (newStatus) => {
    if (!taskId || actionLoading) return;
    setAction(true);
    try {
      const res  = await fetch(`${API_URL}/api/property-tasks/${taskId}`, {
        method: 'PATCH',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ status: newStatus }),
      });
      const data = await res.json();
      if (res.ok) {
        setStatus(data.task?.status || newStatus);
        if (newStatus === 'Done') { setSuccessAnim(true); }
        await fetchTask();
      } else { alert('◊©◊í◊ô◊ê◊î: ' + (data.error || '◊†◊°◊î ◊©◊ï◊ë')); }
    } catch { alert('◊©◊í◊ô◊ê◊™ ◊ó◊ô◊ë◊ï◊® - ◊†◊°◊î ◊©◊ï◊ë.'); }
    finally   { setAction(false); }
  };

  const isDone     = ['Done', 'done', 'Completed'].includes(status);
  const isAccepted = ['Accepted', 'accepted', 'assigned'].includes(status);

  /* ‚îÄ‚îÄ Loading ‚îÄ‚îÄ */
  if (loading) return (
    <div className="wv-shell">
      <div className="wv-center">
        <Loader2 size={42} className="wv-spin" />
        <p>◊ò◊ï◊¢◊ü ◊û◊©◊ô◊û◊î...</p>
      </div>
    </div>
  );

  /* ‚îÄ‚îÄ Error ‚îÄ‚îÄ */
  if (error) return (
    <div className="wv-shell">
      <div className="wv-center wv-error-box">
        <AlertCircle size={48} />
        <h2>◊©◊í◊ô◊ê◊î</h2>
        <p>{error}</p>
      </div>
    </div>
  );

  /* ‚îÄ‚îÄ Name gate ‚îÄ‚îÄ */
  if (!nameOk) return (
    <div className="wv-shell">
      <div className="wv-name-card">
        <img src={MAYA_AVATAR} alt="Maya" className="wv-logo" />
        <h2>◊©◊ú◊ï◊ù! ◊û◊ô ◊ê◊™/◊î?</h2>
        <p className="wv-sub">◊î◊õ◊†◊°/◊ô ◊©◊ù ◊õ◊ì◊ô ◊ú◊ß◊ë◊ú ◊ê◊™ ◊î◊û◊©◊ô◊û◊î</p>
        <input
          className="wv-name-input"
          placeholder="◊©◊ù ◊û◊ú◊ê"
          value={workerName}
          onChange={(e) => setWorkerName(e.target.value)}
          onKeyDown={(e) => e.key === 'Enter' && workerName.trim() && setNameOk(true)}
          autoFocus dir="rtl"
        />
        <button className="wv-action-btn wv-btn-accept"
          onClick={() => workerName.trim() && setNameOk(true)}
          disabled={!workerName.trim()}>
          ◊î◊û◊©◊ö ‚Üí
        </button>
      </div>
    </div>
  );

  /* ‚îÄ‚îÄ Main view ‚îÄ‚îÄ */
  const roomLabel   = task?.property_name || task?.room || '◊ó◊ì◊® ◊ú◊ê ◊ô◊ì◊ï◊¢';
  const taskContent = task?.content || task?.description || task?.task_type || '◊û◊©◊ô◊û◊î';
  const staffName   = task?.staff_name || workerName;
  const statusCfg   = STATUS_CONFIG[status] || { label: status, color: '#6b7280', bg: '#f3f4f6' };

  const rawDate  = task?.created_at ? new Date(task.created_at) : new Date();
  const dateStr  = rawDate.toLocaleDateString('he-IL', { day: '2-digit', month: '2-digit', year: '2-digit' });
  const timeStr  = rawDate.toLocaleTimeString('he-IL', { hour: '2-digit', minute: '2-digit' });

  const whatsappLink = guestPhone
    ? `https://wa.me/${guestPhone.replace(/\D/g, '')}?text=${encodeURIComponent(`◊©◊ú◊ï◊ù, ◊ê◊†◊ô ${workerName}. ◊ê◊†◊ô ◊ë◊ì◊®◊ö ◊ú◊ó◊ì◊® ${roomLabel}.`)}`
    : null;
  const phoneLink = guestPhone ? `tel:${guestPhone}` : null;

  return (
    <div className="wv-shell" dir="rtl">

      {/* ‚îÄ‚îÄ App Bar ‚îÄ‚îÄ */}
      <div className="wv-appbar">
        <img src={MAYA_AVATAR} alt="Maya" className="wv-appbar-avatar" />
        <div>
          <div className="wv-appbar-title">Maya Hotel AI</div>
          <div className="wv-appbar-sub">◊§◊ï◊®◊ò◊ú ◊¢◊ï◊ë◊ì◊ô◊ù</div>
        </div>
      </div>

      {/* ‚îÄ‚îÄ Greeting ‚îÄ‚îÄ */}
      <div className="wv-greeting">
        ◊©◊ú◊ï◊ù <strong>{workerName}</strong>, ◊ô◊© ◊ú◊ö ◊û◊©◊ô◊û◊î ◊ó◊ì◊©◊î üëá
      </div>

      {/* ‚îÄ‚îÄ Rich Task Card ‚îÄ‚îÄ */}
      <div className={`wv-card ${isDone ? 'wv-card-done' : ''}`}>

        {/* Card Header: status + date */}
        <div className="wv-card-header">
          <span className="wv-status-pill" style={{ color: statusCfg.color, background: statusCfg.bg }}>
            {statusCfg.label}
          </span>
          <div className="wv-card-header-time">
            <CalendarDays size={13} />
            <span>{dateStr} ¬∑ {timeStr}</span>
          </div>
        </div>

        <div className="wv-card-divider" />

        {/* Room ‚Äî big and bold */}
        <div className="wv-room-block">
          <BedDouble size={26} className="wv-room-icon" />
          <div>
            <div className="wv-room-label">◊ó◊ì◊®</div>
            <div className="wv-room-number">{roomLabel}</div>
          </div>
        </div>

        {/* Task description */}
        <div className="wv-detail-row">
          <div className="wv-detail-key">◊û◊©◊ô◊û◊î</div>
          <div className="wv-detail-val">{taskContent}</div>
        </div>

        {/* Assigned staff */}
        {staffName && (
          <div className="wv-detail-row">
            <div className="wv-detail-key">
              <User size={14} style={{ display: 'inline', marginLeft: 4 }} />
              ◊©◊ï◊ô◊ö ◊ú
            </div>
            <div className="wv-detail-val">{staffName}</div>
          </div>
        )}

        <div className="wv-card-divider" />

        {/* Contact strip: WhatsApp + Phone */}
        <div className="wv-contact-strip">
          <span className="wv-contact-label">
            <Clock size={14} style={{ marginLeft: 4 }} />
            ◊¶◊ï◊® ◊ß◊©◊® ◊¢◊ù ◊î◊ê◊ï◊®◊ó
          </span>
          <div className="wv-contact-btns">
            {whatsappLink ? (
              <a href={whatsappLink} target="_blank" rel="noreferrer" className="wv-contact-btn wv-wa-btn">
                <MessageCircle size={20} />
                <span>◊ï◊ï◊ê◊ò◊°◊ê◊§</span>
              </a>
            ) : (
              <span className="wv-contact-btn wv-wa-btn wv-disabled">
                <MessageCircle size={20} />
                <span>◊ï◊ï◊ê◊ò◊°◊ê◊§</span>
              </span>
            )}
            {phoneLink ? (
              <a href={phoneLink} className="wv-contact-btn wv-phone-btn">
                <Phone size={20} />
                <span>◊î◊™◊ß◊©◊®</span>
              </a>
            ) : (
              <span className="wv-contact-btn wv-phone-btn wv-disabled">
                <Phone size={20} />
                <span>◊î◊™◊ß◊©◊®</span>
              </span>
            )}
          </div>
        </div>
      </div>

      {/* ‚îÄ‚îÄ Action Buttons ‚îÄ‚îÄ */}
      {isDone ? (
        <div className={`wv-done-banner ${successAnim ? 'wv-pop' : ''}`}>
          <CheckCircle2 size={40} />
          <div>
            <div className="wv-done-title">◊û◊©◊ô◊û◊î ◊î◊ï◊©◊ú◊û◊î!</div>
            <div className="wv-done-sub">◊õ◊ú ◊î◊õ◊ë◊ï◊ì {workerName} üéâ</div>
          </div>
        </div>
      ) : (
        <div className="wv-actions-row">
          {!isAccepted && (
            <button
              className="wv-action-btn wv-btn-accept"
              onClick={() => updateStatus('Accepted')}
              disabled={actionLoading}
            >
              {actionLoading
                ? <Loader2 size={20} className="wv-spin" />
                : <><span className="wv-btn-icon">‚úÖ</span>◊ß◊ë◊ú◊™ ◊û◊©◊ô◊û◊î</>}
            </button>
          )}
          <button
            className="wv-action-btn wv-btn-done"
            onClick={() => updateStatus('Done')}
            disabled={actionLoading}
          >
            {actionLoading
              ? <Loader2 size={20} className="wv-spin" />
              : <><span className="wv-btn-icon">üèÅ</span>◊°◊ô◊ï◊ù ◊û◊©◊ô◊û◊î</>}
          </button>
        </div>
      )}

      <p className="wv-footer">◊û◊ï◊§◊¢◊ú ◊¢"◊ô Maya AI ‚Ä¢ Arizona Hotel</p>
    </div>
  );
};

export default WorkerView;
