import React from 'react';
import { motion } from 'framer-motion';
import { Menu, UserCircle } from 'lucide-react';
import useTranslations from '../../hooks/useTranslations';
import useStore from '../../store/useStore';
import NotificationCenter from '../notifications/NotificationCenter';
import easyHostLogo from '../../assets/easyhost-logo.svg';
import './TopBar.css';

const TopBar = () => {
  const { lang, setLang, role, sidebarOpen, toggleSidebar, tenants, activeTenantId, setActiveTenantId } = useStore();
  const { t } = useTranslations();

  const languages = [
    { code: 'he', label: t('languages.he'), flag: 'ðŸ‡®ðŸ‡±' },
    { code: 'en', label: t('languages.en'), flag: 'ðŸ‡ºðŸ‡¸' },
    { code: 'el', label: t('languages.el'), flag: 'ðŸ‡¬ðŸ‡·' },
  ];

  return (
    <header className="top-bar glass">
      <div className="top-bar-start">
        <motion.button
          whileHover={{ scale: 1.05 }}
          whileTap={{ scale: 0.95 }}
          onClick={toggleSidebar}
          className="menu-btn"
        >
          <Menu size={22} />
        </motion.button>
        <div className="topbar-logo">
          <img src={easyHostLogo} alt="Easy Host AI" className="topbar-logo-img" />
          <span className="topbar-logo-text">Easy Host AI</span>
        </div>
        <div className="breadcrumb">
          <span className="breadcrumb-item">{t('topbar.breadcrumbEnterprise')}</span>
          <span className="breadcrumb-separator">/</span>
          <span className="breadcrumb-item active">{t('topbar.breadcrumbDashboard')}</span>
        </div>
      </div>

      <div className="top-bar-end">
        <div className="tenant-selector">
          <select
            value={activeTenantId}
            onChange={(e) => setActiveTenantId(e.target.value)}
            className="tenant-select"
            aria-label="Select tenant"
          >
            {tenants.map((tenant) => (
              <option key={tenant.id} value={tenant.id}>
                {tenant.name}
              </option>
            ))}
          </select>
        </div>
        {/* Language Selector */}
        <div className="lang-selector">
          {languages.map((l) => (
            <motion.button
              key={l.code}
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
              onClick={() => setLang(l.code)}
              className={`lang-btn ${lang === l.code ? 'active' : ''}`}
            >
              {l.flag}
            </motion.button>
          ))}
        </div>

        {/* Notifications */}
        <NotificationCenter />

        {/* User Menu */}
        <div className="user-menu">
          <div className="user-avatar">
            <UserCircle size={24} />
          </div>
          <div className="user-info">
            <span className="user-name">{t('topbar.userName')}</span>
            <span className="user-role">{t(`roles.${role}`)}</span>
          </div>
        </div>
      </div>
    </header>
  );
};

export default TopBar;
