import React, { useState, useCallback } from 'react';
import { createManualRoomWithDescription, uploadPropertyPhoto } from '../../services/api';
import './PropertyCreatorModal.css';

const AMENITIES = ['Wi-Fi', 'מטבח', 'מכונת כביסה', 'בריכה', 'חניה', 'טלוויזיה'];

const PLACEHOLDER_IMAGE = 'https://images.unsplash.com/photo-1613977257363-707ba9348227?w=800&auto=format&fit=crop';

export default function PropertyCreatorModal({ isOpen, onClose, onSuccess }) {
  const [name, setName] = useState('');
  const [price, setPrice] = useState('');
  const [files, setFiles] = useState([]);
  const [amenities, setAmenities] = useState({});
  const [isDragging, setIsDragging] = useState(false);
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [error, setError] = useState(null);

  const toggleAmenity = (key) => {
    setAmenities((prev) => ({ ...prev, [key]: !prev[key] }));
  };

  const handleFiles = useCallback((newFiles) => {
    const list = Array.from(newFiles || []).filter((f) => f.type.startsWith('image/')).slice(0, 5);
    setFiles((prev) => [...prev, ...list].slice(0, 5));
  }, []);

  const handleDrop = (e) => {
    e.preventDefault();
    setIsDragging(false);
    handleFiles(e.dataTransfer?.files);
  };

  const handleDragOver = (e) => {
    e.preventDefault();
    setIsDragging(true);
  };

  const handleDragLeave = () => {
    setIsDragging(false);
  };

  const removeFile = (index) => {
    setFiles((prev) => prev.filter((_, i) => i !== index));
  };

  const handleSubmit = async () => {
    const trimmedName = name.trim();
    if (!trimmedName) {
      setError('נא להזין שם נכס');
      return;
    }

    setIsSubmitting(true);
    setError(null);

    try {
      let photoUrl = PLACEHOLDER_IMAGE;
      if (files.length > 0) {
        photoUrl = await uploadPropertyPhoto(files[0]);
      }

      const amenityList = AMENITIES.filter((a) => amenities[a]).join(', ');
      const descParts = [];
      if (price) descParts.push(`מחיר ללילה: ₪${price}`);
      if (amenityList) descParts.push(`אמצעים: ${amenityList}`);
      const description = descParts.join(' | ') || 'נכס שנוצר ידנית';

      await createManualRoomWithDescription(trimmedName, description, photoUrl);
      typeof onSuccess === 'function' && onSuccess();
      onClose();
      setName('');
      setPrice('');
      setFiles([]);
      setAmenities({});
    } catch (e) {
      setError(e?.message || 'אירעה שגיאה. נסה שוב.');
    } finally {
      setIsSubmitting(false);
    }
  };

  if (!isOpen) return null;

  return (
    <div className="property-creator-backdrop" onClick={onClose}>
      <div
        className="property-creator-modal"
        onClick={(e) => e.stopPropagation()}
      >
        <div className="p-8 bg-white rounded-[40px] shadow-2xl max-w-2xl mx-auto" dir="rtl">
          <div className="flex justify-between items-center mb-6">
            <h2 className="text-2xl font-black">הקמת נכס חדש ב-10 שניות</h2>
            <button
              type="button"
              onClick={onClose}
              className="w-10 h-10 rounded-full bg-gray-100 hover:bg-gray-200 flex items-center justify-center text-xl"
            >
              ×
            </button>
          </div>

          {/* אזור גרירת תמונות */}
          <div
            className={`property-creator-dropzone border-2 border-dashed rounded-[32px] p-10 text-center mb-8 cursor-pointer transition-colors ${
              isDragging ? 'border-yellow-400 bg-yellow-50' : 'border-gray-200 hover:border-yellow-400 bg-gray-50'
            }`}
            onDrop={handleDrop}
            onDragOver={handleDragOver}
            onDragLeave={handleDragLeave}
            onClick={() => document.getElementById('property-photo-input')?.click()}
          >
            <input
              id="property-photo-input"
              type="file"
              accept="image/*"
              multiple
              className="hidden"
              onChange={(e) => handleFiles(e.target.files)}
            />
            <p className="text-gray-500 font-medium">
              {files.length > 0
                ? `${files.length} תמונות נבחרו (הראשונה תהיה ראשית)`
                : 'גרור לכאן 5 תמונות (או לחץ לבחירה)'}
            </p>
            <span className="text-[10px] text-gray-400">התמונה הראשונה תהיה הראשית</span>
          </div>

          {/* הזנת נתונים בסיסיים */}
          <div className="grid grid-cols-2 gap-4 mb-8">
            <input
              type="text"
              className="bg-gray-50 p-4 rounded-2xl border-none"
              placeholder="שם הנכס (למשל: סוויטה על הים)"
              value={name}
              onChange={(e) => setName(e.target.value)}
            />
            <input
              type="number"
              className="bg-gray-50 p-4 rounded-2xl border-none"
              placeholder="מחיר ללילה (₪)"
              value={price}
              onChange={(e) => setPrice(e.target.value)}
            />
          </div>

          {/* רשימת צ'קבוקסים */}
          <div className="mb-8">
            <p className="font-bold mb-4 text-sm">מה יש בנכס?</p>
            <div className="grid grid-cols-3 gap-3">
              {AMENITIES.map((item) => (
                <label
                  key={item}
                  className={`flex items-center gap-2 p-3 rounded-xl cursor-pointer transition-colors ${
                    amenities[item] ? 'bg-yellow-100' : 'bg-gray-50 hover:bg-yellow-50'
                  }`}
                >
                  <input
                    type="checkbox"
                    className="accent-yellow-400"
                    checked={!!amenities[item]}
                    onChange={() => toggleAmenity(item)}
                  />
                  <span className="text-xs font-bold">{item}</span>
                </label>
              ))}
            </div>
          </div>

          {error && <p className="text-red-600 text-sm mb-4">{error}</p>}

          {/* כפתור הפעלה */}
          <button
            type="button"
            onClick={handleSubmit}
            disabled={isSubmitting}
            className="w-full bg-yellow-400 hover:bg-yellow-500 text-black font-black py-5 rounded-2xl shadow-lg transition-transform active:scale-95 disabled:opacity-60 disabled:cursor-not-allowed"
          >
            {isSubmitting ? 'יוצר...' : 'צור דף נכס והפעל אוטומציה'}
          </button>
        </div>
      </div>
    </div>
  );
}
