import React, { useEffect, useState } from 'react';
import { createPortal } from 'react-dom';
import {
  Building2, TrendingUp, Users, Calendar, DollarSign,
  Activity, Clock, CheckCircle2, AlertCircle, Sparkles,
  BarChart3, PieChart, ArrowUpRight, ArrowDownRight,
  Bot, Zap, Target, FileText, ExternalLink, Check, Brush,
  MessageCircle, ToggleLeft, ToggleRight, Home, Globe
} from 'lucide-react';
import {
  AreaChart, Area, BarChart, Bar, PieChart as RechartsPie, Pie, Cell,
  XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer, Legend
} from 'recharts';
import i18n from '../../i18n';
import useStore from '../../store/useStore';
import { updateRoomStatus, getDailyReport, getAutomationStats, subscribeToLeads, connectCalendar, getCalendarStatus, getPilotAccessToken, getStaffList, subscribeToStaff, createManualCheckout, getManualRooms, createManualRoom, checkoutManualRoom, checkinManualRoom, getManualRoomHistory } from '../../services/api';
import { whatsappService } from '../../services/whatsapp';
import './EnterpriseDashboard.css';

const occupancyData = [
  { nameKey: 'dashboard.rooms.status.occupied', value: 72, color: '#10b981' },
  { nameKey: 'dashboard.rooms.status.available', value: 18, color: '#3b82f6' },
  { nameKey: 'dashboard.rooms.status.maintenance', value: 6, color: '#f59e0b' },
  { nameKey: 'dashboard.rooms.status.cleaning', value: 4, color: '#8b5cf6' },
];

const agentPerformance = [
  { name: 'Scraper', tasks: 156, success: 148 },
  { name: 'Marketing', tasks: 89, success: 82 },
  { name: 'Creative', tasks: 45, success: 43 },
  { name: 'Sales', tasks: 67, success: 58 },
  { name: 'Operations', tasks: 234, success: 228 },
];

const EnterpriseDashboard = () => {
  const {
    stats,
    rooms,
    agents,
    leads,
    notifications,
    automationStats,
    setAutomationStats,
    setObjectionSuccess,
    setGeneratingReport,
    isGeneratingReport,
    lang,
    updateRoom,
    activeTenantId,
    authToken,
    calendarMode,
    setCalendarMode,
  } = useStore();
  const t = (key, options) => i18n.t(key, options);
  const [selectedRoom, setSelectedRoom] = useState(null);
  const [roomActionLoading, setRoomActionLoading] = useState(false);
  const [roomStatusLoading, setRoomStatusLoading] = useState(null);
  const [reportDataLocal, setReportDataLocal] = useState(null);
  const [calendarStatus, setCalendarStatus] = useState({ synced: false, vacant_nights: 0, potential_revenue: 0 });
  const [icalUrl, setIcalUrl] = useState('');
  const [nightlyRate, setNightlyRate] = useState(250);
  const [isSyncingCalendar, setIsSyncingCalendar] = useState(false);
  const [pilotName, setPilotName] = useState('');
  const [pilotToken, setPilotToken] = useState('');
  const [pilotLink, setPilotLink] = useState('');
  const [isGeneratingPilot, setIsGeneratingPilot] = useState(false);
  const [pilotCopied, setPilotCopied] = useState(false);
  const [staffList, setStaffList] = useState([]);
  const [staffLoading, setStaffLoading] = useState(false);
  const [showGuide, setShowGuide] = useState(false);
  const [showSecurity, setShowSecurity] = useState(false);
  const [guideTab, setGuideTab] = useState('airbnb');
  const [manualCheckoutDate, setManualCheckoutDate] = useState('');
  const [manualRoom, setManualRoom] = useState('');
  const [isManualCreating, setIsManualCreating] = useState(false);
  const [isDemoRunning, setIsDemoRunning] = useState(false);
  const [manualRooms, setManualRooms] = useState([]);
  const [manualRoomName, setManualRoomName] = useState('');
  const [manualRoomLoading, setManualRoomLoading] = useState(false);
  const [manualCheckoutLoading, setManualCheckoutLoading] = useState(null);
  const [manualCheckinLoading, setManualCheckinLoading] = useState(null);
  const [historyRoom, setHistoryRoom] = useState(null);
  const [historyItems, setHistoryItems] = useState([]);
  const [historyLoading, setHistoryLoading] = useState(false);

  useEffect(() => {
    if (lang && i18n.language !== lang) {
      i18n.changeLanguage(lang);
    }
  }, [lang]);

  useEffect(() => {
    let isActive = true;
    const loadAutomationStats = async () => {
      try {
        const statsResponse = await getAutomationStats();
        if (!isActive) return;
        setAutomationStats(statsResponse.automation_stats);
        setObjectionSuccess(statsResponse.objection_success);
      } catch (error) {
        console.error('Error fetching automation stats:', error);
      }
    };

    const loadReport = async () => {
      try {
        const reportResponse = await getDailyReport();
        if (!isActive) return;
        setReportDataLocal(normalizeReport(reportResponse));
      } catch (error) {
        console.error('Error fetching report:', error);
      }
    };

    loadAutomationStats();
    loadReport();
    const eventSource = subscribeToLeads(
      () => {},
      () => {},
      (error) => {
        console.error('Automation stats stream error:', error);
      },
      (statsUpdate) => {
        setAutomationStats(statsUpdate);
      }
    );

    return () => {
      isActive = false;
      if (eventSource && eventSource.close) eventSource.close();
    };
  }, [setAutomationStats, setObjectionSuccess, activeTenantId, authToken]);

  useEffect(() => {
    let isActive = true;
    const loadCalendarStatus = async () => {
      try {
        const status = await getCalendarStatus();
        if (isActive) {
          setCalendarStatus(status);
        }
      } catch (error) {
        if (isActive) {
          setCalendarStatus({ synced: false, vacant_nights: 0, potential_revenue: 0 });
        }
      }
    };
    loadCalendarStatus();
    return () => {
      isActive = false;
    };
  }, [activeTenantId, authToken]);

  useEffect(() => {
    let mounted = true;
    const loadRooms = async () => {
      try {
        const data = await getManualRooms();
        if (mounted) setManualRooms(data || []);
      } catch (error) {
        console.error('Failed to load manual rooms:', error);
      }
    };
    loadRooms();
    return () => {
      mounted = false;
    };
  }, [activeTenantId, authToken]);

  useEffect(() => {
    let mounted = true;
    const loadStaff = async () => {
      setStaffLoading(true);
      try {
        const data = await getStaffList();
        if (mounted) setStaffList(data || []);
      } catch (error) {
        console.error('Failed to load staff list:', error);
      } finally {
        if (mounted) setStaffLoading(false);
      }
    };
    loadStaff();
    const source = subscribeToStaff((update) => {
      setStaffList((prev) => {
        const next = [...prev];
        const index = next.findIndex((item) => item.id === update.id);
        if (index >= 0) {
          next[index] = { ...next[index], ...update };
        } else {
          next.unshift(update);
        }
        return next;
      });
    });
    return () => {
      mounted = false;
      if (source && source.close) source.close();
    };
  }, [activeTenantId, authToken]);

  const handleSyncCalendar = async () => {
    if (!icalUrl) return;
    setIsSyncingCalendar(true);
    try {
      const status = await connectCalendar(icalUrl, nightlyRate);
      setCalendarStatus({ ...status, synced: true });
    } catch (error) {
      console.error('Failed to sync calendar:', error);
    } finally {
      setIsSyncingCalendar(false);
    }
  };

  const handleManualCheckout = async () => {
    setIsManualCreating(true);
    try {
      const date = manualCheckoutDate || new Date().toISOString().split('T')[0];
      await createManualCheckout(date, manualRoom);
      setManualCheckoutDate('');
      setManualRoom('');
    } catch (error) {
      console.error('Failed to create manual checkout:', error);
    } finally {
      setIsManualCreating(false);
    }
  };

  const handleCreateRoom = async () => {
    if (!manualRoomName.trim()) return;
    setManualRoomLoading(true);
    try {
      const room = await createManualRoom(manualRoomName.trim());
      setManualRooms((prev) => [room, ...prev]);
      setManualRoomName('');
    } catch (error) {
      console.error('Failed to create manual room:', error);
    } finally {
      setManualRoomLoading(false);
    }
  };

  const handleCheckoutRoom = async (roomId) => {
    setManualCheckoutLoading(roomId);
    try {
      await checkoutManualRoom(roomId);
      setManualRooms((prev) =>
        prev.map((room) =>
          room.id === roomId ? { ...room, last_checkout_at: new Date().toISOString() } : room
        )
      );
    } catch (error) {
      console.error('Failed to checkout room:', error);
    } finally {
      setManualCheckoutLoading(null);
    }
  };

  const handleDemoSimulation = async () => {
    setIsDemoRunning(true);
    try {
      const today = new Date();
      const dateStr = today.toISOString().split('T')[0];
      await createManualCheckout(dateStr, 'Demo Suite 301');
      setCalendarStatus({
        synced: true,
        vacant_nights: 12,
        potential_revenue: 3000,
      });
      setReportDataLocal({
        date: dateStr,
        roi_metrics: {
          hours_saved: 14,
          cleanings_triggered: 3,
          leads_captured: 7,
        },
        automation_interactions: 32,
        message_activity: { inbound: 12, outbound: 20 },
        revenue_summary: { paid_revenue: 5200, potential_revenue: 3000 },
        recommendations: ['AI concierge ready for peak season.'],
        agent_performance: [],
        savings: { automation_rate: 86 },
      });
      setAutomationStats({
        automated_messages: 20,
        leads_total: 7,
        last_scan: new Date().toISOString(),
      });
    } catch (error) {
      console.error('Failed to run demo simulation:', error);
    } finally {
      setIsDemoRunning(false);
    }
  };

  const handleGeneratePilot = async () => {
    if (!pilotName) return;
    setIsGeneratingPilot(true);
    try {
      const result = await getPilotAccessToken(pilotName);
      const token = result.token || '';
      setPilotToken(token);
      if (token) {
        setPilotLink(`${window.location.origin}/?token=${token}`);
      }
    } catch (error) {
      console.error('Failed to generate pilot token:', error);
    } finally {
      setIsGeneratingPilot(false);
    }
  };

  const handleCopyPilotLink = async () => {
    const value = pilotLink || pilotToken;
    if (!value) return;
    try {
      if (navigator?.clipboard?.writeText) {
        await navigator.clipboard.writeText(value);
      } else {
        const temp = document.createElement('input');
        temp.value = value;
        document.body.appendChild(temp);
        temp.select();
        document.execCommand('copy');
        temp.remove();
      }
      setPilotCopied(true);
      setTimeout(() => setPilotCopied(false), 1500);
    } catch (error) {
      console.error('Failed to copy pilot link:', error);
    }
  };

  const handleGenerateReport = async () => {
    if (isGeneratingReport) return;
    setReportDataLocal(null);
    setGeneratingReport(true);
    try {
      const reportResponse = await getDailyReport();
      const normalizedReport = normalizeReport(reportResponse);
      setReportDataLocal(normalizedReport || null);
    } catch (error) {
      console.error('Error generating report:', error);
    } finally {
      setGeneratingReport(false);
    }
  };

  const handleDownloadReport = async () => {
    try {
      const reportResponse = await getDailyReport();
      const payload = normalizeReport(reportResponse) || reportResponse;
      const blob = new Blob([JSON.stringify(payload, null, 2)], { type: 'application/json' });
      const url = window.URL.createObjectURL(blob);
      const anchor = document.createElement('a');
      anchor.href = url;
      anchor.download = `daily-report-${payload?.date || new Date().toISOString().split('T')[0]}.json`;
      document.body.appendChild(anchor);
      anchor.click();
      anchor.remove();
      window.URL.revokeObjectURL(url);
    } catch (error) {
      console.error('Failed to download report:', error);
    }
  };

  const isRTL = lang === 'he';
  const safeRooms = Array.isArray(rooms) ? rooms : [];
  const safeLeads = Array.isArray(leads) ? leads : [];
  const safeNotifications = Array.isArray(notifications) ? notifications : [];
  const reportData = normalizeReport(reportDataLocal) || normalizeReport({});
  const automationRate = reportData?.savings?.automation_rate ?? 0;
  const isReportOpen = Boolean(reportDataLocal);

  const dealsClosed = safeLeads.filter((lead) => ['paid', 'converted'].includes(lead.status || '')).length;
  const closedValue = safeLeads
    .filter((lead) => ['paid', 'converted'].includes(lead.status || ''))
    .reduce((sum, lead) => sum + (lead.value || 0), 0);
  const reportHoursSaved = reportData?.roi_metrics?.hours_saved ?? 0;
  const ROI_HOURLY_RATE = 200;
  const estimatedROI = Math.round(reportHoursSaved * ROI_HOURLY_RATE + closedValue);
  const leadsFoundTotal = automationStats?.leads_total ?? safeLeads.length;
  const messagesSent = automationStats?.automated_messages ?? 0;
  const liveHoursSaved = Math.round(((messagesSent * 10) / 60) * 10) / 10;
  const calendarStatusClass = calendarStatus?.synced ? 'calendar-status online' : 'calendar-status offline';
  const potentialRevenue = calendarStatus?.synced
    ? (calendarStatus?.vacant_nights ?? 0) * nightlyRate
    : (calendarStatus?.potential_revenue ?? 0);
  const inboundMessages = reportData?.message_activity?.inbound ?? 0;
  const outboundMessages = reportData?.message_activity?.outbound ?? 0;
  const paidRevenue = reportData?.revenue_summary?.paid_revenue ?? 0;
  const revenueSeries = reportData
    ? [{ name: reportData.date, revenue: estimatedROI, bookings: reportData.roi_metrics.leads_captured }]
    : [
        { name: 'Jan', revenue: 320000, bookings: 42 },
        { name: 'Feb', revenue: 280000, bookings: 38 },
        { name: 'Mar', revenue: 350000, bookings: 48 },
      ];

  const waiting = 'Waiting for sync';
  const heroCards = [
    {
      id: 'hours-saved',
      title: t('dashboard.report.hoursSaved'),
      value: reportHoursSaved > 0 ? reportHoursSaved : waiting,
      subtitle: t('dashboard.report.humanHoursSaved'),
      trend: reportHoursSaved > 0 ? reportHoursSaved : 0,
      icon: Clock,
      color: 'gold',
      image: safeRooms[0]?.image,
    },
    {
      id: 'automation-rate',
      title: t('dashboard.stats.automationRate'),
      value: automationRate > 0 ? `${automationRate}%` : waiting,
      subtitle: t('dashboard.report.automationMessage', { rate: automationRate }),
      trend: automationRate > 0 ? automationRate : 0,
      icon: Zap,
      color: 'emerald',
      image: safeRooms[1]?.image,
    },
    {
      id: 'paid-revenue',
      title: t('dashboard.stats.revenueToday'),
      value: paidRevenue > 0 ? `₪${paidRevenue.toLocaleString()}` : waiting,
      subtitle: t('dashboard.charts.revenue'),
      trend: paidRevenue > 0 ? 8 : 0,
      icon: DollarSign,
      color: 'blue',
      image: safeRooms[2]?.image,
    },
    {
      id: 'leads-captured',
      title: t('dashboard.report.leadsCaptured'),
      value: reportData?.roi_metrics?.leads_captured ? reportData.roi_metrics.leads_captured : waiting,
      subtitle: t('dashboard.charts.bookings'),
      trend: reportData?.roi_metrics?.leads_captured ? 6 : 0,
      icon: Users,
      color: 'purple',
      image: safeRooms[3]?.image,
    },
  ];

  const handleDownloadPdf = async () => {
    try {
      const reportResponse = await getDailyReport();
      const payload = normalizeReport(reportResponse) || reportResponse;
      const html = `
        <html>
          <head>
            <title>Daily Report</title>
            <style>
              body { font-family: Arial, sans-serif; padding: 24px; color: #0b0f14; }
              h1 { margin-bottom: 8px; }
              .meta { color: #475569; margin-bottom: 24px; }
              .grid { display: grid; grid-template-columns: repeat(2, minmax(0, 1fr)); gap: 16px; }
              .card { border: 1px solid #e2e8f0; padding: 12px 16px; border-radius: 12px; }
              .label { color: #64748b; font-size: 12px; text-transform: uppercase; letter-spacing: 0.08em; }
              .value { font-size: 20px; font-weight: 600; margin-top: 6px; }
              ul { padding-left: 18px; }
            </style>
          </head>
          <body>
            <h1>Daily ROI Report</h1>
            <div class="meta">Window: ${payload?.window_start || ''} → ${payload?.window_end || ''}</div>
            <div class="grid">
              <div class="card"><div class="label">Hours Saved</div><div class="value">${payload?.roi_metrics?.hours_saved ?? 0}</div></div>
              <div class="card"><div class="label">Leads Captured</div><div class="value">${payload?.roi_metrics?.leads_captured ?? 0}</div></div>
              <div class="card"><div class="label">Inbound Messages</div><div class="value">${payload?.message_activity?.inbound ?? 0}</div></div>
              <div class="card"><div class="label">Outbound Messages</div><div class="value">${payload?.message_activity?.outbound ?? 0}</div></div>
              <div class="card"><div class="label">Paid Revenue</div><div class="value">₪${(payload?.revenue_summary?.paid_revenue ?? 0).toLocaleString()}</div></div>
              <div class="card"><div class="label">Automation Interactions</div><div class="value">${payload?.automation_interactions ?? 0}</div></div>
            </div>
            <h3 style="margin-top: 24px;">Recommendations</h3>
            <ul>
              ${(payload?.recommendations || []).map((rec) => `<li>${rec}</li>`).join('')}
            </ul>
            <div style="page-break-before: always;"></div>
            <h1>Security Certificate</h1>
            <div class="meta">Data encryption confirmation</div>
            <div class="card" style="margin-bottom: 12px;">
              <div class="label">Encryption</div>
              <div class="value">Enterprise-grade encryption at rest in PostgreSQL</div>
            </div>
            <div class="card" style="margin-bottom: 12px;">
              <div class="label">Access Scope</div>
              <div class="value">Read-only iCal access — no payments or guest PII</div>
            </div>
            <div class="card">
              <div class="label">Issued</div>
              <div class="value">${new Date().toISOString()}</div>
            </div>
          </body>
        </html>
      `;
      const popup = window.open('', '_blank');
      if (!popup) return;
      popup.document.write(html);
      popup.document.close();
      popup.focus();
      popup.print();
    } catch (error) {
      console.error('Failed to download PDF report:', error);
    }
  };

  const getNextStatus = (current) => {
    const cycle = ['available', 'occupied', 'cleaning', 'maintenance'];
    const currentIndex = cycle.indexOf(current);
    const nextIndex = currentIndex === -1 ? 0 : (currentIndex + 1) % cycle.length;
    return cycle[nextIndex];
  };

  const getStaffPosition = (staffId = '') => {
    let hash = 0;
    for (let i = 0; i < staffId.length; i += 1) {
      hash = (hash * 31 + staffId.charCodeAt(i)) % 1000;
    }
    const x = 10 + (hash % 80);
    const y = 10 + (Math.floor(hash / 10) % 80);
    return { x, y };
  };

  const activeStaff = staffList.filter((member) => member.active && member.on_shift);

  const managementAlerts = buildManagementAlerts({
    leads: safeLeads,
    notifications: safeNotifications,
    t,
  });

  return (
    <div
      className="enterprise-dashboard"
      dir={isRTL ? 'rtl' : 'ltr'}
    >
      <div className="calendar-connection glass-card">
        <div className="calendar-connection-header">
          <h3><Calendar size={20} /> Calendar Connection</h3>
          <span className={calendarMode === 'manual' ? 'calendar-status online' : calendarStatusClass}>
            {calendarMode === 'manual' ? 'Manual Mode' : calendarStatus?.synced ? 'Synced' : 'Not Connected'}
          </span>
        </div>
        <div className="calendar-connection-body">
          <div className="calendar-mode-toggle">
            <span>Mode</span>
            <div className="mode-toggle-buttons">
              <button
                className={`mode-toggle-btn ${calendarMode === 'ical' ? 'active' : ''}`}
                onClick={() => setCalendarMode('ical')}
              >
                iCal Mode
              </button>
              <button
                className={`mode-toggle-btn ${calendarMode === 'manual' ? 'active' : ''}`}
                onClick={() => setCalendarMode('manual')}
              >
                Manual Mode
              </button>
            </div>
          </div>
          <div className="calendar-metrics">
            <div className="metric">
              <span className="label">Vacant Nights</span>
              <span className="value">{calendarStatus?.vacant_nights ?? 0}</span>
            </div>
            <div className="metric">
              <span className="label">Potential Revenue</span>
              <span className="value">₪{potentialRevenue.toLocaleString()}</span>
            </div>
          </div>
          <div className="calendar-form">
            <input
              type="url"
              placeholder="Paste Airbnb/Booking iCal URL"
              value={icalUrl}
              onChange={(e) => setIcalUrl(e.target.value)}
            />
            <input
              type="number"
              min="0"
              value={nightlyRate}
              onChange={(e) => setNightlyRate(Number(e.target.value))}
            />
            <div className="calendar-connect-actions">
              <button className="btn-primary" onClick={handleSyncCalendar} disabled={isSyncingCalendar}>
                {isSyncingCalendar ? t('common.loading') : 'Connect iCal'}
              </button>
              <button className="help-icon-btn" onClick={() => setShowGuide(true)} aria-label="Open iCal guide">
                ?
              </button>
            </div>
          </div>
          <div className="calendar-trust">
            <span>We only read check-out dates. No access to payments or personal guest data.</span>
            <span>Stored with enterprise-grade encryption in our PostgreSQL database.</span>
          </div>
          <div className="calendar-actions-row">
            <button className="calendar-manual-link" onClick={() => setShowGuide(true)}>
            Or enter a manual checkout for testing
            </button>
            <button className="calendar-manual-link" onClick={() => setShowSecurity(true)}>
              Why iCal is safe?
            </button>
            <button
              className="btn-secondary"
              onClick={handleDemoSimulation}
              disabled={isDemoRunning}
            >
              {isDemoRunning ? t('common.loading') : 'נסה ללא חיבור יומן (מצב דמו)'}
            </button>
            <a
              className="btn-secondary"
              href="https://wa.me/972503233332"
              target="_blank"
              rel="noreferrer"
            >
              צריך עזרה בחיבור?
            </a>
          </div>
          <div className="calendar-form">
            <input
              type="text"
              placeholder="Pilot owner name"
              value={pilotName}
              onChange={(e) => setPilotName(e.target.value)}
            />
            <input
              type="text"
              readOnly
              value={pilotLink || pilotToken}
              placeholder="Pilot access link"
            />
            <button className="btn-secondary" onClick={handleGeneratePilot} disabled={isGeneratingPilot}>
              {isGeneratingPilot ? t('common.loading') : 'Generate Pilot Access'}
            </button>
            <button className="btn-primary" onClick={handleCopyPilotLink} disabled={!pilotLink && !pilotToken}>
              {pilotCopied ? 'Copied' : 'Copy Link'}
            </button>
          </div>
        </div>
      </div>

      {showGuide && (
        <div className="report-modal-backdrop is-open" onClick={() => setShowGuide(false)}>
          <div className="report-modal glass-card" onClick={(e) => e.stopPropagation()}>
            <div className="report-header">
              <h2>Connect iCal in 3 steps</h2>
