import React, { useEffect, useMemo, useRef, useState } from 'react';
import './EnterpriseDashboard.css';

const LIVINGSTON = {
  id: 'airbnb-29760773',
  name: 'Livingston Luxury Pool House',
  price: 4115,
  guests: 3,
  image:
    'https://a0.muscache.com/im/pictures/airflow/Hosting-29760773/original/1b4a0a4c-1d3a-47f8-ae9a-7f6a6c4a2c91.jpeg',
};
const PLACEHOLDER_IMAGE =
  'https://images.unsplash.com/photo-1505693416388-ac5ce068fe85?w=1400&auto=format&fit=crop';
const SAUSALITO = {
  id: 'airbnb-845744858638105513',
  name: "Sausalito Skylounge",
  titleHe: "טיולים בפארק לאומי ונופים של האוקיינוס במרחק דקות מסן פרנסיסקו",
  location: "סאוסליטו, קליפורניה, ארצות הברית",
  price: 1175,
  guests: 2,
  bedrooms: 1,
  beds: 1,
  bathrooms: 1,
  rating: 5.0,
  reviews: 10,
  host: "Roxie And John",
  highlights: [
    "צ׳ק-אין עצמי בעזרת קודן",
    "נוף של אוקיינוס ושל הר",
    "בית מועדף על ידי אורחים",
  ],
  amenities: [
    "Wi‑Fi",
    "מטבח",
    "קמין ביולוגי",
    "מזגן/חימום בשלט",
    "מכונת כביסה ומייבש",
    "טלוויזיית HD 50 אינץ׳",
    "חניה חינם במקום",
    "פינת עבודה ייעודית",
  ],
  photoId: "1634563768",
};
const normalizeTitle = (url) => {
  try {
    const parsed = new URL(url);
    const parts = parsed.pathname.split('/').filter(Boolean);
    const roomsIndex = parts.indexOf('rooms');
    const slug = roomsIndex !== -1 ? parts[roomsIndex + 1] : parts[parts.length - 1];
    if (!slug) return 'Property Preview';
    if (/^\d+$/.test(slug)) return `Property ${slug}`;
    return slug
      .replace(/[-_]/g, ' ')
      .replace(/\s+/g, ' ')
      .trim();
  } catch {
    return 'Property Preview';
  }
};

const extractPhotoId = (url) => {
  try {
    const parsed = new URL(url);
    return parsed.searchParams.get('photo_id') || '';
  } catch {
    const match = String(url).match(/photo_id=(\d+)/i);
    return match ? match[1] : '';
  }
};

/** Universal parser: extract Property ID from ANY Airbnb URL format */
const extractPropertyId = (text) => {
  const str = String(text || '').trim();
  if (!str) return '';
  const patterns = [
    /(?:airbnb\.com|airbnb\.[a-z]{2,})\/rooms\/(\d+)/i,
    /\/rooms\/(\d+)/i,
    /\/listings\/(\d+)/i,
    /(?:rooms|listings)[\/=](\d+)/i,
    /[?&]id=(\d+)/i,
    /\b(\d{7,20})\b/,
  ];
  for (const re of patterns) {
    const m = str.match(re);
    if (m) return m[1];
  }
  return '';
};

const extractTitleFromText = (text) => {
  const lines = String(text)
    .split('\n')
    .map((line) => line.trim())
    .filter(Boolean);
  const candidate = lines.find((line) => line.length >= 8 && !/airbnb/i.test(line));
  return candidate || '';
};

const TABS = [
  { id: 'maya', label: 'Maya (Manager)' },
  { id: 'lead', label: 'Lead Scout' },
  { id: 'outreach', label: 'Outreach' },
  { id: 'demo', label: 'Demo Agent' },
];

const EnterpriseDashboard = () => {
  const [activeAgentTab, setActiveAgentTab] = useState('maya');
  const [demoLink, setDemoLink] = useState('');
  const [demoPreview, setDemoPreview] = useState(null);
  const [cleanerPhone, setCleanerPhone] = useState('');
  const [maintenancePhone, setMaintenancePhone] = useState('');
  const [frontDeskPhone, setFrontDeskPhone] = useState('');
  const [restaurantPhone, setRestaurantPhone] = useState('');
  const [activationSuccess, setActivationSuccess] = useState(false);
  const [propertyStatus, setPropertyStatus] = useState('Cleaning');
  const [staffRolePreview, setStaffRolePreview] = useState('cleaner');
  const [isScanning, setIsScanning] = useState(false);
  const [emailPreviewOpen, setEmailPreviewOpen] = useState(false);
  const [propertyImages, setPropertyImages] = useState([PLACEHOLDER_IMAGE]);
  const [roomDetails, setRoomDetails] = useState({
    rooms: 0,
    bathrooms: 0,
    amenities: [],
  });
  const [propertyMeta, setPropertyMeta] = useState(null);
  const [showFullDetails, setShowFullDetails] = useState(false);
  const [staffSyncStatus, setStaffSyncStatus] = useState({
    cleaner: 'idle',
    maintenance: 'idle',
    frontDesk: 'idle',
    restaurant: 'idle',
  });
  const staffSyncTimers = useRef({});
  const activationRef = useRef(null);

  const propertyLoaded = useMemo(() => !!demoPreview, [demoPreview]);
  const showSuccessView = useMemo(() => isScanning || propertyLoaded, [isScanning, propertyLoaded]);
  const displayPreview = useMemo(() => (isScanning ? null : demoPreview), [isScanning, demoPreview]);

  useEffect(() => {
    if (!propertyLoaded) return;
    if (!activationRef.current) return;
    activationRef.current.scrollIntoView({ behavior: 'smooth', block: 'start' });
  }, [propertyLoaded, demoLink]);

  const applyProperty = (property, details, images, meta) => {
    setDemoPreview(property);
    setPropertyImages(images);
    setRoomDetails(details);
    setActiveAgentTab('demo');
    setActivationSuccess(false);
    setEmailPreviewOpen(false);
    setPropertyMeta(meta || null);
    setShowFullDetails(false);
  };

  const startStaffSync = (role, phone) => {
    if (!phone) {
      setStaffSyncStatus((prev) => ({ ...prev, [role]: 'idle' }));
      return;
    }
    if (staffSyncTimers.current[role]) {
      clearTimeout(staffSyncTimers.current[role]);
    }
    setStaffSyncStatus((prev) => ({ ...prev, [role]: 'syncing' }));
    staffSyncTimers.current[role] = setTimeout(() => {
      setStaffSyncStatus((prev) => ({ ...prev, [role]: 'connected' }));
    }, 1200);
  };

  const handleImport = (input) => {
    const raw = String(input ?? demoLink).trim();
    if (raw.length < 5) return;

    const propertyId = extractPropertyId(raw);
    const photoId = extractPhotoId(raw);

    setIsScanning(true);
    setActiveAgentTab('demo');

    const ANALYZE_DURATION = 2500;
    setTimeout(() => {
      try {
        if (propertyId === '29760773' || propertyId === '1583655598370893087') {
          applyProperty(
            LIVINGSTON,
            { rooms: 3, bathrooms: 2, amenities: ['Pool', 'Garden', 'Self Check-in'] },
            [
              LIVINGSTON.image,
              'https://images.unsplash.com/photo-1505693416388-ac5ce068fe85?w=1000&auto=format&fit=crop',
              'https://images.unsplash.com/photo-1502672023488-70e25813eb80?w=1000&auto=format&fit=crop',
            ],
            null
          );
        } else if (propertyId === '845744858638105513') {
          const primary = SAUSALITO.photoId
            ? `https://a0.muscache.com/im/pictures/${SAUSALITO.photoId}.jpg?im_w=1200`
            : PLACEHOLDER_IMAGE;
          applyProperty(
            { id: SAUSALITO.id, name: SAUSALITO.name, price: SAUSALITO.price, guests: SAUSALITO.guests, image: primary },
            { rooms: SAUSALITO.bedrooms, bathrooms: SAUSALITO.bathrooms, amenities: SAUSALITO.amenities },
            [primary, PLACEHOLDER_IMAGE, PLACEHOLDER_IMAGE],
            { titleHe: SAUSALITO.titleHe, location: SAUSALITO.location, rating: SAUSALITO.rating, reviews: SAUSALITO.reviews, host: SAUSALITO.host, bedrooms: SAUSALITO.bedrooms, beds: SAUSALITO.beds, bathrooms: SAUSALITO.bathrooms, highlights: SAUSALITO.highlights, amenities: SAUSALITO.amenities }
          );
        } else {
          const id = propertyId || `temp-${Date.now()}`;
          const title = propertyId ? `Property Luxury Suite #${propertyId}` : 'Property Luxury Suite';
          const image = photoId
            ? `https://a0.muscache.com/im/pictures/${photoId}.jpg?im_w=1200`
            : 'https://images.unsplash.com/photo-1613977257363-707ba9348227?w=1400&auto=format&fit=crop';
          applyProperty(
            { id: `airbnb-${id}`, name: title, price: 1175, guests: 2, image },
            { rooms: 2, bathrooms: 1, amenities: ['Wi‑Fi', 'Kitchen', 'Self Check-in'] },
            [image, PLACEHOLDER_IMAGE, PLACEHOLDER_IMAGE],
            null
          );
        }
      } catch {
        applyProperty(
          { id: `airbnb-fallback-${Date.now()}`, name: 'Looking for the property... almost there', price: 0, guests: 0, image: PLACEHOLDER_IMAGE },
          { rooms: 0, bathrooms: 0, amenities: [] },
          [PLACEHOLDER_IMAGE],
          null
        );
      } finally {
        setIsScanning(false);
      }
    }, ANALYZE_DURATION);
  };

  const handleImportLink = (input) => {
    const raw = String(input ?? demoLink).trim();
    if (raw.length < 5) return false;
    handleImport(raw);
    return true;
  };

  const demoInputRef = useRef(null);
  useEffect(() => {
    if (activeAgentTab === 'demo' && demoInputRef.current) {
      demoInputRef.current.focus();
    }
  }, [activeAgentTab]);

  const handleActivate = () => {
    setActivationSuccess(true);
    setTimeout(() => setActivationSuccess(false), 2000);
  };

  return (
    <div dir="rtl" className="maya-command-center rtl-layout">
      <div className="maya-layout" style={{ display: 'flex', flexDirection: 'row-reverse' }}>
        <aside className="maya-sidebar">
          <div className="maya-sidebar-title">Maya Command Center</div>
          {TABS.map((tab) => (
            <button
              key={tab.id}
              className={`maya-nav-item ${activeAgentTab === tab.id ? 'active' : ''}`}
              onClick={() => setActiveAgentTab(tab.id)}
            >
              {tab.label}
            </button>
          ))}
        </aside>

        <main className="maya-main">
          <div
            className="demo-input-card"
            style={{
              background: '#ffffff',
              border: '2px solid #111827',
              marginBottom: '1.5rem',
            }}
          >
            <label className="demo-label">Airbnb Link</label>
            <div style={{ display: 'flex', gap: '0.5rem', alignItems: 'center' }}>
              <input
                ref={demoInputRef}
                type="url"
                value={demoLink}
                onChange={(e) => setDemoLink(e.target.value)}
                onPaste={(e) => {
                  const pasted = e.clipboardData.getData('text');
                  setDemoLink(pasted);
                  handleImportLink(pasted);
                }}
                className="demo-link-input"
                placeholder="הדבק כאן קישור Airbnb של הלקוח..."
                style={{ direction: 'rtl', textAlign: 'right', flex: 1 }}
              />
              <button
                type="button"
                className="btn-primary"
                onClick={() => handleImportLink()}
                disabled={!demoLink.trim() || demoLink.trim().length < 5}
              >
                Import
              </button>
            </div>
            {isScanning && (
              <div className="maya-analyzing" style={{ marginTop: '0.75rem' }}>
                <div style={{ fontWeight: 600, marginBottom: '0.5rem' }}>Maya AI Analyzing Property...</div>
                <div style={{ height: 6, background: '#e2e8f0', borderRadius: 3, overflow: 'hidden' }}>
                  <div
                    style={{
                      height: '100%',
                      width: '70%',
                      background: 'linear-gradient(90deg, #d4af37, #f9e27d)',
                      borderRadius: 3,
                      animation: 'maya-progress 2.5s ease-out forwards',
                    }}
                  />
                </div>
              </div>
            )}
          </div>
          <div className="maya-profile-card">
            <div className="maya-avatar">M</div>
            <div>
              <div className="maya-name">Maya</div>
              <div className="maya-role">Manager AI</div>
            </div>
          </div>

          {!showSuccessView && (
            <>
              <div className="agent-panel">
                <h2>Current Task</h2>
                <p>
                  Automating guest communications for Livingston Luxury Suite.
                  Estimated savings: 4 hours/day.
                </p>
              </div>

              {activeAgentTab === 'demo' && (
                <div className="demo-input-card">
                  <label className="demo-label">Demo Agent Link</label>
                  <input
                    type="url"
                    value={demoLink}
                    onChange={(e) => handleDemoLinkChange(e.target.value)}
                    className="demo-link-input"
                    placeholder="Paste Airbnb link here..."
                  />
                </div>
              )}
            </>
          )}

          {showSuccessView && (
            <div className="success-view">
              <div className="property-hero-card" style={{ maxWidth: 920, margin: '0 auto' }}>
                <div
                  className="property-hero-image"
                  style={{ backgroundImage: `url(${displayPreview?.image || PLACEHOLDER_IMAGE})` }}
                >
                  <span className="property-badge">{propertyStatus}</span>
                </div>
                <div className="property-hero-body">
                  <div className="property-hero-title">
                    {displayPreview?.name || (isScanning ? 'Scanning Property…' : 'Property Preview')}
                  </div>
                  <div className="property-hero-meta">
                    <span className="price">
                      ₪{(displayPreview?.price || 0).toLocaleString()}
                    </span>
                    <span className="per-night">per night</span>
                    <span className="guests">{displayPreview?.guests || 0} guests</span>
                  </div>
                </div>
              </div>

            <div
              className="stat-card"
              style={{
                maxWidth: 920,
                margin: '1.5rem auto 0',
                display: 'grid',
                gridTemplateColumns: '1fr 1fr',
                gap: '1.5rem',
              }}
            >
              <div>
                <div className="stat-card-title">Property Gallery</div>
                <div style={{ display: 'grid', gridTemplateColumns: 'repeat(3, 1fr)', gap: '0.75rem' }}>
                  {propertyImages.map((img) => (
                    <div
                      key={img}
                      style={{
                        width: '100%',
                        aspectRatio: '4 / 3',
                        backgroundImage: `url(${img})`,
                        backgroundSize: 'cover',
                        backgroundPosition: 'center',
                        borderRadius: '12px',
                      }}
                    />
                  ))}
                </div>
              </div>
              <div>
                <div className="stat-card-title">Property Details</div>
                <div style={{ display: 'grid', gap: '0.5rem' }}>
                  <div>Rooms: {roomDetails.rooms}</div>
                  <div>Bathrooms: {roomDetails.bathrooms}</div>
                  <div>Amenities: {roomDetails.amenities.join(', ')}</div>
                </div>
                {propertyMeta && (
                  <div style={{ display: 'grid', gap: '0.5rem', marginTop: '0.75rem' }}>
                    <div>{propertyMeta.titleHe}</div>
                    <div>{propertyMeta.location}</div>
                    <div>
                      ⭐ {propertyMeta.rating} · {propertyMeta.reviews} reviews
                    </div>
                    <div>Host: {propertyMeta.host}</div>
                    <div>
                      {propertyMeta.bedrooms} bedrooms · {propertyMeta.beds} beds ·{' '}
                      {propertyMeta.bathrooms} bath
                    </div>
                    <div>Highlights: {propertyMeta.highlights.join(', ')}</div>
                    <div>
                      Amenities:{' '}
                      {showFullDetails
                        ? propertyMeta.amenities.join(', ')
                        : propertyMeta.amenities.slice(0, 4).join(', ')}
                    </div>
                    <button
                      className="btn-primary"
                      style={{ marginTop: '0.5rem' }}
                      onClick={() => setShowFullDetails((prev) => !prev)}
                    >
                      {showFullDetails ? 'הצג פחות' : 'הצג עוד'}
                    </button>
                  </div>
                )}
              </div>
            </div>

              <div className="staff-connection-section" key="staff-connection">
                <div
                  ref={activationRef}
                  className="stat-card"
                  style={{
                    display: 'grid',
                    gridTemplateColumns: '1fr 1fr',
                    gap: '2rem',
                    marginTop: '2rem',
                    alignItems: 'start',
                    opacity: isScanning ? 0.4 : 1,
                    pointerEvents: isScanning ? 'none' : 'auto',
                  }}
                >
                  <div key="staff-inputs">
                  <div className="stat-card-title">One-Click Activation</div>
                  <div className="stat-card-value">הפעלה בלחיצה אחת</div>
                  <div className="activation-grid">
                    <div className="activation-field" key="staff-cleaner">
                      <label>Cleaner</label>
                      <input
                        type="tel"
                        value={cleanerPhone}
                        onChange={(e) => {
                          setCleanerPhone(e.target.value);
                          if (e.target.value) setStaffRolePreview('cleaner');
                        startStaffSync('cleaner', e.target.value);
                        }}
                        placeholder="+972..."
                      />
                    {cleanerPhone && staffSyncStatus.cleaner === 'syncing' && (
                        <div className="activation-status">
                          AI syncing with {cleanerPhone} via WhatsApp...
                        </div>
                      )}
                    {cleanerPhone && staffSyncStatus.cleaner === 'connected' && (
                      <div className="activation-status">Connected ✓</div>
                    )}
                    </div>
                    <div className="activation-field" key="staff-maintenance">
                      <label>Maintenance</label>
                      <input
                        type="tel"
                        value={maintenancePhone}
                        onChange={(e) => {
                          setMaintenancePhone(e.target.value);
                          if (e.target.value) setStaffRolePreview('maintenance');
                        startStaffSync('maintenance', e.target.value);
                        }}
                        placeholder="+972..."
                      />
                    {maintenancePhone && staffSyncStatus.maintenance === 'syncing' && (
                        <div className="activation-status">
                          AI syncing with {maintenancePhone} via WhatsApp...
                        </div>
                      )}
                    {maintenancePhone && staffSyncStatus.maintenance === 'connected' && (
                      <div className="activation-status">Connected ✓</div>
                    )}
                    </div>
                    <div className="activation-field" key="staff-frontdesk">
                      <label>Front Desk</label>
                      <input
                        type="tel"
                        value={frontDeskPhone}
                        onChange={(e) => {
                          setFrontDeskPhone(e.target.value);
                          if (e.target.value) setStaffRolePreview('frontDesk');
                        startStaffSync('frontDesk', e.target.value);
                        }}
                        placeholder="+972..."
                      />
                    {frontDeskPhone && staffSyncStatus.frontDesk === 'syncing' && (
                        <div className="activation-status">
                          AI syncing with {frontDeskPhone} via WhatsApp...
                        </div>
                      )}
                    {frontDeskPhone && staffSyncStatus.frontDesk === 'connected' && (
                      <div className="activation-status">Connected ✓</div>
                    )}
                    </div>
                    <div className="activation-field" key="staff-restaurant">
                      <label>Restaurant</label>
                      <input
                        type="tel"
                        value={restaurantPhone}
                        onChange={(e) => {
                          setRestaurantPhone(e.target.value);
                          if (e.target.value) setStaffRolePreview('restaurant');
                        startStaffSync('restaurant', e.target.value);
                        }}
                        placeholder="+972..."
                      />
                    {restaurantPhone && staffSyncStatus.restaurant === 'syncing' && (
                        <div className="activation-status">
                          AI syncing with {restaurantPhone} via WhatsApp...
                        </div>
                      )}
                    {restaurantPhone && staffSyncStatus.restaurant === 'connected' && (
                      <div className="activation-status">Connected ✓</div>
                    )}
                    </div>
                  </div>
                  <button
                    className="btn-primary"
                    style={{ fontSize: '1.1rem', padding: '0.9rem 1.6rem' }}
                    onClick={handleActivate}
                  >
                    הפעל אוטומציה של מאיה
                  </button>
                  <button
                    className="btn-primary"
                    style={{ marginTop: '0.75rem' }}
                    onClick={() => setEmailPreviewOpen((prev) => !prev)}
                  >
                    שלח מייל אוטומטי ללקוח
                  </button>
                  {emailPreviewOpen && (
                    <div className="agent-panel" style={{ marginTop: '1rem' }}>
                      <h3>Email Preview</h3>
                      <div
                        style={{
                          border: '1px solid #e2e8f0',
                          borderRadius: '12px',
                          padding: '1rem',
                          background: '#fff',
                        }}
                      >
                        <div style={{ fontWeight: 600, marginBottom: '0.5rem' }}>
                          {demoPreview?.name || 'Property Preview'}
                        </div>
                        <div
                          style={{
                            width: '100%',
                            aspectRatio: '16 / 9',
                            backgroundImage: `url(${demoPreview?.image || PLACEHOLDER_IMAGE})`,
                            backgroundSize: 'cover',
                            backgroundPosition: 'center',
                            borderRadius: '10px',
                            marginBottom: '0.75rem',
                          }}
                        />
                        <button className="btn-primary">Login</button>
                      </div>
                    </div>
                  )}
                </div>

                <div className="staff-preview-card" key="staff-preview">
                  <div className="stat-card-title">Live Staff View</div>
                  <div className="stat-card-value">תצוגת עובד חיה</div>
                  <div
                    className="staff-preview-screen"
                    key="staff-preview-screen"
                    style={{
                      background: '#0f172a',
                      color: '#fff',
                      borderRadius: '18px',
                      padding: '1.25rem',
                      minHeight: '260px',
                      boxShadow: '0 10px 20px rgba(15, 23, 42, 0.25)',
                    }}
                  >
                    {staffRolePreview === 'maintenance' ? (
                      <>
                        <div style={{ fontWeight: 600, marginBottom: '0.75rem' }}>
                          Maintenance Portal
                        </div>
                        <div style={{ marginBottom: '0.75rem' }}>
                          Urgent: Fix Flamingo pump in the pool
                        </div>
                      </>
                    ) : (
                      <>
                        <div style={{ fontWeight: 600, marginBottom: '0.75rem' }}>
                          Cleaner Portal
                        </div>
                        <div>{demoPreview?.name || 'Property'} - Cleaning Task:</div>
                        <div style={{ marginTop: '0.75rem' }}>
                          [ ] Bedroom
                          <br />
                          [ ] Pool Area
                        </div>
                      </>
                    )}
                    <div
                      style={{
                        marginTop: '1rem',
                        background: '#1f2937',
                        padding: '0.75rem',
                        borderRadius: '12px',
                        fontSize: '0.9rem',
                      }}
                    >
                      Maya: Hi, I’ve updated the guest list for {demoPreview?.name || 'this property'}.
                      Please ensure the pool area is ready by 15:00.
                    </div>
                    <button
                      style={{
                        marginTop: '1rem',
                        width: '100%',
                        padding: '0.8rem',
                        background: '#22c55e',
                        color: '#fff',
                        border: 'none',
                        borderRadius: '12px',
                        fontWeight: 700,
                      }}
                      onClick={() => setPropertyStatus('Ready for Guest')}
                    >
                      MARK AS DONE
                    </button>
                  </div>
              </div>
              {activationSuccess && (
                <div className="success-toast">SUCCESS: AI Management Activated</div>
              )}
            </div>
              </div>
            </div>
          )}
        </main>
      </div>
    </div>
  );
};

export default EnterpriseDashboard;
