import React from 'react';

const stripMotionProps = (props) => {
  // Remove framer-motion specific props to avoid DOM errors
  const {
    initial,
    animate,
    variants,
    transition,
    whileHover,
    whileTap,
    whileInView,
    exit,
    layoutId,
    layout,
    drag,
    dragConstraints,
    dragElastic,
    ...rest
  } = props || {};
  return rest;
};

const make = (Tag) =>
  React.forwardRef(({ children, ...props }, ref) => (
    <Tag ref={ref} {...stripMotionProps(props)}>
      {children}
    </Tag>
  ));

export const motion = {
  div: make('div'),
  button: make('button'),
};
