/**
 * Maya - The Master Orchestrator
 * Manages all agents and handles high-level decisions
 */
class MayaOrchestrator {
  constructor() {
    this.agents = {
      [AGENT_TYPES.SCRAPER]: new ScraperAgent(),
      [AGENT_TYPES.MARKETING]: new MarketingAgent(),
      [AGENT_TYPES.CREATIVE]: new CreativeAgent(),
      [AGENT_TYPES.SALES]: new SalesAgent(),
      [AGENT_TYPES.OPERATIONS]: new OperationsAgent(),
    };

    this.taskHistory = [];
    this.isProcessing = false;
    this.systemPrompt = MAYA_SYSTEM_PROMPT;
  }

  /**
   * Execute a task using the appropriate agent
   */
  async executeTask(agentType, task) {
    const agent = this.agents[agentType];

    if (!agent) {
      throw new Error(`Agent ${agentType} not found`);
    }

    const taskRecord = {
      id: Date.now(),
      agentType,
      task,
      startedAt: new Date().toISOString(),
      status: 'running',
    };

    this.taskHistory.push(taskRecord);

    try {
      const result = await agent.execute(task);
      taskRecord.completedAt = new Date().toISOString();
      taskRecord.status = 'completed';
      taskRecord.result = result;
      return result;
    } catch (error) {
      taskRecord.completedAt = new Date().toISOString();
      taskRecord.status = 'failed';
      taskRecord.error = error.message;
      throw error;
    }
  }

  /**
   * Get all agent statuses
   */
  getAgentStatuses() {
    return Object.entries(this.agents).reduce((acc, [type, agent]) => {
      acc[type] = {
        status: agent.status,
        name: agent.name,
        description: agent.description,
      };
      return acc;
    }, {});
  }

  /**
   * Process natural language command from Maya chat
   */
  async processCommand(command) {
    const lowerCommand = command.toLowerCase();

    // Detect intent and route to appropriate agent
    if (lowerCommand.includes('lead') || lowerCommand.includes('scan') || lowerCommand.includes('airbnb') || lowerCommand.includes('◊ú◊ô◊ì') || lowerCommand.includes('◊°◊®◊ô◊ß◊î')) {
      return await this.executeTask(AGENT_TYPES.SCRAPER, { platforms: ['airbnb', 'booking'] });
    }

    if (lowerCommand.includes('post') || lowerCommand.includes('marketing') || lowerCommand.includes('campaign') || lowerCommand.includes('◊§◊ï◊°◊ò') || lowerCommand.includes('◊©◊ô◊ï◊ï◊ß') || lowerCommand.includes('◊ß◊û◊§◊ô◊ô◊ü')) {
      return await this.executeTask(AGENT_TYPES.MARKETING, {
        type: 'social_post',
        content: { text: 'New hotel post', platforms: ['instagram', 'facebook'] },
      });
    }

    if (lowerCommand.includes('video') || lowerCommand.includes('image') || lowerCommand.includes('edit') || lowerCommand.includes('◊ï◊ô◊ì◊ê◊ï') || lowerCommand.includes('◊™◊û◊ï◊†') || lowerCommand.includes('◊¢◊®◊ô◊õ◊î')) {
      return await this.executeTask(AGENT_TYPES.CREATIVE, {
        type: 'enhance_images',
        images: ['https://example.com/room1.jpg'],
      });
    }

    if (lowerCommand.includes('demo') || lowerCommand.includes('deal') || lowerCommand.includes('sales') || lowerCommand.includes('◊ì◊û◊ï') || lowerCommand.includes('◊û◊õ◊ô◊®') || lowerCommand.includes('◊¢◊°◊ß◊î')) {
      return await this.executeTask(AGENT_TYPES.SALES, {
        type: 'send_demo',
        lead: { id: Date.now(), name: 'New lead' },
      });
    }

    if (lowerCommand.includes('towel') || lowerCommand.includes('clean') || lowerCommand.includes('maintenance') || lowerCommand.includes('room') || lowerCommand.includes('◊û◊í◊ë◊™') || lowerCommand.includes('◊†◊ô◊ß◊ô◊ï◊ü') || lowerCommand.includes('◊™◊ó◊ñ◊ï◊ß◊î') || lowerCommand.includes('◊ó◊ì◊®')) {
      const roomMatch = command.match(/\d+/);
      const room = roomMatch ? roomMatch[0] : '101';

      return await this.executeTask(AGENT_TYPES.OPERATIONS, {
        type: 'request_towels',
        room,
        description: command,
      });
    }

    // Default response
    return {
      success: true,
      message: i18n.t('orchestrator.messages.unknown'),
    };
  }

  /**
   * Get task history
   */
  getTaskHistory(limit = 50) {
    return this.taskHistory.slice(-limit).reverse();
  }

  /**
   * Generate daily report
   */
  async generateDailyReport() {
    const response = await fetch(`${API_BASE_URL}/api/reports/daily`, {
      headers: { ...getAuthHeaders() },
    });
    if (!response.ok) {
      throw new Error('Failed to generate report');
    }
    const data = await response.json();
    return data.report || data;
  }
}

// Singleton instance
export const maya = new MayaOrchestrator();

export default maya;
