import React, { useState, useRef, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { 
  MessageCircle, X, Send, Bot, User, Sparkles, 
  Loader2, Minimize2, Maximize2, Settings,
  Search, FileText, Image, Zap
} from 'lucide-react';
import useStore from '../../store/useStore';
import { maya } from '../../services/agentOrchestrator';
import './MayaChat.css';

// Quick action buttons for Maya
const quickActions = [
  { id: 'scan', icon: Search, label: 'סרוק לידים', command: 'סרוק לידים חדשים ב-Airbnb' },
  { id: 'report', icon: FileText, label: 'דוח יומי', command: 'הפק דוח יומי' },
  { id: 'post', icon: Image, label: 'צור פוסט', command: 'צור פוסט שיווקי לאינסטגרם' },
  { id: 'auto', icon: Zap, label: 'אוטומציה', command: 'הפעל אוטומציות' },
];

const MayaChat = () => {
  const {
    mayaChatOpen,
    toggleMayaChat,
    mayaMessages,
    addMayaMessage,
    mayaIsTyping,
    setMayaTyping,
    addNotification,
  } = useStore();

  const [input, setInput] = useState('');
  const [isMinimized, setIsMinimized] = useState(false);
  const messagesEndRef = useRef(null);
  const inputRef = useRef(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  useEffect(() => {
    scrollToBottom();
  }, [mayaMessages]);

  useEffect(() => {
    if (mayaChatOpen && inputRef.current) {
      inputRef.current.focus();
    }
  }, [mayaChatOpen]);

  const handleSend = async () => {
    if (!input.trim() || mayaIsTyping) return;

    const userMessage = input.trim();
    setInput('');

    // Add user message
    addMayaMessage({
      role: 'user',
      content: userMessage,
    });

    // Set typing state
    setMayaTyping(true);

    try {
      // Process with Maya orchestrator
      const result = await maya.processCommand(userMessage);

      // Add Maya's response
      addMayaMessage({
        role: 'assistant',
        content: result.message || result.displayMessage || 'המשימה הושלמה בהצלחה!',
        data: result,
      });

      // Add notification if applicable
      if (result.success) {
        addNotification({
          type: 'success',
          title: 'Maya',
          message: result.message || result.displayMessage,
        });
      }
    } catch (error) {
      addMayaMessage({
        role: 'assistant',
        content: `אירעה שגיאה: ${error.message}`,
        isError: true,
      });
    } finally {
      setMayaTyping(false);
    }
  };

  const handleQuickAction = (command) => {
    setInput(command);
    setTimeout(() => handleSend(), 100);
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSend();
    }
  };

  return (
    <div className="maya-container">
      <AnimatePresence>
        {mayaChatOpen && (
          <motion.div
            initial={{ opacity: 0, scale: 0.8, y: 20 }}
            animate={{ 
              opacity: 1, 
              scale: 1, 
              y: 0,
              height: isMinimized ? 'auto' : '600px',
            }}
            exit={{ opacity: 0, scale: 0.8, y: 20 }}
            transition={{ type: 'spring', damping: 25, stiffness: 300 }}
            className="maya-chat glass-dark"
          >
            {/* Header */}
            <div className="maya-header">
              <div className="maya-header-info">
                <div className="maya-avatar">
                  <motion.div
                    animate={{ rotate: 360 }}
                    transition={{ duration: 20, repeat: Infinity, ease: 'linear' }}
                    className="maya-avatar-glow"
                  />
                  <Bot size={24} />
                </div>
                <div className="maya-header-text">
                  <h3>Maya</h3>
                  <span className="maya-status">
                    <span className="status-dot" />
                    מנהלת פרויקט דיגיטלית
                  </span>
                </div>
              </div>
              <div className="maya-header-actions">
                <button
                  onClick={() => setIsMinimized(!isMinimized)}
                  className="maya-header-btn"
                >
                  {isMinimized ? <Maximize2 size={18} /> : <Minimize2 size={18} />}
                </button>
                <button onClick={toggleMayaChat} className="maya-header-btn">
                  <X size={18} />
                </button>
              </div>
            </div>

            {!isMinimized && (
              <>
                {/* Quick Actions */}
                <div className="maya-quick-actions">
                  {quickActions.map((action) => (
                    <motion.button
                      key={action.id}
                      whileHover={{ scale: 1.05 }}
                      whileTap={{ scale: 0.95 }}
                      onClick={() => handleQuickAction(action.command)}
                      className="quick-action-btn"
                    >
                      <action.icon size={16} />
                      <span>{action.label}</span>
                    </motion.button>
                  ))}
                </div>

                {/* Messages */}
                <div className="maya-messages">
                  {mayaMessages.map((message) => (
                    <motion.div
                      key={message.id}
                      initial={{ opacity: 0, y: 10 }}
                      animate={{ opacity: 1, y: 0 }}
                      className={`maya-message ${message.role}`}
                    >
                      <div className="message-avatar">
                        {message.role === 'assistant' ? (
                          <Bot size={16} />
                        ) : (
                          <User size={16} />
                        )}
                      </div>
                      <div className={`message-content ${message.isError ? 'error' : ''}`}>
                        <p>{message.content}</p>
                        {message.data && message.data.leads && (
                          <div className="message-data">
                            <span className="data-label">לידים שנמצאו:</span>
                            <span className="data-value">{message.data.leads.length}</span>
                          </div>
                        )}
                        <span className="message-time">
                          {new Date(message.timestamp).toLocaleTimeString('he-IL', {
                            hour: '2-digit',
                            minute: '2-digit',
                          })}
                        </span>
                      </div>
                    </motion.div>
                  ))}

                  {mayaIsTyping && (
                    <motion.div
                      initial={{ opacity: 0 }}
                      animate={{ opacity: 1 }}
                      className="maya-message assistant"
                    >
                      <div className="message-avatar">
                        <Bot size={16} />
                      </div>
                      <div className="message-content typing">
                        <div className="typing-indicator">
                          <span />
                          <span />
                          <span />
                        </div>
                      </div>
                    </motion.div>
                  )}
                  <div ref={messagesEndRef} />
                </div>

                {/* Input */}
                <div className="maya-input-container">
                  <div className="maya-input-wrapper">
                    <input
                      ref={inputRef}
                      type="text"
                      value={input}
                      onChange={(e) => setInput(e.target.value)}
                      onKeyPress={handleKeyPress}
                      placeholder="שאל את Maya משהו..."
                      className="maya-input"
                      disabled={mayaIsTyping}
                    />
                    <motion.button
                      whileHover={{ scale: 1.1 }}
                      whileTap={{ scale: 0.9 }}
                      onClick={handleSend}
                      disabled={!input.trim() || mayaIsTyping}
                      className="maya-send-btn"
                    >
                      {mayaIsTyping ? (
                        <Loader2 size={20} className="animate-spin" />
                      ) : (
                        <Send size={20} />
                      )}
                    </motion.button>
                  </div>
                  <p className="maya-hint">
                    <Sparkles size={12} />
                    Maya מנהלת 5 סוכנים חכמים שעובדים בשבילך
                  </p>
                </div>
              </>
            )}
          </motion.div>
        )}
      </AnimatePresence>

      {/* Floating Button */}
      <motion.button
        whileHover={{ scale: 1.1 }}
        whileTap={{ scale: 0.9 }}
        onClick={toggleMayaChat}
        className="maya-button"
      >
        <AnimatePresence mode="wait">
          {mayaChatOpen ? (
            <motion.div
              key="close"
              initial={{ rotate: -90, opacity: 0 }}
              animate={{ rotate: 0, opacity: 1 }}
              exit={{ rotate: 90, opacity: 0 }}
            >
              <X size={28} />
            </motion.div>
          ) : (
            <motion.div
              key="open"
              initial={{ rotate: 90, opacity: 0 }}
              animate={{ rotate: 0, opacity: 1 }}
              exit={{ rotate: -90, opacity: 0 }}
            >
              <MessageCircle size={28} />
            </motion.div>
          )}
        </AnimatePresence>
        <motion.div
          className="maya-button-ring"
          animate={{
            scale: [1, 1.2, 1],
            opacity: [0.5, 0, 0.5],
          }}
          transition={{
            duration: 2,
            repeat: Infinity,
            ease: 'easeInOut',
          }}
        />
      </motion.button>
    </div>
  );
};

export default MayaChat;
