import React, { useState } from 'react';
import { Bot, Users, Bed, TrendingUp, Send } from 'lucide-react';
import { BarChart, Bar, XAxis, YAxis, Tooltip, ResponsiveContainer, CartesianGrid } from 'recharts';
import { agents } from './agentData'; // 砖转砖 转 转 砖
import './AgentTable.css';

function App() {
  const [userInput, setUserInput] = useState("");
  const [aiResponse, setAiResponse] = useState("砖!  注专 -AI 砖 .   住注  爪转 ?");
  const [loading, setLoading] = useState(false);

  const askAI = async () => {
    if (!userInput) return;
    setLoading(true);
    try {
      const response = await fetch('http://127.0.0.1:5000/ask', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ message: userInput }),
      });
      const data = await response.json();
      setAiResponse(data.reply || data.message);
      setUserInput(""); // 拽 转 转
    } catch (error) {
      setAiResponse("砖:  砖砖专转 -Python 专抓 专!");
    }
    setLoading(false);
  };

  return (
    <div style={{ padding: '20px', fontFamily: 'Segoe UI', direction: 'rtl', backgroundColor: '#f0f2f5', minHeight: '100vh' }}>
      {/* Header */}
      <header style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '30px', backgroundColor: '#fff', padding: '15px 25px', borderRadius: '15px', boxShadow: '0 2px 10px rgba(0,0,0,0.05)' }}>
        <h1 style={{ margin: 0, color: '#1a73e8' }}> EasyHost AI Dashboard</h1>
        <div style={{ display: 'flex', gap: '15px', alignItems: 'center' }}>
          <span style={{ fontSize: '14px', color: '#666' }}>住住 注专转: <strong style={{ color: '#34a853' }}>专</strong></span>
        </div>
      </header>

      {/* Stats Cards */}
      <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(240px, 1fr))', gap: '20px', marginBottom: '30px' }}>
        <Card icon={<Bed color="#1a73e8"/>} title="专 驻" value="12" sub="转 150" />
        <Card icon={<Users color="#ea4335"/>} title="住 驻注" value={agents.length} sub="爪转 拽专" />
        <Card icon={<TrendingUp color="#34a853"/>} title="转驻住 转" value="85%" sub="+2% 转" />
      </div>

      <div style={{ display: 'grid', gridTemplateColumns: '2fr 1fr', gap: '20px' }}>
        {/* AI Chat Section */}
        <div style={{ backgroundColor: '#fff', padding: '25px', borderRadius: '20px', boxShadow: '0 4px 15px rgba(0,0,0,0.05)' }}>
          <h2 style={{ display: 'flex', alignItems: 'center', gap: '10px', marginTop: 0 }}><Bot color="#1a73e8" /> 转注爪转 注 -AI</h2>
          <div style={{ height: '150px', backgroundColor: '#f8f9fa', padding: '20px', borderRadius: '15px', overflowY: 'auto', marginBottom: '20px', borderRight: '5px solid #1a73e8', lineHeight: '1.6' }}>
            {loading ? "转 转  转砖..." : aiResponse}
          </div>
          <div style={{ display: 'flex', gap: '10px' }}>
            <input 
              value={userInput}
              onChange={(e) => setUserInput(e.target.value)}
              placeholder="砖 转 注 爪注 住, 专  转转 转驻住..."
              style={{ flex: 1, padding: '15px', borderRadius: '10px', border: '1px solid #ddd', outline: 'none', fontSize: '16px' }}
              onKeyPress={(e) => e.key === 'Enter' && askAI()}
            />
            <button onClick={askAI} style={{ backgroundColor: '#1a73e8', color: '#fff', border: 'none', padding: '0 25px', borderRadius: '10px', cursor: 'pointer', transition: '0.3s' }}>
              <Send size={20} />
            </button>
          </div>
        </div>

        {/* Chart Section */}
        <div style={{ backgroundColor: '#fff', padding: '25px', borderRadius: '20px', boxShadow: '0 4px 15px rgba(0,0,0,0.05)' }}>
          <h3 style={{ marginTop: 0 }}>爪注 住 (砖转)</h3>
          <div style={{ height: '250px' }}>
            <ResponsiveContainer width="100%" height="100%">
              <BarChart data={agents}>
                <CartesianGrid strokeDasharray="3 3" vertical={false} />
                <XAxis dataKey="name" />
                <YAxis />
                <Tooltip />
                <Bar dataKey="tasks" fill="#1a73e8" radius={[5, 5, 0, 0]} />
              </BarChart>
            </ResponsiveContainer>
          </div>
        </div>
      </div>
    </div>
  );
}

function Card({ icon, title, value, sub }) {
  return (
    <div style={{ backgroundColor: '#fff', padding: '25px', borderRadius: '20px', boxShadow: '0 4px 15px rgba(0,0,0,0.05)', display: 'flex', alignItems: 'center', gap: '20px' }}>
      <div style={{ backgroundColor: '#f0f4ff', padding: '15px', borderRadius: '15px' }}>{icon}</div>
      <div>
        <h4 style={{ margin: 0, color: '#666', fontSize: '14px' }}>{title}</h4>
        <div style={{ fontSize: '24px', fontWeight: 'bold', margin: '5px 0' }}>{value}</div>
        <div style={{ fontSize: '12px', color: '#999' }}>{sub}</div>
      </div>
    </div>
  );
}

export default App;
