import React, { useState } from 'react';
import { Bot, Users, Bed, TrendingUp, Send } from 'lucide-react';
import { BarChart, Bar, XAxis, YAxis, Tooltip, ResponsiveContainer, CartesianGrid } from 'recharts';
import { agents } from './agentData'; 
import './AgentTable.css';

function App() {
  const [userInput, setUserInput] = useState("");
  const [aiResponse, setAiResponse] = useState("◊©◊ú◊ï◊ù! ◊ê◊†◊ô ◊¢◊ï◊ñ◊® ◊î-AI ◊©◊ú ◊î◊û◊ú◊ï◊ü. ◊ê◊ô◊ö ◊ê◊ï◊õ◊ú ◊ú◊°◊ô◊ô◊¢ ◊î◊ô◊ï◊ù?");
  const [loading, setLoading] = useState(false);

  const askAI = async () => {
    if (!userInput) return;
    setLoading(true);
    try {
      const response = await fetch('http://127.0.0.1:5000/ask', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ message: userInput }),
      });
      const data = await response.json();
      setAiResponse(data.reply || data.message);
      setUserInput(""); 
    } catch (error) {
      setAiResponse("◊©◊í◊ô◊ê◊î: ◊ï◊ï◊ì◊ê ◊©◊©◊®◊™ ◊î-Python (app.py) ◊®◊•!");
    }
    setLoading(false);
  };

  return (
    <div style={{ padding: '20px', fontFamily: 'Segoe UI, sans-serif', direction: 'rtl', backgroundColor: '#f8f9fa', minHeight: '100vh' }}>
      {/* Header */}
      <header style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '30px', backgroundColor: '#fff', padding: '15px 30px', borderRadius: '15px', boxShadow: '0 4px 6px rgba(0,0,0,0.05)' }}>
        <h1 style={{ margin: 0, color: '#1a73e8', display: 'flex', alignItems: 'center', gap: '10px' }}>üè® EasyHost AI</h1>
        <div style={{ color: '#34a853', fontWeight: 'bold' }}>‚óè ◊©◊®◊™ ◊û◊ó◊ï◊ë◊®</div>
      </header>

      {/* Stats Section */}
      <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(250px, 1fr))', gap: '20px', marginBottom: '30px' }}>
        <StatCard icon={<Bed color="#1a73e8"/>} title="◊ó◊ì◊®◊ô◊ù ◊§◊†◊ï◊ô◊ô◊ù" value="12" color="#e8f0fe" />
        <StatCard icon={<Users color="#ea4335"/>} title="◊°◊ï◊õ◊†◊ô◊ù ◊§◊¢◊ô◊ú◊ô◊ù" value={agents.length} color="#fce8e6" />
        <StatCard icon={<TrendingUp color="#34a853"/>} title="◊™◊§◊ï◊°◊î" value="85%" color="#e6f4ea" />
      </div>

      <div style={{ display: 'grid', gridTemplateColumns: '1.5fr 1fr', gap: '25px' }}>
        {/* Chat Interface */}
        <div style={{ backgroundColor: '#fff', padding: '25px', borderRadius: '20px', boxShadow: '0 10px 25px rgba(0,0,0,0.05)' }}>
          <h2 style={{ display: 'flex', alignItems: 'center', gap: '10px', marginTop: 0 }}><Bot color="#1a73e8" /> ◊î◊™◊ô◊ô◊¢◊¶◊ï◊™ ◊ó◊õ◊û◊î</h2>
          <div style={{ height: '180px', backgroundColor: '#f1f3f4', padding: '20px', borderRadius: '15px', overflowY: 'auto', marginBottom: '20px', fontSize: '16px', borderRight: '6px solid #1a73e8' }}>
            {loading ? "◊û◊¢◊ë◊ì ◊†◊™◊ï◊†◊ô◊ù..." : aiResponse}
          </div>
          <div style={{ display: 'flex', gap: '10px' }}>
            <input 
              value={userInput}
              onChange={(e) => setUserInput(e.target.value)}
              placeholder="◊õ◊™◊ï◊ë ◊õ◊ê◊ü ◊©◊ê◊ú◊î ◊ú-AI (◊ú◊û◊©◊ú: ◊ê◊ô◊ö ◊ú◊©◊§◊® ◊ê◊™ ◊™◊§◊ï◊°◊™ ◊î◊ó◊ì◊®◊ô◊ù?)..."
              style={{ flex: 1, padding: '15px', borderRadius: '12px', border: '1px solid #ddd', fontSize: '16px', outline: 'none' }}
              onKeyPress={(e) => e.key === 'Enter' && askAI()}
            />
            <button onClick={askAI} style={{ backgroundColor: '#1a73e8', color: '#fff', border: 'none', padding: '0 25px', borderRadius: '12px', cursor: 'pointer' }}>
              <Send size={20} />
            </button>
          </div>
        </div>

        {/* Charts Section */}
        <div style={{ backgroundColor: '#fff', padding: '25px', borderRadius: '20px', boxShadow: '0 10px 25px rgba(0,0,0,0.05)' }}>
          <h3 style={{ marginTop: 0 }}>◊ë◊ô◊¶◊ï◊¢◊ô ◊¶◊ï◊ï◊™ (◊û◊©◊ô◊û◊ï◊™)</h3>
          <div style={{ height: '250px' }}>
            <ResponsiveContainer width="100%" height="100%">
              <BarChart data={agents}>
                <CartesianGrid strokeDasharray="3 3" vertical={false} />
                <XAxis dataKey="name" />
                <YAxis />
                <Tooltip />
                <Bar dataKey="tasks" fill="#1a73e8" radius={[5, 5, 0, 0]} />
              </BarChart>
            </ResponsiveContainer>
          </div>
        </div>
      </div>
    </div>
  );
}

function StatCard({ icon, title, value, color }) {
  return (
    <div style={{ backgroundColor: '#fff', padding: '20px', borderRadius: '15px', display: 'flex', alignItems: 'center', gap: '20px', boxShadow: '0 4px 6px rgba(0,0,0,0.03)' }}>
      <div style={{ backgroundColor: color, padding: '15px', borderRadius: '12px' }}>{icon}</div>
      <div>
        <div style={{ color: '#5f6368', fontSize: '14px' }}>{title}</div>
        <div style={{ fontSize: '24px', fontWeight: 'bold' }}>{value}</div>
      </div>
    </div>
  );
}

export default App;
