import React, { useEffect, useState } from 'react';
import { createPortal } from 'react-dom';
import { motion } from 'framer-motion';
import {
  Building2, TrendingUp, Users, Calendar, DollarSign,
  Activity, Clock, CheckCircle2, AlertCircle, Sparkles,
  BarChart3, PieChart, ArrowUpRight, ArrowDownRight,
  Bot, Zap, Target, FileText, ExternalLink, Check, Brush,
  MessageCircle, ToggleLeft, ToggleRight
} from 'lucide-react';
import {
  AreaChart, Area, BarChart, Bar, PieChart as RechartsPie, Pie, Cell,
  XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer, Legend
} from 'recharts';
import i18n from '../../i18n';
import useStore from '../../store/useStore';
import { updateRoomStatus, getDailyReport, getAutomationStats, subscribeToLeads, connectCalendar, getCalendarStatus } from '../../services/api';
import { whatsappService } from '../../services/whatsapp';
import './EnterpriseDashboard.css';

// Chart data
const revenueData = [
  { name: 'Jan', revenue: 320000, bookings: 42 },
  { name: 'Feb', revenue: 280000, bookings: 38 },
  { name: 'Mar', revenue: 350000, bookings: 48 },
  { name: 'Apr', revenue: 420000, bookings: 55 },
  { name: 'May', revenue: 480000, bookings: 62 },
  { name: 'Jun', revenue: 520000, bookings: 68 },
];

const occupancyData = [
  { nameKey: 'dashboard.rooms.status.occupied', value: 72, color: '#10b981' },
  { nameKey: 'dashboard.rooms.status.available', value: 18, color: '#3b82f6' },
  { nameKey: 'dashboard.rooms.status.maintenance', value: 6, color: '#f59e0b' },
  { nameKey: 'dashboard.rooms.status.cleaning', value: 4, color: '#8b5cf6' },
];

const agentPerformance = [
  { name: 'Scraper', tasks: 156, success: 148 },
  { name: 'Marketing', tasks: 89, success: 82 },
  { name: 'Creative', tasks: 45, success: 43 },
  { name: 'Sales', tasks: 67, success: 58 },
  { name: 'Operations', tasks: 234, success: 228 },
];

const containerVariants = {
  hidden: { opacity: 0 },
  visible: {
    opacity: 1,
    transition: {
      staggerChildren: 0.1,
    },
  },
};

const itemVariants = {
  hidden: { opacity: 0, y: 20 },
  visible: { opacity: 1, y: 0 },
};

const EnterpriseDashboard = () => {
  const {
    stats,
    rooms,
    agents,
    leads,
    notifications,
    automationStats,
    setAutomationStats,
    setObjectionSuccess,
    setGeneratingReport,
    isGeneratingReport,
    lang,
    updateRoom,
    activeTenantId,
    authToken,
  } = useStore();
  const t = (key, options) => i18n.t(key, options);
  const [activeTab, setActiveTab] = useState('overview');
  const [selectedRoom, setSelectedRoom] = useState(null);
  const [roomActionLoading, setRoomActionLoading] = useState(false);
  const [roomStatusLoading, setRoomStatusLoading] = useState(null);
  const [reportDataLocal, setReportDataLocal] = useState(null);
  const [calendarStatus, setCalendarStatus] = useState({ synced: false, vacant_nights: 0, potential_revenue: 0 });
  const [icalUrl, setIcalUrl] = useState('');
  const [nightlyRate, setNightlyRate] = useState(250);
  const [isSyncingCalendar, setIsSyncingCalendar] = useState(false);

  useEffect(() => {
    if (lang && i18n.language !== lang) {
      i18n.changeLanguage(lang);
    }
  }, [lang]);

  useEffect(() => {
    let isActive = true;
    const loadAutomationStats = async () => {
      try {
        const statsResponse = await getAutomationStats();
        if (!isActive) return;
        setAutomationStats(statsResponse.automation_stats);
        setObjectionSuccess(statsResponse.objection_success);
      } catch (error) {
        console.error('Error fetching automation stats:', error);
      }
    };

    loadAutomationStats();
    const eventSource = subscribeToLeads(
      () => {},
      () => {},
      (error) => {
        console.error('Automation stats stream error:', error);
      },
      (statsUpdate) => {
        setAutomationStats(statsUpdate);
      }
    );

    return () => {
      isActive = false;
      if (eventSource && eventSource.close) eventSource.close();
    };
  }, [setAutomationStats, setObjectionSuccess, activeTenantId, authToken]);

  useEffect(() => {
    let isActive = true;
    const loadCalendarStatus = async () => {
      try {
        const status = await getCalendarStatus();
        if (isActive) {
          setCalendarStatus(status);
        }
      } catch (error) {
        if (isActive) {
          setCalendarStatus({ synced: false, vacant_nights: 0, potential_revenue: 0 });
        }
      }
    };
    loadCalendarStatus();
    return () => {
      isActive = false;
    };
  }, [activeTenantId, authToken]);

  const handleSyncCalendar = async () => {
    if (!icalUrl) return;
    setIsSyncingCalendar(true);
    try {
      const status = await connectCalendar(icalUrl, nightlyRate);
      setCalendarStatus({ ...status, synced: true });
    } catch (error) {
      console.error('Failed to sync calendar:', error);
    } finally {
      setIsSyncingCalendar(false);
    }
  };

  const handleGenerateReport = async () => {
    if (isGeneratingReport) return;
    setReportDataLocal(null);
    setGeneratingReport(true);
    try {
      const reportResponse = await getDailyReport();
      const normalizedReport = normalizeReport(reportResponse);
      setReportDataLocal(normalizedReport || null);
    } catch (error) {
      console.error('Error generating report:', error);
    } finally {
      setGeneratingReport(false);
    }
  };

  const isRTL = lang === 'he';
  const safeRooms = Array.isArray(rooms) ? rooms : [];
  const occupancyPercent = stats?.occupancy?.current ?? 0;
  const revenueToday = stats?.revenue?.today ?? 0;
  const bookingCount = stats?.bookings?.today ?? 0;
  const automationRate = stats?.savings?.automationRate ?? 0;
  const safeLeads = Array.isArray(leads) ? leads : [];
  const safeNotifications = Array.isArray(notifications) ? notifications : [];
  const reportData = normalizeReport(reportDataLocal);
  const isReportOpen = Boolean(reportDataLocal && reportData);

  const dealsClosed = safeLeads.filter((lead) => ['paid', 'converted'].includes(lead.status || '')).length;
  const closedValue = safeLeads
    .filter((lead) => ['paid', 'converted'].includes(lead.status || ''))
    .reduce((sum, lead) => sum + (lead.value || 0), 0);
  const reportHoursSaved = reportData?.roi_metrics?.hours_saved ?? 0;
  const ROI_HOURLY_RATE = 200;
  const estimatedROI = Math.round(reportHoursSaved * ROI_HOURLY_RATE + closedValue);
  const leadsFoundTotal = automationStats?.leads_total ?? safeLeads.length;
  const messagesSent = automationStats?.automated_messages ?? 0;
  const liveHoursSaved = Math.round(((messagesSent * 10) / 60) * 10) / 10;
  const calendarStatusClass = calendarStatus?.synced ? 'calendar-status online' : 'calendar-status offline';

  const heroCards = [
    {
      id: 'occupancy',
      title: t('dashboard.stats.occupancy'),
      value: `${occupancyPercent}%`,
      subtitle: t('dashboard.rooms.status.occupied'),
      trend: stats?.occupancy?.trend ?? 0,
      icon: Users,
      color: 'blue',
      image: safeRooms[0]?.image,
    },
    {
      id: 'revenue',
      title: t('dashboard.stats.revenueToday'),
      value: `₪${revenueToday.toLocaleString()}`,
      subtitle: t('dashboard.charts.revenue'),
      trend: stats?.revenue?.trend ?? 0,
      icon: DollarSign,
      color: 'gold',
      image: safeRooms[1]?.image,
    },
    {
      id: 'bookings',
      title: t('dashboard.stats.bookingsToday'),
      value: bookingCount,
      subtitle: t('dashboard.charts.bookings'),
      trend: stats?.bookings?.trend ?? 0,
      icon: Calendar,
      color: 'purple',
      image: safeRooms[2]?.image,
    },
    {
      id: 'automation',
      title: t('dashboard.stats.automationSavings'),
      value: `₪${estimatedROI.toLocaleString()}`,
      subtitle: t('dashboard.report.automationMessage', { rate: automationRate }),
      trend: automationRate,
      icon: Zap,
      color: 'emerald',
      image: safeRooms[3]?.image,
    },
  ];

  const getNextStatus = (current) => {
    const cycle = ['available', 'occupied', 'cleaning', 'maintenance'];
    const currentIndex = cycle.indexOf(current);
    const nextIndex = currentIndex === -1 ? 0 : (currentIndex + 1) % cycle.length;
    return cycle[nextIndex];
  };

  const managementAlerts = buildManagementAlerts({
    leads: safeLeads,
    notifications: safeNotifications,
    t,
  });

  return (
    <motion.div
      className="enterprise-dashboard"
      variants={containerVariants}
      initial="hidden"
      animate="visible"
      dir={isRTL ? 'rtl' : 'ltr'}
    >
      {/* Header */}
      <motion.div className="dashboard-header" variants={itemVariants}>
        <div className="header-content">
          <h1 className="dashboard-title">
            <Building2 size={32} />
            <span className="text-gradient">{t('branding.name')}</span>
          </h1>
          <p className="dashboard-subtitle">{t('dashboard.subtitle')}</p>
        </div>
        <motion.button
          whileHover={{ scale: 1.02 }}
          whileTap={{ scale: 0.98 }}
          onClick={handleGenerateReport}
          disabled={isGeneratingReport}
          className="btn-primary report-btn"
        >
          {isGeneratingReport ? (
            <>
              <Activity size={20} className="animate-spin" />
              {t('dashboard.generatingReport')}
            </>
          ) : (
            <>
              <FileText size={20} />
              {t('dashboard.generateReport')}
            </>
          )}
        </motion.button>
      </motion.div>

      {/* Hero Insight Cards */}
      <motion.div className="insight-grid" variants={itemVariants}>
        {heroCards.map((card) => (
          <StatCard
            key={card.id}
            title={card.title}
            value={card.value}
            subtitle={card.subtitle}
            trend={card.trend}
            icon={card.icon}
            color={card.color}
            image={card.image}
            t={t}
          />
        ))}
      </motion.div>

      <motion.div className="automation-overview glass-card" variants={itemVariants}>
        <div className="automation-overview-header">
          <h3><Bot size={20} /> AI Sales ROI</h3>
          <span>{t('dashboard.report.automationMessage', { rate: automationRate })}</span>
        </div>
        <div className="automation-overview-grid">
          <div className="automation-overview-item">
            <span className="label">Leads Found</span>
            <span className="value">{leadsFoundTotal}</span>
          </div>
          <div className="automation-overview-item">
            <span className="label">AI Messages</span>
            <span className="value">{messagesSent}</span>
          </div>
          <div className="automation-overview-item">
            <span className="label">Hours Saved</span>
            <span className="value">{liveHoursSaved}</span>
          </div>
          <div className="automation-overview-item">
            <span className="label">Deals Closed</span>
            <span className="value">{dealsClosed}</span>
          </div>
          <div className="automation-overview-item highlight">
            <span className="label">Estimated ROI</span>
            <span className="value">₪{estimatedROI.toLocaleString()}</span>
          </div>
        </div>
      </motion.div>

      {/* Main Content Grid */}
      <div className="dashboard-grid">
        {/* Revenue Chart */}
        <motion.div className="glass-card chart-card revenue-chart" variants={itemVariants}>
          <div className="card-header">
            <h3><BarChart3 size={20} /> {t('dashboard.charts.revenueBookings')}</h3>
            <div className="chart-legend">
              <span className="legend-item"><span className="dot gold" /> {t('dashboard.charts.revenue')}</span>
              <span className="legend-item"><span className="dot blue" /> {t('dashboard.charts.bookings')}</span>
            </div>
          </div>
          <div className="chart-container">
            <ResponsiveContainer width="100%" height={280}>
              <AreaChart data={revenueData}>
                <defs>
                  <linearGradient id="colorRevenue" x1="0" y1="0" x2="0" y2="1">
                    <stop offset="5%" stopColor="#eab308" stopOpacity={0.4} />
                    <stop offset="95%" stopColor="#eab308" stopOpacity={0} />
                  </linearGradient>
                  <linearGradient id="colorBookings" x1="0" y1="0" x2="0" y2="1">
                    <stop offset="5%" stopColor="#3b82f6" stopOpacity={0.4} />
                    <stop offset="95%" stopColor="#3b82f6" stopOpacity={0} />
                  </linearGradient>
                </defs>
                <CartesianGrid strokeDasharray="3 3" stroke="rgba(255,255,255,0.1)" />
                <XAxis dataKey="name" stroke="#64748b" />
                <YAxis stroke="#64748b" />
                <Tooltip
                  contentStyle={{
                    background: 'rgba(15, 23, 42, 0.9)',
                    border: '1px solid rgba(255,255,255,0.1)',
                    borderRadius: '12px',
                    color: '#fff',
                  }}
                />
                <Area
                  type="monotone"
                  dataKey="revenue"
                  stroke="#eab308"
                  fillOpacity={1}
                  fill="url(#colorRevenue)"
                  strokeWidth={2}
                />
                <Area
                  type="monotone"
                  dataKey="bookings"
                  stroke="#3b82f6"
                  fillOpacity={1}
                  fill="url(#colorBookings)"
                  strokeWidth={2}
                  yAxisId={0}
                />
              </AreaChart>
            </ResponsiveContainer>
          </div>
        </motion.div>

        {/* Occupancy Chart */}
        <motion.div className="glass-card chart-card occupancy-chart" variants={itemVariants}>
          <div className="card-header">
            <h3><PieChart size={20} /> {t('dashboard.charts.roomStatus')}</h3>
          </div>
          <div className="chart-container pie-chart-container">
            <ResponsiveContainer width="100%" height={240}>
              <RechartsPie>
                <Pie
                  data={occupancyData}
                  cx="50%"
                  cy="50%"
                  innerRadius={60}
                  outerRadius={90}
                  paddingAngle={5}
                  dataKey="value"
                >
                  {occupancyData.map((entry, index) => (
                    <Cell key={`cell-${index}`} fill={entry.color} />
                  ))}
                </Pie>
                <Tooltip
                  contentStyle={{
                    background: 'rgba(15, 23, 42, 0.9)',
                    border: '1px solid rgba(255,255,255,0.1)',
                    borderRadius: '12px',
                    color: '#fff',
                  }}
                />
              </RechartsPie>
            </ResponsiveContainer>
            <div className="pie-legend">
              {occupancyData.map((item) => (
                <div key={item.nameKey} className="pie-legend-item">
                  <span className="pie-dot" style={{ background: item.color }} />
                  <span>{t(item.nameKey)}</span>
                  <span className="pie-value">{item.value}%</span>
                </div>
              ))}
            </div>
          </div>
        </motion.div>

        {/* Agent Status */}
        <motion.div className="glass-card agents-card" variants={itemVariants}>
          <div className="card-header">
            <h3><Bot size={20} /> {t('dashboard.charts.agentsStatus')}</h3>
            <span className="badge-success">{t('dashboard.charts.agentsAllActive')}</span>
          </div>
          <div className="agents-list">
            {Object.entries(agents).map(([id, agent]) => (
              <AgentStatusItem key={id} id={id} agent={agent} t={t} />
            ))}
          </div>
        </motion.div>

        {/* Management Alerts */}
        <motion.div className="glass-card alerts-card" variants={itemVariants}>
          <div className="card-header">
            <h3><AlertCircle size={20} /> {t('dashboard.alerts.title')}</h3>
          </div>
          <div className="alerts-list">
            {managementAlerts.length === 0 ? (
              <div className="alert-empty">{t('dashboard.alerts.empty')}</div>
            ) : (
              managementAlerts.map((alert) => (
                <div key={alert.id} className={`alert-item ${alert.level}`}>
                  <div className="alert-dot" />
                  <div className="alert-content">
                    <span className="alert-title">{alert.title}</span>
                    <span className="alert-subtitle">{alert.subtitle}</span>
                  </div>
                  <span className="alert-time">{alert.time}</span>
                </div>
              ))
            )}
          </div>
        </motion.div>

        {/* Rooms Grid */}
        <motion.div className="glass-card rooms-card" variants={itemVariants}>
          <div className="card-header">
            <h3><Building2 size={20} /> {t('dashboard.rooms.title')}</h3>
            <span className="room-count">{t('dashboard.rooms.count', { count: safeRooms.length })}</span>
          </div>
          <div className="rooms-grid modern-rooms">
            {safeRooms.slice(0, 6).map((room) => (
              <RoomCard
                key={room.id}
                room={room}
                onViewDetails={() => setSelectedRoom(room)}
                onWhatsApp={async () => {
                  try {
                    await whatsappService.notifyCleaningRequest(room.id, lang);
                  } catch (error) {
                    console.error('Failed to send WhatsApp request:', error);
                  }
                }}
                onToggleStatus={async () => {
                  const nextStatus = getNextStatus(room.status);
                  setRoomStatusLoading(room.id);
                  try {
                    await updateRoomStatus(room.id, { status: nextStatus });
                    updateRoom(room.id, { status: nextStatus });
                  } catch (error) {
                    console.error('Failed to update room status:', error);
                  } finally {
                    setRoomStatusLoading(null);
                  }
                }}
                statusLoading={roomStatusLoading === room.id}
                t={t}
              />
            ))}
          </div>
        </motion.div>

        {/* Agent Performance Chart */}
        <motion.div className="glass-card performance-chart" variants={itemVariants}>
          <div className="card-header">
            <h3><Target size={20} /> {t('dashboard.charts.agentsPerformance')}</h3>
          </div>
          <div className="chart-container">
            <ResponsiveContainer width="100%" height={220}>
              <BarChart data={agentPerformance} layout="vertical">
                <CartesianGrid strokeDasharray="3 3" stroke="rgba(255,255,255,0.1)" />
                <XAxis type="number" stroke="#64748b" />
                <YAxis type="category" dataKey="name" stroke="#64748b" width={80} />
                <Tooltip
                  contentStyle={{
                    background: 'rgba(15, 23, 42, 0.9)',
                    border: '1px solid rgba(255,255,255,0.1)',
                    borderRadius: '12px',
                    color: '#fff',
                  }}
                />
                <Bar dataKey="tasks" fill="#3b82f6" radius={[0, 4, 4, 0]} name={t('dashboard.charts.tasks')} />
                <Bar dataKey="success" fill="#10b981" radius={[0, 4, 4, 0]} name={t('dashboard.charts.successes')} />
              </BarChart>
            </ResponsiveContainer>
          </div>
        </motion.div>
      </div>

      {/* Daily Report Modal */}
      <ReportModal
        report={reportData}
        automationStats={{
          leadsFoundTotal,
          messagesSent,
          liveHoursSaved,
          estimatedROI,
          dealsClosed,
          closedValue,
          reportHoursSaved,
        }}
        isOpen={isReportOpen}
        onClose={() => setReportDataLocal(null)}
        t={t}
      />

      {/* Room Details Modal */}
      {selectedRoom && (
        <div
          className="report-modal-backdrop"
          onClick={() => setSelectedRoom(null)}
        >
          <div
            className="report-modal glass-card room-details-modal"
            onClick={(e) => e.stopPropagation()}
          >
            <div className="report-header">
              <h2>
                <Building2 size={22} />
                {t('dashboard.rooms.roomLabel', { number: selectedRoom.id })}
              </h2>
              <button onClick={() => setSelectedRoom(null)} className="close-btn">×</button>
            </div>

            <div className="report-content">
              <div className="room-details-grid">
                <div className="room-detail-item">
                  <span className="detail-label">{t('dashboard.rooms.title')}</span>
                  <span className="detail-value">{selectedRoom.name}</span>
                </div>
                <div className="room-detail-item">
                  <span className="detail-label">{t('dashboard.charts.roomStatus')}</span>
                  <span className="detail-value">{t(`dashboard.rooms.status.${selectedRoom.status}`)}</span>
                </div>
                <div className="room-detail-item">
                  <span className="detail-label">{t('roles.guest')}</span>
                  <span className="detail-value">{selectedRoom.guest || '-'}</span>
                </div>
                <div className="room-detail-item">
                  <span className="detail-label">{t('dashboard.rooms.checkout')}</span>
                  <span className="detail-value">{selectedRoom.checkOut || '-'}</span>
                </div>
              </div>
            </div>

            <div className="report-footer room-details-actions">
              <button
                className="btn-secondary"
                onClick={() => setSelectedRoom(null)}
              >
                {t('common.close')}
              </button>
              <button
                className="btn-primary"
                disabled={roomActionLoading}
                onClick={async () => {
                  setRoomActionLoading(true);
                  try {
                    await updateRoomStatus(selectedRoom.id, { status: 'cleaning' });
                    updateRoom(selectedRoom.id, { status: 'cleaning' });
                  } catch (error) {
                    console.error('Failed to trigger cleaning:', error);
                  } finally {
                    setRoomActionLoading(false);
                    setSelectedRoom(null);
                  }
                }}
              >
                <Brush size={18} />
                {t('dashboard.rooms.sendCleaner')}
              </button>
            </div>
          </div>
        </div>
      )}
    </motion.div>
  );
};

// Stat Card Component
const normalizeReport = (report) => {
  if (!report || typeof report !== 'object') {
    return null;
  }

  const leadsCaptured = report.roi_metrics?.leads_captured ?? 0;
  const cleaningsTriggered = report.roi_metrics?.cleanings_triggered ?? 0;
  const interactionCount = Number.isFinite(report.automation_interactions)
    ? report.automation_interactions
    : leadsCaptured + cleaningsTriggered;
  const humanHoursSaved = Math.round(((interactionCount * 10) / 60) * 10) / 10;

  return {
    date: report.date || new Date().toISOString().split('T')[0],
    roi_metrics: {
      hours_saved: report.roi_metrics?.hours_saved ?? 0,
      cleanings_triggered: cleaningsTriggered,
      leads_captured: leadsCaptured,
      human_hours_saved: humanHoursSaved,
    },
    recommendations: Array.isArray(report.recommendations)
      ? report.recommendations.filter(Boolean).map((rec) => String(rec))
      : [],
    agent_performance: Array.isArray(report.agent_performance) ? report.agent_performance : [],
    savings: {
      automation_rate: report.savings?.automation_rate ?? 0,
    },
  };
};

const buildManagementAlerts = ({ leads, notifications, t }) => {
  const now = Date.now();
  const toTime = (value) => {
    const ts = new Date(value).getTime();
    if (!Number.isFinite(ts)) return t('dashboard.alerts.justNow');
    const diffMin = Math.floor((now - ts) / 60000);
    if (diffMin < 1) return t('dashboard.alerts.justNow');
    if (diffMin < 60) return t('dashboard.alerts.minutesAgo', { count: diffMin });
    const diffHours = Math.floor(diffMin / 60);
    return t('dashboard.alerts.hoursAgo', { count: diffHours });
  };

  const leadNoReply = leads
    .filter((lead) => lead.status === 'contacted' && lead.createdAt)
    .filter((lead) => now - new Date(lead.createdAt).getTime() > 2 * 60 * 60 * 1000)
    .map((lead) => ({
      id: `lead-no-reply-${lead.id}`,
      level: 'high',
      title: t('dashboard.alerts.leadNoReply', { name: lead.contact || lead.name }),
      subtitle: lead.name,
      time: toTime(lead.createdAt),
    }));

  const bookingAlerts = leads
    .filter((lead) => ['booked', 'converted'].includes(lead.status))
    .map((lead) => ({
      id: `booking-${lead.id}`,
      level: 'medium',
      title: t('dashboard.alerts.bookingConfirmed'),
      subtitle: lead.name,
      time: toTime(lead.createdAt),
    }));

  const notificationAlerts = notifications
    .filter((n) => n.type === 'warning' || n.type === 'success')
    .slice(0, 3)
    .map((n) => ({
      id: `notification-${n.id}`,
      level: n.type === 'warning' ? 'high' : 'low',
      title: n.title || t('dashboard.alerts.notification'),
      subtitle: n.message || '',
      time: toTime(n.timestamp),
    }));

  return [...leadNoReply, ...bookingAlerts, ...notificationAlerts].slice(0, 5);
};

