            <div className="report-stat">
              <span className="stat-value" style={{ color: '#10b981' }}>{safeReport.roi_metrics.cleanings_triggered}</span>
              <span className="stat-label">{t('dashboard.report.cleaningsTriggered')}</span>
            </div>
            <div className="report-stat">
