                <span className="stat-value" style={{ color: '#38bdf8' }}>{safeAutomation.leadsFoundTotal ?? 0}</span>
                <span className="stat-label">Leads Found</span>
              </div>
              <div className="report-stat">
                <span className="stat-value" style={{ color: '#a855f7' }}>{safeAutomation.messagesSent ?? 0}</span>
                <span className="stat-label">AI Messages</span>
              </div>
              <div className="report-stat">
                <span className="stat-value" style={{ color: '#10b981' }}>{safeAutomation.dealsClosed ?? 0}</span>
                <span className="stat-label">Deals Closed</span>
              </div>
              <div className="report-stat">
                <span className="stat-value text-gradient">₪{(safeAutomation.estimatedROI ?? 0).toLocaleString()}</span>
                <span className="stat-label">Estimated ROI</span>
              </div>
            </div>
          </div>

          <div className="report-section">
            <h4>{t('dashboard.report.recommendationsTitle')}</h4>
            <ul className="recommendations-list">
              {safeReport.recommendations.map((rec, i) => (
                <li key={i}><CheckCircle2 size={16} /> {rec}</li>
              ))}
            </ul>
          </div>

          <div className="report-section">
            <h4>{t('dashboard.report.agentSummaryTitle')}</h4>
            <div className="agent-summary">
              {safeReport.agent_performance.map((agent, idx) => (
                <div key={agent.type || agent.name || idx} className="agent-summary-item">
                  <span className="agent-name">{agent.name}</span>
                  <span className="agent-tasks">{agent.tasks_today} {t('dashboard.charts.tasks')}</span>
