/**
 * WorkerView â€” TikTok-style worker portal
 * Route: /worker/:workerName  (e.g. /worker/levikobi)
 *
 * Design:
 *  - Full-bleed dark gradient, one card at a time (swipe-stack feel)
 *  - Huge bold room numbers â€” like a scoreboard
 *  - CSS pop-in on mount, confetti burst + slide-out on complete
 *  - Auto-refresh every 30 s, manual spin button
 */
import React, { useState, useEffect, useCallback, useRef } from 'react';
import { API_URL } from '../utils/apiClient';

/* â”€â”€ palette â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€ */
const P = {
  bg1: '#0a0f1e',
  bg2: '#0f2a1e',
  green:  '#25D366',
  teal:   '#075E54',
  accent: '#34d399',
  amber:  '#fbbf24',
  red:    '#f87171',
  white:  '#ffffff',
  muted:  'rgba(255,255,255,0.45)',
  card:   'rgba(255,255,255,0.06)',
  border: 'rgba(255,255,255,0.12)',
};

/* â”€â”€ helpers â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€ */
const REFRESH_MS = 30_000;

function workerNameFromPath() {
  const parts = window.location.pathname.split('/').filter(Boolean);
  return parts[1] ? decodeURIComponent(parts[1]) : null;
}

function fmt(iso) {
  if (!iso) return '';
  try {
    const d = new Date(iso);
    return d.toLocaleTimeString('he-IL', { hour: '2-digit', minute: '2-digit' }) +
      '  ' +
      d.toLocaleDateString('he-IL', { day: '2-digit', month: '2-digit' });
  } catch { return ''; }
}

function isPending(s = '') {
  return ['pending', 'Pending', 'assigned', 'Assigned'].includes(s);
}

/* â”€â”€ CSS injection (keyframes only) â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€ */
const STYLES = `
  @keyframes wvPopIn {
    0%   { opacity:0; transform:scale(0.72) translateY(40px); }
    60%  { transform:scale(1.04) translateY(-4px); }
    100% { opacity:1; transform:scale(1) translateY(0); }
  }
  @keyframes wvSlideRight {
    to { opacity:0; transform:translateX(120%) rotate(8deg); }
  }
  @keyframes wvFlyUp {
    0%   { opacity:1; transform:translate(var(--tx),var(--ty)) scale(1); }
    100% { opacity:0; transform:translate(var(--tx2),var(--ty2)) scale(0.4); }
  }
  @keyframes wvPulse {
    0%,100% { box-shadow: 0 0 0 0 rgba(37,211,102,0.5); }
    50%     { box-shadow: 0 0 0 18px rgba(37,211,102,0); }
  }
  @keyframes wvSpin {
    to { transform:rotate(360deg); }
  }
  @keyframes wvToastIn {
    from { opacity:0; transform:translateX(-50%) translateY(16px); }
    to   { opacity:1; transform:translateX(-50%) translateY(0); }
  }
  @keyframes wvBounce {
    0%,100% { transform:scale(1); }
    40%     { transform:scale(1.15); }
    70%     { transform:scale(0.92); }
  }
  body { margin:0; }
`;

/* â”€â”€ Confetti â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€ */
const CONFETTI_COLORS = ['#25D366','#fbbf24','#f87171','#60a5fa','#c084fc','#34d399','#ffffff'];

function Confetti({ origin }) {
  const particles = Array.from({ length: 28 }, (_, i) => {
    const angle = (i / 28) * 360;
    const dist  = 60 + Math.random() * 90;
    const rad   = (angle * Math.PI) / 180;
    const tx2   = `${Math.cos(rad) * dist}px`;
    const ty2   = `${Math.sin(rad) * dist - 60}px`;
    const color = CONFETTI_COLORS[i % CONFETTI_COLORS.length];
    const size  = 6 + Math.random() * 8;
    const delay = Math.random() * 0.2;
    return { tx2, ty2, color, size, delay };
  });

  return (
    <div style={{ position:'absolute', top: origin.y, left: origin.x, pointerEvents:'none', zIndex:50 }}>
      {particles.map((p, i) => (
        <div
          key={i}
          style={{
            position: 'absolute',
            width: p.size, height: p.size,
            borderRadius: Math.random() > 0.5 ? '50%' : '2px',
            background: p.color,
            '--tx': '0px', '--ty': '0px',
            '--tx2': p.tx2, '--ty2': p.ty2,
            animation: `wvFlyUp 0.9s cubic-bezier(0.22,1,0.36,1) ${p.delay}s forwards`,
          }}
        />
      ))}
    </div>
  );
}

/* â”€â”€ Toast â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€ */
function Toast({ msg, onClose }) {
  useEffect(() => {
    const t = setTimeout(onClose, 3000);
    return () => clearTimeout(t);
  }, [onClose]);
  return (
    <div style={{
      position:'fixed', bottom:30, left:'50%',
      transform:'translateX(-50%)',
      background: `linear-gradient(135deg, ${P.teal}, ${P.green})`,
      color:'#fff', borderRadius:50,
      padding:'13px 28px', fontWeight:800, fontSize:15,
      boxShadow:'0 8px 32px rgba(37,211,102,0.45)',
      zIndex:9999, whiteSpace:'nowrap',
      animation:'wvToastIn .3s ease',
    }}>
      {msg}
    </div>
  );
}

/* â”€â”€ TaskCard â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€ */
function TaskCard({ task, index, workerName, onDone }) {
  const [phase,     setPhase]     = useState('idle');   // idle | loading | done | exit
  const [confetti,  setConfetti]  = useState(null);
  const btnRef = useRef(null);

  const room = task.property_name || task.room || (task.property_id ? `×—×“×¨ ${task.property_id}` : '×—×“×¨');
  const desc = task.description || task.content || task.task_type || '×‘×™×¦×•×¢ ××©×™××”';
  const staff = task.staff_name || workerName || '';

  const handleComplete = async () => {
    if (phase !== 'idle') return;
    setPhase('loading');

    // Confetti origin
    if (btnRef.current) {
      const rect = btnRef.current.getBoundingClientRect();
      const cardRect = btnRef.current.closest('[data-card]')?.getBoundingClientRect() || rect;
      setConfetti({
        x: rect.left - cardRect.left + rect.width / 2,
        y: rect.top  - cardRect.top  + rect.height / 2,
      });
    }

    try {
      const res = await fetch(`${API_URL}/api/property-tasks/${task.id}`, {
        method:  'PATCH',
        headers: { 'Content-Type': 'application/json' },
        body:    JSON.stringify({ status: 'completed' }),
      });
      if (res.ok) {
        setPhase('done');
        setTimeout(() => {
          setPhase('exit');
          setTimeout(() => onDone(task.id), 600);
        }, 1600);
      } else {
        const d = await res.json().catch(() => ({}));
        alert('×©×’×™××”: ' + (d.error || res.status));
        setPhase('idle');
        setConfetti(null);
      }
    } catch {
      alert('×©×’×™××ª ×—×™×‘×•×¨ â€” × ×¡×” ×©×•×‘');
      setPhase('idle');
      setConfetti(null);
    }
  };

  const isDone    = phase === 'done' || phase === 'exit';
  const isLoading = phase === 'loading';
  const isExit    = phase === 'exit';

  return (
    <div
      data-card
      style={{
        position: 'relative',
        background: `linear-gradient(145deg, rgba(255,255,255,0.09) 0%, rgba(255,255,255,0.04) 100%)`,
        border: isDone ? `2px solid ${P.green}` : `2px solid ${P.border}`,
        borderRadius: 28,
        overflow: 'hidden',
        marginBottom: 20,
        boxShadow: isDone
          ? `0 0 40px rgba(37,211,102,0.25), 0 8px 32px rgba(0,0,0,0.4)`
          : '0 8px 32px rgba(0,0,0,0.35)',
        animation: isExit
          ? 'wvSlideRight 0.55s cubic-bezier(0.4,0,1,1) forwards'
          : `wvPopIn 0.6s cubic-bezier(0.175,0.885,0.32,1.275) ${index * 0.08}s both`,
        backdropFilter: 'blur(12px)',
        WebkitBackdropFilter: 'blur(12px)',
        transition: 'border-color 0.3s',
      }}
    >
      {/* Confetti burst */}
      {confetti && <Confetti origin={confetti} />}

      {/* Glowing top accent bar */}
      <div style={{
        height: 4,
        background: isDone
          ? `linear-gradient(90deg, ${P.green}, ${P.accent})`
          : `linear-gradient(90deg, ${P.teal}, ${P.green})`,
        transition: 'background 0.5s',
      }} />

      {/* Header row: status badge + time */}
      <div style={{
        display: 'flex', justifyContent: 'space-between', alignItems: 'center',
        padding: '14px 20px 0',
      }}>
        <span style={{
          background: isDone ? 'rgba(37,211,102,0.18)' : 'rgba(251,191,36,0.15)',
          color:  isDone ? P.accent : P.amber,
          border: `1px solid ${isDone ? 'rgba(37,211,102,0.35)' : 'rgba(251,191,36,0.3)'}`,
          fontSize: 11, fontWeight: 800, padding: '4px 12px', borderRadius: 30,
          letterSpacing: '0.07em', textTransform: 'uppercase',
        }}>
          {isDone ? 'âœ… DONE' : 'â³ PENDING'}
        </span>
        <span style={{ color: P.muted, fontSize: 12 }}>{fmt(task.created_at)}</span>
      </div>

      {/* ROOM â€” Giant hero number */}
      <div style={{ padding: '12px 20px 0', direction: 'rtl' }}>
        <div style={{ fontSize: 11, color: P.muted, fontWeight: 700, letterSpacing: '0.1em', textTransform: 'uppercase', marginBottom: 2 }}>
          ×—×“×¨ / × ×›×¡
        </div>
        <div style={{
          fontSize: 'clamp(52px, 14vw, 86px)',
          fontWeight: 900,
          color: P.white,
          lineHeight: 1,
          letterSpacing: '-0.03em',
          textShadow: `0 0 40px rgba(37,211,102,0.3)`,
          animation: isDone ? 'wvBounce 0.5s ease' : 'none',
        }}>
          {room}
        </div>
      </div>

      {/* Description */}
      <div style={{ padding: '10px 20px 0', direction: 'rtl' }}>
        <div style={{
          background: 'rgba(255,255,255,0.07)',
          borderRadius: 14,
          padding: '10px 14px',
          fontSize: 14,
          color: 'rgba(255,255,255,0.82)',
          lineHeight: 1.6,
          border: '1px solid rgba(255,255,255,0.08)',
        }}>
          {desc}
        </div>
      </div>

      {/* Staff chip */}
      {staff && (
        <div style={{ padding: '8px 20px 0', direction: 'rtl' }}>
          <span style={{
            fontSize: 12, color: P.muted, background: 'rgba(255,255,255,0.06)',
            borderRadius: 20, padding: '4px 12px',
          }}>
            ğŸ‘¤ {staff}
          </span>
        </div>
      )}

      {/* Action */}
      <div style={{ padding: '16px 20px 20px', direction: 'rtl' }}>
        {isDone ? (
          <div style={{
            textAlign: 'center', padding: '16px',
            background: 'rgba(37,211,102,0.12)',
            borderRadius: 18, border: '1px solid rgba(37,211,102,0.3)',
            color: P.accent, fontWeight: 800, fontSize: 16,
            animation: 'wvBounce 0.5s ease',
          }}>
            ğŸ‰ ×›×œ ×”×›×‘×•×“! ××©×™××” ×”×•×©×œ××”
          </div>
        ) : (
          <button
            ref={btnRef}
            onClick={handleComplete}
            disabled={isLoading}
            style={{
              width: '100%', padding: '17px 0',
              background: isLoading
                ? 'rgba(37,211,102,0.3)'
                : `linear-gradient(135deg, #16a34a 0%, ${P.green} 50%, #34d399 100%)`,
              border: 'none', borderRadius: 18,
              color: '#fff', fontWeight: 900, fontSize: 17,
              cursor: isLoading ? 'wait' : 'pointer',
              letterSpacing: '0.04em',
              boxShadow: '0 6px 24px rgba(37,211,102,0.45)',
              animation: 'wvPulse 2s infinite',
              transition: 'background 0.3s, transform 0.15s',
              display: 'flex', alignItems: 'center', justifyContent: 'center', gap: 10,
            }}
            onMouseDown={(e) => { e.currentTarget.style.transform = 'scale(0.96)'; }}
            onMouseUp={(e)   => { e.currentTarget.style.transform = 'scale(1)'; }}
          >
            {isLoading
              ? <><span style={{ animation: 'wvSpin 0.7s linear infinite', display:'inline-block' }}>â³</span> ×©×•××¨...</>
              : <>ğŸ &nbsp; ×¡××Ÿ ×›×”×•×©×œ×</>}
          </button>
        )}
      </div>
    </div>
  );
}

/* â”€â”€ Main â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€ */
export default function WorkerView() {
  const workerName = workerNameFromPath() || '×¢×•×‘×“';
  const [tasks,   setTasks]   = useState([]);
  const [loading, setLoading] = useState(true);
  const [spin,    setSpin]    = useState(false);
  const [toast,   setToast]   = useState(null);
  const [lastSync,setLastSync]= useState(null);
