import React, { useState, useEffect, useCallback } from 'react';
import {
  Sparkles, RefreshCw, Send, Search,
  Instagram, Facebook, Mail, Target, TrendingUp, Users,
  CheckCircle, Loader2, Copy, Star, BarChart3, Zap, PlusCircle,
  Phone, Calendar, Wifi, WifiOff, Coffee, Bed, LogOut, Bell,
  MessageSquare, ChevronRight
} from 'lucide-react';
import Card from '../ui/Card';
import Button from '../ui/Button';
import {
  getAIInsights, getAgentProfile, getLeads, getLeadsStats, createLead,
  generateMarketingPost, searchPropertyLeads, draftOutreachEmail,
  generateAcquisitionStrategy, publishPost, sendOutreachEmail
} from '../../services/api';
import { API_BASE_URL } from '../../utils/constants';
import './OwnerView.css';

/**
 * easyhost Owner Dashboard - Maya AI Concierge
 * Premium glassmorphism design with smart AI suggestions
 */
const OwnerView = ({ aiLog, onShowSummary, onAIAction, t }) => {
  // Agent State
  const [agent, setAgent] = useState(null);
  const [activeTab, setActiveTab] = useState('dashboard');

  // Marketing Post State
  const [postPlatform, setPostPlatform] = useState('instagram');
  const [propertyName, setPropertyName] = useState('Villa Mykonos');
  const [generatedPost, setGeneratedPost] = useState(null);
  const [postLoading, setPostLoading] = useState(false);
  const [postPublished, setPostPublished] = useState(false);

  // Lead Search State
  const [searchLocation, setSearchLocation] = useState('Mykonos, Greece');
  const [searchType, setSearchType] = useState('villa');
  const [searchResults, setSearchResults] = useState(null);
  const [searchLoading, setSearchLoading] = useState(false);
  const [selectedLead, setSelectedLead] = useState(null);
  const [emailDraft, setEmailDraft] = useState(null);
  const [emailLoading, setEmailLoading] = useState(false);
  const [emailSent, setEmailSent] = useState(false);

  // Acquisition Strategy State
  const [targetSegment, setTargetSegment] = useState('luxury travelers');
  const [strategy, setStrategy] = useState(null);
  const [strategyLoading, setStrategyLoading] = useState(false);

  // Insight State
  const [aiInsight, setAiInsight] = useState(null);

  // Real Leads State (from database)
  const [realLeads, setRealLeads] = useState([]);
  const [leadsStats, setLeadsStats] = useState(null);
  const [leadsLoading, setLeadsLoading] = useState(false);
  const [serverOnline, setServerOnline] = useState(null);

  // Smart Suggestions State
  const [suggestions, setSuggestions] = useState([]);
  const [suggestionsLoading, setSuggestionsLoading] = useState(false);

  // Fetch smart suggestions from Maya
  const fetchSuggestions = useCallback(async () => {
    setSuggestionsLoading(true);
    try {
      const response = await fetch(`${API_BASE_URL}/api/agent/suggestions`);
      if (response.ok) {
        const data = await response.json();
        setSuggestions(data.suggestions || []);
      }
    } catch (error) {
      console.error('[Maya] Error fetching suggestions:', error);
    } finally {
      setSuggestionsLoading(false);
    }
  }, []);

  // Fetch real leads from database
  const fetchRealLeads = useCallback(async () => {
    setLeadsLoading(true);
    try {
      const [leads, stats] = await Promise.all([
        getLeads(),
        getLeadsStats()
      ]);
      const leadsArray = Array.isArray(leads) ? leads : [];
      setRealLeads(leadsArray);
      setLeadsStats(stats);
      setServerOnline(true);
      console.log(`[easyhost] Loaded ${leadsArray.length} leads`);
    } catch (error) {
      console.error('[easyhost] Error fetching leads:', error);
      setServerOnline(false);
    } finally {
      setLeadsLoading(false);
    }
  }, []);

  // Add demo lead for testing
  const handleAddDemoLead = async () => {
    const demoNames = ['John Smith', 'Maria Garcia', 'David Chen', 'Anna MÃ¼ller', 'Yuki Tanaka'];
    const randomName = demoNames[Math.floor(Math.random() * demoNames.length)];
    const randomGuests = Math.floor(Math.random() * 4) + 1;

    const checkinDate = new Date();
    checkinDate.setDate(checkinDate.getDate() + Math.floor(Math.random() * 23) + 7);
    const checkoutDate = new Date(checkinDate);
    checkoutDate.setDate(checkoutDate.getDate() + Math.floor(Math.random() * 4) + 3);

    const demoLead = {
      name: randomName,
      email: `${randomName.toLowerCase().replace(' ', '.')}@example.com`,
      phone: `+1${Math.floor(Math.random() * 9000000000) + 1000000000}`,
      guests: randomGuests,
      checkin: checkinDate.toISOString().split('T')[0],
      checkout: checkoutDate.toISOString().split('T')[0],
      source: 'widget'
    };

    try {
      console.log('[easyhost] Creating demo lead:', demoLead);
      await createLead(demoLead);
      fetchRealLeads();
      fetchSuggestions();
    } catch (err) {
      console.error('[easyhost] Error creating demo lead:', err);
    }
  };

  // Fetch agent profile and leads on mount
  useEffect(() => {
    const fetchAgent = async () => {
      try {
        const data = await getAgentProfile();
        setAgent(data.agent);
      } catch (error) {
        console.error('Error fetching agent:', error);
        setAgent({
          name: "Maya",
          title: "easyhost AI Concierge",
          avatar: "https://api.dicebear.com/7.x/personas/svg?seed=Maya&backgroundColor=667eea"
        });
      }
    };
    fetchAgent();
    fetchInsights();
    fetchRealLeads();
    fetchSuggestions();
  }, [fetchRealLeads, fetchSuggestions]);

  // Fetch AI insights
  const fetchInsights = useCallback(async () => {
    try {
      const data = await getAIInsights();
      setAiInsight(data);
    } catch (error) {
      console.error('Error fetching insights:', error);
    }
  }, []);

  // Generate Marketing Post
  const handleGeneratePost = async () => {
    setPostLoading(true);
    setPostPublished(false);
    setGeneratedPost(null);
    try {
      const result = await generateMarketingPost({
        property_name: propertyName,
        target_platform: postPlatform,
        offer_type: 'new_listing',
        key_features: ['ocean view', 'private pool', 'luxury amenities'],
        target_audience: 'luxury travelers'
      });
      setGeneratedPost(result);
    } catch (error) {
      console.error('Error generating post:', error);
    } finally {
      setPostLoading(false);
    }
  };

  // Publish Post
  const handlePublishPost = async () => {
    try {
      await publishPost({
        platform: postPlatform,
        content: generatedPost?.post_content
      });
      setPostPublished(true);
    } catch (error) {
      console.error('Error publishing:', error);
    }
  };

  // Search Property Leads
  const handleSearchLeads = async () => {
    setSearchLoading(true);
    setSearchResults(null);
    setEmailDraft(null);
    setSelectedLead(null);
    try {
      const result = await searchPropertyLeads({
        location: searchLocation,
        property_type: searchType,
        min_bedrooms: 3
      });
      setSearchResults(result);
    } catch (error) {
      console.error('Error searching leads:', error);
    } finally {
      setSearchLoading(false);
    }
  };

  // Draft Email for Lead
  const handleDraftEmail = async (lead) => {
    setSelectedLead(lead);
    setEmailLoading(true);
    setEmailDraft(null);
    setEmailSent(false);
    try {
      const result = await draftOutreachEmail({
        owner_name: lead.name,
        property_description: lead.property || `${lead.name}'s property`,
        key_benefits: ['40% higher bookings', 'professional management', 'premium guest network']
      });
      setEmailDraft(result);
    } catch (error) {
      console.error('Error drafting email:', error);
    } finally {
      setEmailLoading(false);
    }
  };

  // Send Email
  const handleSendEmail = async () => {
    try {
      await sendOutreachEmail({
        recipient: selectedLead?.email,
        subject: 'easyhost Partnership',
        content: emailDraft?.email_draft
      });
      setEmailSent(true);
    } catch (error) {
      console.error('Error sending email:', error);
    }
  };

  // Generate Acquisition Strategy
  const handleGenerateStrategy = async () => {
    setStrategyLoading(true);
    setStrategy(null);
    try {
      const result = await generateAcquisitionStrategy({
        target_segment: targetSegment,
        budget_level: 'medium',
        season: 'summer'
      });
      setStrategy(result);
    } catch (error) {
      console.error('Error generating strategy:', error);
    } finally {
      setStrategyLoading(false);
    }
  };

  // Copy to clipboard
  const copyToClipboard = (text) => {
    navigator.clipboard.writeText(text);
  };

  // Platform icons
  const platformIcons = {
    instagram: <Instagram size={18} />,
    facebook: <Facebook size={18} />,
    email: <Mail size={18} />
  };

  // Get suggestion icon
  const getSuggestionIcon = (iconName) => {
    const icons = {
      star: <Star size={20} />,
      users: <Users size={20} />,
      calendar: <Calendar size={20} />,
      sparkles: <Sparkles size={20} />,
    };
    return icons[iconName] || <Bell size={20} />;
  };

  return (
    <div className="owner-view-agent easyhost-theme">
      {/* Maya AI Concierge Header */}
      <div className="agent-header-card glass-card">
        <div className="agent-profile">
          <div className="agent-avatar-wrapper">
            {agent?.avatar ? (
              <img src={agent.avatar} alt={agent?.name} className="agent-avatar-img" />
            ) : (
              <div className="agent-avatar-placeholder">
                <Sparkles size={32} />
              </div>
            )}
            <span className="agent-online-dot"></span>
          </div>
          <div className="agent-info">
            <div className="brand-header">
              <span className="brand-name">easyhost</span>
              <span className="brand-badge">AI</span>
            </div>
            <h2 className="agent-name-title">{agent?.name || 'Maya'}</h2>
            <p className="agent-role-subtitle">{agent?.title || 'AI Concierge'}</p>
          </div>
        </div>
        <div className="agent-metrics">
          <div className="metric-box glass-metric">
            <Users size={20} />
            <div className="metric-data">
              <span className="metric-value">{leadsStats?.total || realLeads.length}</span>
              <span className="metric-label">Leads</span>
            </div>
          </div>
          <div className="metric-box glass-metric">
            <BarChart3 size={20} />
            <div className="metric-data">
              <span className="metric-value">{leadsStats?.new || 0}</span>
              <span className="metric-label">New</span>
            </div>
          </div>
          <div className="metric-box glass-metric">
            <TrendingUp size={20} />
            <div className="metric-data">
              <span className="metric-value">{leadsStats?.won || 0}</span>
              <span className="metric-label">Won</span>
            </div>
          </div>
          <div className={`metric-box glass-metric status-box ${serverOnline ? 'online' : 'offline'}`}>
            {serverOnline === null ? (
              <Loader2 size={20} className="spin" />
            ) : serverOnline ? (
              <Wifi size={20} />
            ) : (
              <WifiOff size={20} />
            )}
            <div className="metric-data">
              <span className="metric-value">
                {serverOnline === null ? '...' : serverOnline ? 'Online' : 'Offline'}
              </span>
              <span className="metric-label">Server</span>
            </div>
          </div>
        </div>
      </div>

      {/* Smart Suggestions from Maya */}
      {suggestions.length > 0 && activeTab === 'dashboard' && (
        <div className="suggestions-section">
          <h3 className="section-heading">
            <MessageSquare size={18} />
            Maya's Suggestions
          </h3>
          <div className="suggestions-list">
            {suggestions.map((suggestion, idx) => (
              <div key={idx} className={`suggestion-card glass-card priority-${suggestion.priority}`}>
                <div className="suggestion-icon">
                  {getSuggestionIcon(suggestion.icon)}
                </div>
                <div className="suggestion-content">
                  <h4>{suggestion.title}</h4>
                  <p>{suggestion.message}</p>
                </div>
                {suggestion.action && (
                  <button className="suggestion-action">
                    <ChevronRight size={18} />
                  </button>
                )}
              </div>
            ))}
          </div>
        </div>
      )}

      {/* Service Modules - Thailand Style */}
      {activeTab === 'dashboard' && (
        <div className="services-grid">
          <div className="service-card glass-card" onClick={() => console.log('Room Service')}>
            <div className="service-icon service-room">
              <Coffee size={28} />
            </div>
            <h4>Room Service</h4>
            <p>Order food & drinks</p>
          </div>
          <div className="service-card glass-card" onClick={() => console.log('Housekeeping')}>
            <div className="service-icon service-housekeeping">
              <Bed size={28} />
            </div>
            <h4>Housekeeping</h4>
            <p>Clean & refresh</p>
          </div>
          <div className="service-card glass-card" onClick={() => console.log('Checkout')}>
            <div className="service-icon service-checkout">
              <LogOut size={28} />
            </div>
            <h4>Checkout</h4>
            <p>Express departure</p>
          </div>
          <div className="service-card glass-card" onClick={() => setActiveTab('myleads')}>
            <div className="service-icon service-leads">
              <Users size={28} />
            </div>
            <h4>My Leads</h4>
            <p>{realLeads.length} contacts</p>
          </div>
        </div>
      )}

      {/* Navigation Tabs */}
      <div className="agent-nav-tabs glass-tabs">
        <button
          className={`nav-tab-btn ${activeTab === 'dashboard' ? 'active' : ''}`}
          onClick={() => setActiveTab('dashboard')}
        >
          <Sparkles size={18} />
          <span>Dashboard</span>
        </button>
        <button
          className={`nav-tab-btn ${activeTab === 'myleads' ? 'active' : ''}`}
          onClick={() => setActiveTab('myleads')}
        >
          <Users size={18} />
          <span>Leads</span>
          {realLeads.length > 0 && <span className="tab-badge">{realLeads.length}</span>}
        </button>
        <button
          className={`nav-tab-btn ${activeTab === 'marketing' ? 'active' : ''}`}
          onClick={() => setActiveTab('marketing')}
        >
          <Instagram size={18} />
          <span>Marketing</span>
        </button>
        <button
          className={`nav-tab-btn ${activeTab === 'acquisition' ? 'active' : ''}`}
          onClick={() => setActiveTab('acquisition')}
        >
          <Target size={18} />
          <span>Strategy</span>
        </button>
      </div>

      {/* Tab Content */}
      <div className="tab-content-area">
        {/* My Leads Tab */}
        {activeTab === 'myleads' && (
          <Card className="content-card glass-card">
            <div className="content-card-header">
              <div className="header-with-actions">
                <div>
                  <h3><Users size={20} /> Sales Leads</h3>
                  <p>Real-time leads from easyhost</p>
                </div>
                <div className="header-actions">
                  <Button
                    variant="primary"
                    size="sm"
                    onClick={handleAddDemoLead}
                    className="demo-btn"
                  >
                    <PlusCircle size={16} />
                    Add Demo
                  </Button>
                  <Button
                    variant="ghost"
                    size="sm"
                    onClick={() => { fetchRealLeads(); fetchSuggestions(); }}
                    disabled={leadsLoading}
                  >
                    <RefreshCw size={16} className={leadsLoading ? 'spin' : ''} />
                  </Button>
                </div>
              </div>
            </div>
            <div className="content-card-body">
              {/* Stats Row */}
              <div className="leads-stats-row">
                <div className="stat-pill">
                  <span className="stat-number">{leadsStats?.total || 0}</span>
                  <span className="stat-text">Total</span>
                </div>
                <div className="stat-pill stat-new">
                  <span className="stat-number">{leadsStats?.new || 0}</span>
                  <span className="stat-text">New</span>
                </div>
                <div className="stat-pill stat-qualified">
                  <span className="stat-number">{leadsStats?.qualified || 0}</span>
                  <span className="stat-text">Qualified</span>
                </div>
                <div className="stat-pill stat-won">
                  <span className="stat-number">{leadsStats?.won || 0}</span>
                  <span className="stat-text">Won</span>
                </div>
              </div>

              {/* Leads List */}
              {leadsLoading ? (
                <div className="leads-loading-state">
                  <Loader2 size={32} className="spin" />
                  <p>Loading leads...</p>
                </div>
              ) : realLeads.length === 0 ? (
                <div className="leads-empty-state">
                  <Users size={48} />
                  <h4>No leads yet</h4>
                  <p>Click "Add Demo" to test the connection</p>
                  <Button variant="primary" onClick={handleAddDemoLead}>
                    <PlusCircle size={18} />
                    Create First Lead
                  </Button>
                </div>
              ) : (
                <div className="real-leads-list">
                  {realLeads.slice(0, 10).map(lead => (
                    <div key={lead.id} className="real-lead-card glass-card">
                      <div className="lead-score-badge" data-score={lead.score >= 70 ? 'high' : lead.score >= 40 ? 'medium' : 'low'}>
                        {lead.score}
                      </div>
                      <div className="lead-main-info">
                        <h4 className="lead-name">{lead.name}</h4>
                        <div className="lead-contact-row">
                          {lead.email && (
                            <span className="lead-contact-item">
                              <Mail size={12} />
                              {lead.email}
                            </span>
                          )}
                          {lead.phone && (
                            <span className="lead-contact-item">
                              <Phone size={12} />
                              {lead.phone}
                            </span>
                          )}
                        </div>
                        {(lead.checkin || lead.checkout) && (
                          <div className="lead-dates-row">
                            <Calendar size={12} />
                            <span>{lead.checkin} â {lead.checkout}</span>
                            {lead.guests && <span className="lead-guests">{lead.guests} guests</span>}
                          </div>
                        )}
                      </div>
                      <div className="lead-status-col">
                        <span className={`lead-status-badge status-${lead.status}`}>
                          {lead.status}
                        </span>
                        <span className="lead-source">{lead.source}</span>
                      </div>
                      <div className="lead-actions-col">
                        <Button
                          variant="secondary"
                          size="sm"
                          onClick={() => handleDraftEmail(lead)}
                        >
                          <Mail size={14} />
                          Email
                        </Button>
                      </div>
                    </div>
                  ))}
                </div>
              )}

              {/* Email Draft Section */}
              {emailDraft && (
                <div className="email-section glass-card">
                  <div className="output-header">
                    <span className="output-badge">
                      <Mail size={16} /> Email to {selectedLead?.name}
                    </span>
                    <button className="icon-btn" onClick={() => copyToClipboard(emailDraft.email_draft)}>
                      <Copy size={16} />
                    </button>
                  </div>
                  <pre className="output-content email-content">{emailDraft.email_draft}</pre>
                  <div className="output-actions">
                    {emailSent ? (
                      <Button variant="success" disabled className="success-btn">
                        <CheckCircle size={18} /> Sent!
                      </Button>
                    ) : (
                      <Button variant="accent" onClick={handleSendEmail} className="send-btn">
                        <Send size={18} /> Send Email
                      </Button>
                    )}
                  </div>
                </div>
              )}
            </div>
          </Card>
        )}

        {/* Marketing Posts Tab */}
        {activeTab === 'marketing' && (
          <Card className="content-card glass-card">
            <div className="content-card-header">
              <h3><Sparkles size={20} /> Marketing Post Generator</h3>
              <p>Create engaging content with Maya</p>
            </div>
            <div className="content-card-body">
              <div className="input-row">
                <div className="input-group">
                  <label>Property Name</label>
                  <input
                    type="text"
                    value={propertyName}
                    onChange={(e) => setPropertyName(e.target.value)}
                    placeholder="Villa Mykonos"
                    className="form-input glass-input"
                  />
                </div>
                <div className="input-group">
                  <label>Platform</label>
                  <div className="platform-buttons">
                    {['instagram', 'facebook', 'email'].map(platform => (
                      <button
                        key={platform}
                        className={`platform-select-btn ${postPlatform === platform ? 'selected' : ''}`}
                        onClick={() => setPostPlatform(platform)}
                      >
                        {platformIcons[platform]}
                        <span>{platform}</span>
                      </button>
                    ))}
                  </div>
                </div>
              </div>

              <Button
                variant="primary"
                onClick={handleGeneratePost}
                disabled={postLoading}
                className="action-btn-full"
              >
                {postLoading ? (
                  <><Loader2 size={18} className="spin" /> Generating...</>
                ) : (
                  <><Sparkles size={18} /> Generate Post</>
                )}
              </Button>

              {generatedPost && (
                <div className="output-section glass-card">
                  <div className="output-header">
                    <span className="output-badge">
                      {platformIcons[postPlatform]} {postPlatform.toUpperCase()}
                    </span>
                    <button className="icon-btn" onClick={() => copyToClipboard(generatedPost.post_content)}>
                      <Copy size={16} />
                    </button>
                  </div>
                  <pre className="output-content">{generatedPost.post_content}</pre>
                  <div className="output-actions">
                    {postPublished ? (
                      <Button variant="success" disabled className="success-btn">
                        <CheckCircle size={18} /> Published!
                      </Button>
                    ) : (
                      <Button variant="accent" onClick={handlePublishPost} className="publish-btn">
                        <Send size={18} /> Publish
                      </Button>
                    )}
                  </div>
                </div>
              )}
            </div>
          </Card>
        )}

        {/* Acquisition Strategy Tab */}
        {activeTab === 'acquisition' && (
          <Card className="content-card glass-card">
            <div className="content-card-header">
              <h3><Target size={20} /> Client Acquisition Strategy</h3>
              <p>Data-driven strategies from Maya</p>
            </div>
            <div className="content-card-body">
              <div className="input-row single">
                <div className="input-group">
                  <label>Target Segment</label>
                  <select
                    value={targetSegment}
                    onChange={(e) => setTargetSegment(e.target.value)}
                    className="form-select glass-input"
                  >
                    <option value="luxury travelers">Luxury Travelers</option>
                    <option value="families">Families</option>
                    <option value="business travelers">Business Travelers</option>
                    <option value="couples">Couples & Honeymoon</option>
                  </select>
                </div>
              </div>

              <Button
                variant="primary"
                onClick={handleGenerateStrategy}
                disabled={strategyLoading}
                className="action-btn-full"
              >
                {strategyLoading ? (
                  <><Loader2 size={18} className="spin" /> Generating...</>
                ) : (
                  <><TrendingUp size={18} /> Generate Strategy</>
                )}
              </Button>

              {strategy && (
                <div className="strategy-section">
                  <div className="strategy-block glass-card">
                    <h4><TrendingUp size={18} /> Marketing Channels</h4>
                    <div className="channels-list">
                      {strategy.strategy.recommendations.channels.map((channel, i) => (
                        <div key={i} className="channel-row">
                          <span className="channel-name">{channel.name}</span>
                          <span className={`channel-priority priority-${channel.priority.toLowerCase()}`}>
                            {channel.priority}
                          </span>
                        </div>
                      ))}
                    </div>
                  </div>

                  <div className="strategy-block glass-card">
                    <h4>Pricing Strategy</h4>
                    <div className="pricing-info">
                      <strong>{strategy.strategy.recommendations.pricing_strategy.type}</strong>
                      <p>{strategy.strategy.recommendations.pricing_strategy.suggestion}</p>
                    </div>
                  </div>

                  <div className="strategy-block glass-card">
                    <h4>Ad Headlines</h4>
                    <div className="headlines-row">
                      {strategy.strategy.recommendations.ad_headlines.map((headline, i) => (
                        <div key={i} className="headline-box">
                          <span>{headline}</span>
                          <button className="icon-btn-sm" onClick={() => copyToClipboard(headline)}>
                            <Copy size={14} />
                          </button>
                        </div>
                      ))}
                    </div>
                  </div>
                </div>
              )}
            </div>
          </Card>
        )}
      </div>

      {/* AI Insight Footer */}
      <div className="insight-footer-bar glass-card">
        <div className="insight-message">
          <Sparkles size={18} />
          <span>{aiInsight?.insight || 'Maya is ready to help grow your easyhost business!'}</span>
        </div>
        <button className="refresh-insight-btn" onClick={() => { fetchInsights(); fetchSuggestions(); }}>
          <RefreshCw size={16} />
        </button>
      </div>
    </div>
  );
};

export default OwnerView;
