/**
 * ManagerPipeline ‚Äî "Completed Today" glassmorphism feed
 * Polls /api/completed-today every 12 s.
 * Shows: [Time] Task Completed: Room X (Type) by Worker
 */
import React, { useState, useEffect, useCallback, useRef } from 'react';
import { API_URL } from '../../utils/apiClient';

/* ‚îÄ‚îÄ styles injected once ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ */
const CSS = `
  @keyframes mpFadeIn {
    from { opacity:0; transform:translateY(10px); }
    to   { opacity:1; transform:translateY(0); }
  }
  @keyframes mpPulse {
    0%,100% { opacity:1; }
    50%     { opacity:0.4; }
  }
  @keyframes mpSpin {
    to { transform:rotate(360deg); }
  }
`;

/* ‚îÄ‚îÄ helpers ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ */
function fmtTime(iso) {
  if (!iso) return '--:--';
  try {
    return new Date(iso).toLocaleTimeString('he-IL', { hour: '2-digit', minute: '2-digit' });
  } catch { return '--:--'; }
}

function taskLabel(t) {
  const room  = t.property_name || t.room || (t.property_id ? `◊ó◊ì◊® ${t.property_id}` : '◊ó◊ì◊® ?');
  const type  = t.description   || t.task_type || t.content || '◊û◊©◊ô◊û◊î';
  const short = type.length > 60 ? type.slice(0, 58) + '‚Ä¶' : type;
  return { room, short };
}

/* ‚îÄ‚îÄ FeedRow ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ */
function FeedRow({ task, idx }) {
  const { room, short } = taskLabel(task);
  const worker = task.staff_name || '‚Äî';

  return (
    <div style={{
      display: 'flex', gap: 14, alignItems: 'flex-start',
      padding: '12px 16px',
      borderBottom: '1px solid rgba(255,255,255,0.06)',
      animation: `mpFadeIn 0.4s ease ${idx * 0.05}s both`,
      transition: 'background 0.2s',
    }}
    onMouseEnter={(e) => { e.currentTarget.style.background = 'rgba(255,255,255,0.04)'; }}
    onMouseLeave={(e) => { e.currentTarget.style.background = 'transparent'; }}
    >
      {/* Time + dot */}
      <div style={{ display:'flex', flexDirection:'column', alignItems:'center', gap:4, flexShrink:0, paddingTop:3 }}>
        <span style={{ fontSize:11, fontWeight:700, color:'rgba(255,255,255,0.4)', whiteSpace:'nowrap' }}>
          {fmtTime(task.updated_at || task.created_at)}
        </span>
