import React, { useEffect, useRef, useState } from 'react';
import { createPortal } from 'react-dom';
import {
  Building2, TrendingUp, Users, Calendar, DollarSign,
  Activity, Clock, CheckCircle2, AlertCircle, Sparkles,
  BarChart3, PieChart, ArrowUpRight, ArrowDownRight,
  Bot, Zap, Target, FileText, ExternalLink, Check, Brush,
  MessageCircle, ToggleLeft, ToggleRight, Home, Globe, RefreshCw
} from 'lucide-react';
import {
  AreaChart, Area, BarChart, Bar, PieChart as RechartsPie, Pie, Cell,
  XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer, Legend
} from 'recharts';
import i18n from '../../i18n';
import useStore from '../../store/useStore';
import { updateRoomStatus, getDailyReport, getAutomationStats, subscribeToLeads, connectCalendar, getCalendarStatus, getPilotAccessToken, getStaffList, subscribeToStaff, createManualCheckout, getManualRooms, createManualRoomWithDescription, checkoutManualRoom, checkinManualRoom, getManualRoomHistory, assignManualRoom, searchProperties, resolveManualRoom, refreshCalendar } from '../../services/api';
import { whatsappService } from '../../services/whatsapp';
import './EnterpriseDashboard.css';

const occupancyData = [
  { nameKey: 'dashboard.rooms.status.occupied', value: 72, color: '#10b981' },
  { nameKey: 'dashboard.rooms.status.available', value: 18, color: '#3b82f6' },
  { nameKey: 'dashboard.rooms.status.maintenance', value: 6, color: '#f59e0b' },
  { nameKey: 'dashboard.rooms.status.cleaning', value: 4, color: '#8b5cf6' },
];

const agentPerformance = [
  { name: 'Scraper', tasks: 156, success: 148 },
  { name: 'Marketing', tasks: 89, success: 82 },
  { name: 'Creative', tasks: 45, success: 43 },
  { name: 'Sales', tasks: 67, success: 58 },
  { name: 'Operations', tasks: 234, success: 228 },
];

const EnterpriseDashboard = () => {
  const {
    stats,
    rooms,
    agents,
    leads,
    notifications,
    automationStats,
    setAutomationStats,
    setObjectionSuccess,
    setGeneratingReport,
    isGeneratingReport,
    lang,
    updateRoom,
    activeTenantId,
    authToken,
    calendarMode,
    setCalendarMode,
  } = useStore();
  const t = (key, options) => i18n.t(key, options);
  const [selectedRoom, setSelectedRoom] = useState(null);
  const [roomActionLoading, setRoomActionLoading] = useState(false);
  const [roomStatusLoading, setRoomStatusLoading] = useState(null);
  const [reportDataLocal, setReportDataLocal] = useState(null);
  const [calendarStatus, setCalendarStatus] = useState({ synced: false, vacant_nights: 0, potential_revenue: 0 });
  const [icalUrl, setIcalUrl] = useState('');
  const [nightlyRate, setNightlyRate] = useState(250);
  const [isSyncingCalendar, setIsSyncingCalendar] = useState(false);
  const [pilotName, setPilotName] = useState('');
  const [pilotToken, setPilotToken] = useState('');
  const [pilotLink, setPilotLink] = useState('');
  const [isGeneratingPilot, setIsGeneratingPilot] = useState(false);
  const [pilotCopied, setPilotCopied] = useState(false);
  const [staffList, setStaffList] = useState([]);
  const [staffLoading, setStaffLoading] = useState(false);
  const [showGuide, setShowGuide] = useState(false);
  const [showSecurity, setShowSecurity] = useState(false);
  const [guideTab, setGuideTab] = useState('airbnb');
  const [exportLoading, setExportLoading] = useState(null);
  const [manualCheckoutDate, setManualCheckoutDate] = useState('');
  const [manualRoom, setManualRoom] = useState('');
  const [isManualCreating, setIsManualCreating] = useState(false);
  const [isDemoRunning, setIsDemoRunning] = useState(false);
  const [manualRooms, setManualRooms] = useState([]);
  const [manualRoomName, setManualRoomName] = useState('');
  const [manualRoomDescription, setManualRoomDescription] = useState('');
  const [manualRoomLoading, setManualRoomLoading] = useState(false);
  const [manualCheckoutLoading, setManualCheckoutLoading] = useState(null);
  const [manualCheckinLoading, setManualCheckinLoading] = useState(null);
  const [manualAssignLoading, setManualAssignLoading] = useState(null);
  const [manualAssignStaff, setManualAssignStaff] = useState({});
  const [historyRoom, setHistoryRoom] = useState(null);
  const [historyItems, setHistoryItems] = useState([]);
  const [historyLoading, setHistoryLoading] = useState(false);
  const [historyFilterStaffId, setHistoryFilterStaffId] = useState('all');
  const [historyIssues, setHistoryIssues] = useState([]);
  const [historyRoomStatus, setHistoryRoomStatus] = useState('active');
  const [historyResolveLoading, setHistoryResolveLoading] = useState(false);
  const [propertyQuery, setPropertyQuery] = useState('');
  const [propertyResults, setPropertyResults] = useState([]);
  const [propertyLoading, setPropertyLoading] = useState(false);
  const [exportOpen, setExportOpen] = useState(false);
  const [sidebarOpen, setSidebarOpen] = useState(true);
  const [importUrl, setImportUrl] = useState('');
  const [importLoading, setImportLoading] = useState(false);
  const [importError, setImportError] = useState(false);
  const importTimeoutRef = useRef(null);
  const lastImportedRef = useRef('');

  useEffect(() => {
    if (lang && i18n.language !== lang) {
      i18n.changeLanguage(lang);
    }
  }, [lang]);

  useEffect(() => {
    let isActive = true;
    const loadAutomationStats = async () => {
      try {
        const statsResponse = await getAutomationStats();
        if (!isActive) return;
        setAutomationStats(statsResponse.automation_stats);
        setObjectionSuccess(statsResponse.objection_success);
      } catch (error) {
        console.error('Error fetching automation stats:', error);
      }
    };

    const loadReport = async () => {
      try {
        const reportResponse = await getDailyReport();
        if (!isActive) return;
        setReportDataLocal(normalizeReport(reportResponse));
      } catch (error) {
        console.error('Error fetching report:', error);
      }
    };

    loadAutomationStats();
    loadReport();
    const eventSource = subscribeToLeads(
      () => {},
      () => {},
      (error) => {
        console.error('Automation stats stream error:', error);
      },
      (statsUpdate) => {
        setAutomationStats(statsUpdate);
      }
    );

    return () => {
      isActive = false;
      if (eventSource && eventSource.close) eventSource.close();
    };
  }, [setAutomationStats, setObjectionSuccess, activeTenantId, authToken]);

  useEffect(() => {
    let isActive = true;
    const loadCalendarStatus = async () => {
      try {
        const status = await getCalendarStatus();
        if (isActive) {
          setCalendarStatus(status);
        }
      } catch (error) {
        if (isActive) {
          setCalendarStatus({ synced: false, vacant_nights: 0, potential_revenue: 0 });
        }
      }
    };
    loadCalendarStatus();
    return () => {
      isActive = false;
    };
  }, [activeTenantId, authToken]);

  useEffect(() => {
    let mounted = true;
    const loadRooms = async () => {
      try {
        const data = await getManualRooms();
        if (mounted) setManualRooms(data || []);
      } catch (error) {
        console.error('Failed to load manual rooms:', error);
      }
    };
    loadRooms();
    const interval = setInterval(loadRooms, 20000);
    return () => {
      mounted = false;
      clearInterval(interval);
    };
  }, [activeTenantId, authToken]);

  useEffect(() => {
    let mounted = true;
    const loadStaff = async () => {
      setStaffLoading(true);
      try {
        const data = await getStaffList();
        if (mounted) setStaffList(data || []);
      } catch (error) {
        console.error('Failed to load staff list:', error);
      } finally {
        if (mounted) setStaffLoading(false);
      }
    };
    loadStaff();
    const source = subscribeToStaff((update) => {
      setStaffList((prev) => {
        const next = [...prev];
        const index = next.findIndex((item) => item.id === update.id);
        if (index >= 0) {
          next[index] = { ...next[index], ...update };
        } else {
          next.unshift(update);
        }
        return next;
      });
    });
    return () => {
      mounted = false;
      if (source && source.close) source.close();
    };
  }, [activeTenantId, authToken]);

  const handleSyncCalendar = async () => {
    if (!icalUrl) return;
    setIsSyncingCalendar(true);
    try {
      const status = await connectCalendar(icalUrl, nightlyRate);
      setCalendarStatus({ ...status, synced: true });
    } catch (error) {
      console.error('Failed to sync calendar:', error);
    } finally {
      setIsSyncingCalendar(false);
    }
  };

  const handleManualCheckout = async () => {
    setIsManualCreating(true);
    try {
      const date = manualCheckoutDate || new Date().toISOString().split('T')[0];
      await createManualCheckout(date, manualRoom);
      setManualCheckoutDate('');
      setManualRoom('');
    } catch (error) {
      console.error('Failed to create manual checkout:', error);
    } finally {
      setIsManualCreating(false);
    }
  };

  const handleCreateRoom = async () => {
    if (!manualRoomName.trim()) return;
    setManualRoomLoading(true);
    try {
      const room = await createManualRoomWithDescription(
        manualRoomName.trim(),
        manualRoomDescription.trim()
      );
      setManualRooms((prev) => [room, ...prev]);
      setManualRoomName('');
      setManualRoomDescription('');
    } catch (error) {
      console.error('Failed to create manual room:', error);
    } finally {
      setManualRoomLoading(false);
    }
  };

  const handleCheckoutRoom = async (roomId) => {
    setManualCheckoutLoading(roomId);
    try {
      await checkoutManualRoom(roomId);
      setManualRooms((prev) =>
        prev.map((room) =>
          room.id === roomId ? { ...room, last_checkout_at: new Date().toISOString() } : room
        )
      );
    } catch (error) {
      console.error('Failed to checkout room:', error);
    } finally {
      setManualCheckoutLoading(null);
    }
  };

  const handleCheckinRoom = async (roomId) => {
    setManualCheckinLoading(roomId);
    try {
      await checkinManualRoom(roomId);
      setManualRooms((prev) =>
        prev.map((room) =>
          room.id === roomId ? { ...room, last_checkin_at: new Date().toISOString() } : room
        )
      );
    } catch (error) {
      console.error('Failed to check-in room:', error);
    } finally {
      setManualCheckinLoading(null);
    }
  };

  const handleOpenHistory = async (room) => {
    setHistoryRoom(room);
    setHistoryLoading(true);
    setHistoryFilterStaffId('all');
    try {
      const data = await getManualRoomHistory(room.id);
      setHistoryItems(data?.history || []);
      setHistoryIssues(data?.issues || []);
      setHistoryRoomStatus(data?.room_status || 'active');
    } catch (error) {
      console.error('Failed to load room history:', error);
    } finally {
      setHistoryLoading(false);
    }
  };

  const handleResolveRoom = async () => {
    if (!historyRoom) return;
    setHistoryResolveLoading(true);
    try {
      await resolveManualRoom(historyRoom.id);
      setHistoryRoomStatus('active');
      setManualRooms((prev) =>
        prev.map((room) => (room.id === historyRoom.id ? { ...room, status: 'active' } : room))
      );
    } catch (error) {
      console.error('Failed to resolve room:', error);
    } finally {
      setHistoryResolveLoading(false);
    }
  };

  const handleAssignRoom = async (roomId) => {
    const staffId = manualAssignStaff[roomId];
    if (!staffId) return;
    setManualAssignLoading(roomId);
    try {
      await assignManualRoom(roomId, staffId);
      setManualAssignStaff((prev) => ({ ...prev, [roomId]: '' }));
    } catch (error) {
      console.error('Failed to assign room:', error);
    } finally {
      setManualAssignLoading(null);
    }
  };

  const handlePropertySearch = async () => {
    if (!propertyQuery.trim()) return;
    setPropertyLoading(true);
    try {
      const results = await searchProperties(propertyQuery.trim());
      setPropertyResults(results || []);
    } catch (error) {
      console.error('Failed to search properties:', error);
    } finally {
      setPropertyLoading(false);
    }
  };

  const handleApplyProperty = (property) => {
    setManualRoomName(property.name);
    setManualRoomDescription(
      `${property.city} · ${property.rooms} rooms. ${property.description || ''}`.trim()
    );
  };

  const buildMockRoomFromLink = (url) => {
    const slug = url.split('/').filter(Boolean).pop() || 'Listing';
    const safeSlug = slug.replace(/[-_]/g, ' ').replace(/\s+/g, ' ').trim();
    const seed = safeSlug.length % 5;
    const images = [
      'https://images.unsplash.com/photo-1505693416388-ac5ce068fe85?w=900&auto=format&fit=crop',
      'https://images.unsplash.com/photo-1502672023488-70e25813eb80?w=900&auto=format&fit=crop',
      'https://images.unsplash.com/photo-1560448204-603b3fc33ddc?w=900&auto=format&fit=crop',
      'https://images.unsplash.com/photo-1566665797739-1674de7a421a?w=900&auto=format&fit=crop',
      'https://images.unsplash.com/photo-1613977257363-707ba9348227?w=900&auto=format&fit=crop',
    ];
    return {
      id: `import-${Date.now()}`,
      name: safeSlug || t('manual.importFallbackName'),
      description: t('manual.importedFrom', { source: 'Airbnb' }),
      photo_url: images[seed],
      status: 'active',
      last_checkout_at: null,
      latest_staff_name: null,
    };
  };

  const handleImportLink = async () => {
    const trimmed = importUrl.trim();
    if (!trimmed || importLoading) return;
    if (lastImportedRef.current === trimmed) return;
    setImportLoading(true);
    setImportError(false);
    try {
      const room = buildMockRoomFromLink(trimmed);
      setManualRooms((prev) => [room, ...prev]);
      lastImportedRef.current = trimmed;
      setImportUrl('');
    } catch (error) {
      console.error('Failed to import room:', error);
      setImportError(true);
    } finally {
      setImportLoading(false);
    }
  };

  useEffect(() => {
    if (!importUrl.trim()) return;
    if (!/^https?:\/\//i.test(importUrl.trim())) return;
    if (importTimeoutRef.current) clearTimeout(importTimeoutRef.current);
    importTimeoutRef.current = setTimeout(() => {
      handleImportLink();
    }, 550);
    return () => {
      if (importTimeoutRef.current) clearTimeout(importTimeoutRef.current);
    };
  }, [importUrl]);

  const handleDemoSimulation = async () => {
    setIsDemoRunning(true);
    try {
      const today = new Date();
      const dateStr = today.toISOString().split('T')[0];
      await createManualCheckout(dateStr, 'Demo Suite 301');
      setCalendarStatus({
        synced: true,
        vacant_nights: 12,
        potential_revenue: 3000,
      });
      setReportDataLocal({
        date: dateStr,
        roi_metrics: {
          hours_saved: 14,
          cleanings_triggered: 3,
          leads_captured: 7,
        },
        automation_interactions: 32,
        message_activity: { inbound: 12, outbound: 20 },
        revenue_summary: { paid_revenue: 5200, potential_revenue: 3000 },
        recommendations: ['AI concierge ready for peak season.'],
        agent_performance: [],
        savings: { automation_rate: 86 },
      });
      setAutomationStats({
        automated_messages: 20,
        leads_total: 7,
        last_scan: new Date().toISOString(),
      });
    } catch (error) {
      console.error('Failed to run demo simulation:', error);
    } finally {
      setIsDemoRunning(false);
    }
  };

  const handleGeneratePilot = async () => {
    if (!pilotName) return;
    setIsGeneratingPilot(true);
    try {
      const result = await getPilotAccessToken(pilotName);
      const token = result.token || '';
      setPilotToken(token);
      if (token) {
        setPilotLink(`${window.location.origin}/?token=${token}`);
      }
    } catch (error) {
      console.error('Failed to generate pilot token:', error);
    } finally {
      setIsGeneratingPilot(false);
    }
  };

  const handleCopyPilotLink = async () => {
    const value = pilotLink || pilotToken;
    if (!value) return;
    try {
      if (navigator?.clipboard?.writeText) {
        await navigator.clipboard.writeText(value);
      } else {
        const temp = document.createElement('input');
        temp.value = value;
        document.body.appendChild(temp);
        temp.select();
        document.execCommand('copy');
        temp.remove();
      }
