// Stat Card Component
const normalizeReport = (report) => {
  if (!report || typeof report !== 'object') {
    return null;
  }

  const leadsCaptured = report.roi_metrics?.leads_captured ?? 0;
  const cleaningsTriggered = report.roi_metrics?.cleanings_triggered ?? 0;
  const interactionCount = Number.isFinite(report.automation_interactions)
    ? report.automation_interactions
    : leadsCaptured + cleaningsTriggered;
  const humanHoursSaved = Math.round(((interactionCount * 10) / 60) * 10) / 10;

  return {
    date: report.date || new Date().toISOString().split('T')[0],
    roi_metrics: {
      hours_saved: report.roi_metrics?.hours_saved ?? 0,
      cleanings_triggered: cleaningsTriggered,
      leads_captured: leadsCaptured,
      human_hours_saved: humanHoursSaved,
    },
    recommendations: Array.isArray(report.recommendations)
      ? report.recommendations.filter(Boolean).map((rec) => String(rec))
      : [],
    agent_performance: Array.isArray(report.agent_performance) ? report.agent_performance : [],
    savings: {
      automation_rate: report.savings?.automation_rate ?? 0,
    },
    message_activity: report.message_activity || { inbound: 0, outbound: 0 },
    revenue_summary: report.revenue_summary || { paid_revenue: 0 },
  };
};

const buildManagementAlerts = ({ leads, notifications, t }) => {
  const now = Date.now();
  const toTime = (value) => {
    const ts = new Date(value).getTime();
    if (!Number.isFinite(ts)) return t('dashboard.alerts.justNow');
    const diffMin = Math.floor((now - ts) / 60000);
    if (diffMin < 1) return t('dashboard.alerts.justNow');
    if (diffMin < 60) return t('dashboard.alerts.minutesAgo', { count: diffMin });
    const diffHours = Math.floor(diffMin / 60);
    return t('dashboard.alerts.hoursAgo', { count: diffHours });
  };

  const leadNoReply = leads
    .filter((lead) => lead.status === 'contacted' && lead.createdAt)
    .filter((lead) => now - new Date(lead.createdAt).getTime() > 2 * 60 * 60 * 1000)
    .map((lead) => ({
      id: `lead-no-reply-${lead.id}`,
      level: 'high',
      title: t('dashboard.alerts.leadNoReply', { name: lead.contact || lead.name }),
      subtitle: lead.name,
      time: toTime(lead.createdAt),
    }));

  const bookingAlerts = leads
    .filter((lead) => ['booked', 'converted'].includes(lead.status))
    .map((lead) => ({
      id: `booking-${lead.id}`,
      level: 'medium',
      title: t('dashboard.alerts.bookingConfirmed'),
      subtitle: lead.name,
      time: toTime(lead.createdAt),
    }));

  const notificationAlerts = notifications
    .filter((n) => n.type === 'warning' || n.type === 'success')
    .slice(0, 3)
    .map((n) => ({
      id: `notification-${n.id}`,
      level: n.type === 'warning' ? 'high' : 'low',
      title: n.title || t('dashboard.alerts.notification'),
      subtitle: n.message || '',
      time: toTime(n.timestamp),
    }));

  return [...leadNoReply, ...bookingAlerts, ...notificationAlerts].slice(0, 5);
};

const buildRecentActivity = ({ notifications, t }) => {
  const now = Date.now();
  const toTime = (value) => {
    const ts = new Date(value).getTime();
    if (!Number.isFinite(ts)) return t('dashboard.alerts.justNow');
    const diffMin = Math.floor((now - ts) / 60000);
    if (diffMin < 1) return t('dashboard.alerts.justNow');
    if (diffMin < 60) return t('dashboard.alerts.minutesAgo', { count: diffMin });
    const diffHours = Math.floor(diffMin / 60);
    return t('dashboard.alerts.hoursAgo', { count: diffHours });
  };

  const activityKey = (action) => {
    switch (action) {
      case 'on_my_way':
        return 'activity.onMyWay';
      case 'started':
        return 'activity.cleaningStarted';
      case 'finished':
        return 'activity.cleaningFinished';
      case 'damage_reported':
        return 'activity.damageReported';
      default:
        return 'activity.generic';
    }
  };

  return (notifications || [])
    .filter((item) => item.type === 'activity' || item.type === 'damage' || item.type === 'success')
    .slice(0, 6)
    .map((item) => {
      const room = item.room || item.roomName || t('activity.unknownRoom');
      const key = activityKey(item.action);
      return {
        id: `activity-${item.id}`,
        text: t(key, { room }),
        time: toTime(item.timestamp),
        level: item.type === 'damage' ? 'high' : 'low',
      };
    });
};

