 */
export const subscribeToLeads = (onNewLead, onError) => {
  const eventSource = new EventSource(`${API_BASE_URL}/api/stream/leads`);

  eventSource.addEventListener('new_lead', (event) => {
    try {
      const lead = JSON.parse(event.data);
