import React, { useState, useEffect } from 'react';
import { Bot, Send, BarChart as BarIcon } from 'lucide-react';
import { BarChart, Bar, XAxis, YAxis, Tooltip, ResponsiveContainer } from 'recharts';
import { agents } from './agentData';

function App() {
  const [userInput, setUserInput] = useState("");
  const [aiResponse, setAiResponse] = useState("砖! 注专转  驻.   注专?");
  const [isClient, setIsClient] = useState(false);

  //  驻 砖转 useContext -  砖专祝 注 专拽 砖祝 
  useEffect(() => { setIsClient(true); }, []);

  const askAI = async () => {
    if (!userInput) return;
    setAiResponse("砖...");
    try {
      const response = await fetch('http://127.0.0.1:5000/ask', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ message: userInput }),
      });
      const data = await response.json();
      setAiResponse(data.reply || data.message);
    } catch (e) { setAiResponse("砖 专 砖专转 -AI"); }
  };

  return (
    <div style={{ padding: '20px', fontFamily: 'Arial', direction: 'rtl', backgroundColor: '#f4f7f6', minHeight: '100vh' }}>
      <h1 style={{ textAlign: 'center', color: '#1a73e8' }}> 驻 EasyHost AI</h1>
      
      <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '20px', marginTop: '20px' }}>
        {/* 爪' */}
        <div style={{ background: '#fff', padding: '20px', borderRadius: '15px', boxShadow: '0 4px 6px rgba(0,0,0,0.1)' }}>
          <h3><Bot /> 注专 AI 砖</h3>
          <div style={{ height: '200px', background: '#f8f9fa', padding: '15px', borderRadius: '10px', marginBottom: '10px' }}>{aiResponse}</div>
          <div style={{ display: 'flex', gap: '10px' }}>
            <input value={userInput} onChange={(e)=>setUserInput(e.target.value)} style={{ flex: 1, padding: '10px' }} placeholder="砖 砖..." />
            <button onClick={askAI} style={{ background: '#1a73e8', color: '#fff', border: 'none', padding: '10px 20px', borderRadius: '5px', cursor: 'pointer' }}>砖</button>
          </div>
        </div>

        {/* 专祝 */}
        <div style={{ background: '#fff', padding: '20px', borderRadius: '15px', boxShadow: '0 4px 6px rgba(0,0,0,0.1)' }}>
          <h3>爪注 住</h3>
          <div style={{ height: '250px' }}>
            {isClient && (
              <ResponsiveContainer width="100%" height="100%">
                <BarChart data={agents}>
                  <XAxis dataKey="name" />
                  <YAxis />
                  <Tooltip />
                  <Bar dataKey="tasks" fill="#1a73e8" />
                </BarChart>
              </ResponsiveContainer>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}
export default App;
