import React, { useState, useEffect, useRef, useCallback } from 'react';
import { Users, Phone, Mail, Calendar, UserCheck, Clock, Filter, RefreshCw, Wifi, WifiOff } from 'lucide-react';
import Card from '../ui/Card';
import Button from '../ui/Button';
import LeadDetailsModal from './LeadDetailsModal';
import { getLeads, updateLead, subscribeToLeads } from '../../services/api';
import './LeadsPage.css';

/**
 * Leads page component with table and real-time updates
 */
const LeadsPage = ({ t, lang }) => {
  const [leads, setLeads] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [statusFilter, setStatusFilter] = useState(null);
  const [selectedLead, setSelectedLead] = useState(null);
  const [connectionStatus, setConnectionStatus] = useState('connecting');

  const eventSourceRef = useRef(null);

  // Status options for filter
  const statusOptions = [
    { value: null, label: lang === 'he' ? 'הכל' : 'All' },
    { value: 'new', label: lang === 'he' ? 'חדש' : 'New' },
    { value: 'contacted', label: lang === 'he' ? 'נוצר קשר' : 'Contacted' },
    { value: 'qualified', label: lang === 'he' ? 'מתאים' : 'Qualified' },
    { value: 'booked', label: lang === 'he' ? 'הוזמן' : 'Booked' },
    { value: 'lost', label: lang === 'he' ? 'אבוד' : 'Lost' },
  ];

  // Fetch leads
  const fetchLeads = useCallback(async () => {
    setLoading(true);
    setError(null);
    try {
      const data = await getLeads(statusFilter);
      setLeads(data);
    } catch (err) {
      console.error('Error fetching leads:', err);
      setError(lang === 'he' ? 'שגיאה בטעינת לידים' : 'Error loading leads');
    } finally {
      setLoading(false);
    }
  }, [statusFilter, lang]);

  // Handle new lead from SSE
  const handleNewLead = useCallback((lead) => {
    console.log('New lead received:', lead);
    setLeads(prev => [lead, ...prev]);
  }, []);

  // Start SSE connection
  const startSSE = useCallback(() => {
    if (eventSourceRef.current) {
      eventSourceRef.current.close();
    }

    setConnectionStatus('connecting');

    const eventSource = subscribeToLeads(
      handleNewLead,
      () => setConnectionStatus('error')
    );

    eventSource.addEventListener('connected', () => {
      setConnectionStatus('sse');
    });

    eventSourceRef.current = eventSource;
  }, [handleNewLead]);

  // Initial load
  useEffect(() => {
    fetchLeads();
    startSSE();

    return () => {
      if (eventSourceRef.current) {
        eventSourceRef.current.close();
      }
    };
  }, [fetchLeads, startSSE]);

  // Handle status update
  const handleStatusUpdate = async (leadId, newStatus) => {
    try {
      await updateLead(leadId, { status: newStatus });
      setLeads(prev =>
        prev.map(lead =>
          lead.id === leadId ? { ...lead, status: newStatus } : lead
        )
      );
      if (selectedLead && selectedLead.id === leadId) {
        setSelectedLead(prev => ({ ...prev, status: newStatus }));
      }
    } catch (err) {
      console.error('Error updating lead status:', err);
    }
  };

  // Get score color
  const getScoreColor = (score) => {
    if (score >= 70) return 'score-high';
    if (score >= 40) return 'score-medium';
    return 'score-low';
  };

  // Get status badge class
  const getStatusClass = (status) => {
    return `status-badge status-${status}`;
  };

  // Format date
  const formatDate = (dateStr) => {
    if (!dateStr) return '-';
    return new Date(dateStr).toLocaleDateString(lang === 'he' ? 'he-IL' : 'en-US');
  };

  // Format time
  const formatTime = (dateStr) => {
    if (!dateStr) return '';
    return new Date(dateStr).toLocaleTimeString(lang === 'he' ? 'he-IL' : 'en-US', {
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  // Connection status indicator
  const renderConnectionStatus = () => {
    if (connectionStatus === 'sse') {
      return (
        <div className="connection-status connected">
          <Wifi size={14} />
          <span>Live</span>
        </div>
      );
    }
    if (connectionStatus === 'error') {
      return (
        <div className="connection-status error">
          <WifiOff size={14} />
          <span>Offline</span>
        </div>
      );
    }
    return (
      <div className="connection-status connecting">
        <Wifi size={14} className="blink" />
        <span>...</span>
      </div>
    );
  };

  return (
    <div className="leads-page fade-in">
      {/* Header */}
      <div className="leads-header">
        <div className="leads-title-row">
          <h2 className="leads-title">
            <Users size={24} />
            {lang === 'he' ? 'לידים / מכירות' : 'Sales / Leads'}
          </h2>
          {renderConnectionStatus()}
        </div>

        <div className="leads-actions">
          <div className="filter-group">
            <Filter size={16} />
            <select
              value={statusFilter || ''}
              onChange={(e) => setStatusFilter(e.target.value || null)}
              className="status-filter"
            >
              {statusOptions.map(opt => (
                <option key={opt.value || 'all'} value={opt.value || ''}>
                  {opt.label}
                </option>
              ))}
            </select>
          </div>
          <Button
            variant="ghost"
            size="sm"
            onClick={fetchLeads}
            disabled={loading}
          >
            <RefreshCw size={16} className={loading ? 'spin' : ''} />
          </Button>
        </div>
      </div>

      {/* Stats Cards */}
      <div className="leads-stats">
        <Card className="stat-card">
          <div className="stat-value">{leads.length}</div>
          <div className="stat-label">{lang === 'he' ? 'סה"כ לידים' : 'Total Leads'}</div>
        </Card>
        <Card className="stat-card stat-new">
          <div className="stat-value">{leads.filter(l => l.status === 'new').length}</div>
          <div className="stat-label">{lang === 'he' ? 'חדשים' : 'New'}</div>
        </Card>
        <Card className="stat-card stat-qualified">
          <div className="stat-value">{leads.filter(l => l.status === 'qualified').length}</div>
          <div className="stat-label">{lang === 'he' ? 'מתאימים' : 'Qualified'}</div>
        </Card>
        <Card className="stat-card stat-booked">
          <div className="stat-value">{leads.filter(l => l.status === 'booked').length}</div>
          <div className="stat-label">{lang === 'he' ? 'הוזמנו' : 'Booked'}</div>
        </Card>
      </div>

      {/* Leads Table */}
      <Card className="leads-table-card">
        {loading && leads.length === 0 ? (
          <div className="leads-loading">
            {lang === 'he' ? 'טוען...' : 'Loading...'}
          </div>
        ) : error ? (
          <div className="leads-error">{error}</div>
        ) : leads.length === 0 ? (
          <div className="leads-empty">
            <Users size={48} />
            <p>{lang === 'he' ? 'אין לידים עדיין' : 'No leads yet'}</p>
          </div>
        ) : (
          <div className="leads-table-wrapper">
            <table className="leads-table">
              <thead>
                <tr>
                  <th>{lang === 'he' ? 'ציון' : 'Score'}</th>
                  <th>{lang === 'he' ? 'שם' : 'Name'}</th>
                  <th>{lang === 'he' ? 'יצירת קשר' : 'Contact'}</th>
                  <th>{lang === 'he' ? 'תאריכים' : 'Dates'}</th>
                  <th>{lang === 'he' ? 'אורחים' : 'Guests'}</th>
                  <th>{lang === 'he' ? 'סטטוס' : 'Status'}</th>
                  <th>{lang === 'he' ? 'נוצר' : 'Created'}</th>
                </tr>
              </thead>
              <tbody>
                {leads.map(lead => (
                  <tr
                    key={lead.id}
                    className="lead-row"
                    onClick={() => setSelectedLead(lead)}
                  >
                    <td>
                      <span className={`score-badge ${getScoreColor(lead.score)}`}>
                        {lead.score}
                      </span>
                    </td>
                    <td className="lead-name">{lead.name}</td>
                    <td className="lead-contact">
                      {lead.phone && (
                        <span className="contact-item">
                          <Phone size={14} />
                          {lead.phone}
                        </span>
                      )}
                      {lead.email && (
                        <span className="contact-item">
                          <Mail size={14} />
                          {lead.email}
                        </span>
                      )}
                    </td>
                    <td className="lead-dates">
                      {lead.checkin && lead.checkout ? (
                        <span className="dates-item">
                          <Calendar size={14} />
                          {formatDate(lead.checkin)} - {formatDate(lead.checkout)}
                        </span>
                      ) : (
                        '-'
                      )}
                    </td>
                    <td className="lead-guests">
                      {lead.guests ? (
                        <span className="guests-item">
                          <UserCheck size={14} />
                          {lead.guests}
                        </span>
                      ) : (
                        '-'
                      )}
                    </td>
                    <td>
                      <span className={getStatusClass(lead.status)}>
                        {statusOptions.find(s => s.value === lead.status)?.label || lead.status}
                      </span>
                    </td>
                    <td className="lead-created">
                      <span className="created-item">
                        <Clock size={14} />
                        {formatDate(lead.created_at)}
                        <span className="created-time">{formatTime(lead.created_at)}</span>
                      </span>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
      </Card>

      {/* Lead Details Modal */}
      {selectedLead && (
        <LeadDetailsModal
          lead={selectedLead}
          lang={lang}
          onClose={() => setSelectedLead(null)}
          onStatusUpdate={handleStatusUpdate}
        />
      )}
    </div>
  );
};

export default LeadsPage;
