import React, { useState } from 'react';
import { Bot } from 'lucide-react';
import Header from './components/layout/Header';
import OwnerView from './components/features/OwnerView';
import StaffView from './components/features/StaffView';
import GuestView from './components/features/GuestView';
import Modal from './components/ui/Modal';
import { useTranslations } from './hooks/useTranslations';
import { sendAIAction } from './services/api';
import './styles/global.css';
import './App.css';

function App() {
  const [lang, setLang] = useState('he');
  const [role, setRole] = useState('owner');
  const [aiLog, setAiLog] = useState(['System Ready...']);
  const [showSummary, setShowSummary] = useState(false);
  
  const t = useTranslations(lang);

  // Update AI log when actions are sent
  const handleAIAction = async (type, room) => {
    const timestamp = new Date().toLocaleTimeString();
    const logEntry = `[${timestamp}] ${type} request at ${room}`;
    setAiLog(prev => [logEntry, ...prev.slice(0, 9)]);

    try {
      const result = await sendAIAction(type, room, role, lang);
      if (result && result.draft) {
        const draftEntry = `[${timestamp}] AI Draft: ${result.draft.substring(0, 50)}...`;
        setAiLog(prev => [draftEntry, ...prev.slice(0, 9)]);
      }
    } catch (error) {
      console.error('Error sending AI action:', error);
      const errorEntry = `[${timestamp}] Error: Could not process request`;
      setAiLog(prev => [errorEntry, ...prev.slice(0, 9)]);
    }
  };

  return (
    <div className={`app-container ${lang === 'he' ? 'rtl' : 'ltr'}`}>
      <Header lang={lang} setLang={setLang} role={role} setRole={setRole} t={t} />
      
      <div className="app-content">
        <h1 className="app-title">
          <Bot size={30} />
          {t.title}
        </h1>

        {/* Role-based views */}
        {role === 'owner' && (
          <OwnerView
            aiLog={aiLog}
            onShowSummary={() => setShowSummary(true)}
            onAIAction={handleAIAction}
            t={t}
          />
        )}

        {role === 'staff' && <StaffView t={t} />}

        {role === 'guest' && <GuestView t={t} lang={lang} />}
      </div>

      {/* Summary Modal */}
      <Modal
        isOpen={showSummary}
        onClose={() => setShowSummary(false)}
        title={t.summary}
      >
        <div className="summary-content">
          <p className="summary-text">12 {t.actionsCompleted}</p>
          <div className="modal-footer">
            <button
              className="btn btn-primary"
              onClick={() => setShowSummary(false)}
            >
              {t.close}
            </button>
          </div>
        </div>
      </Modal>
    </div>
  );
}

export default App;
