import React, { useState, useRef, useEffect } from 'react';
import {
  MessageCircle, X, Send, User, Mic, Check,
  Loader2, Search, FileText, Image, Zap, Droplets,
} from 'lucide-react';
import useTranslations from '../../hooks/useTranslations';
import useStore from '../../store/useStore';
import { maya } from '../../services/agentOrchestrator';
import { API_URL } from '../../utils/apiClient';
import './MayaChat.css';

const MAYA_AVATAR_URL =
  'https://api.dicebear.com/7.x/personas/svg?seed=MayaManager&backgroundColor=25D366';

const hasHebrew = (text) => /[\u0590-\u05FF]/.test(text || '');

const MayaChat = () => {
  const mayaChatOpen   = useStore((s) => s.mayaChatOpen);
  const toggleMayaChat = useStore((s) => s.toggleMayaChat);
  const {
    mayaMessages, addMayaMessage,
    mayaIsTyping, setMayaTyping,
    addNotification, stats,
  } = useStore();
  const { t, i18n } = useTranslations();
  const lang  = useStore((s) => s.lang);
  const setLang = useStore((s) => s.setLang);
  const isRTL = lang === 'he';

  const [input, setInput]         = useState('');
  const [mayaOnline, setOnline]   = useState(false);
  const [toast, setToast]         = useState(null);

  const messagesEndRef      = useRef(null);
  const inputRef            = useRef(null);
  const connectedRef        = useRef(false);
  const proactiveSentRef    = useRef(false);

  /* ‚îÄ‚îÄ Auto-scroll ‚îÄ‚îÄ */
  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [mayaMessages]);

  /* ‚îÄ‚îÄ Focus input when chat opens ‚îÄ‚îÄ */
  useEffect(() => {
    if (mayaChatOpen) inputRef.current?.focus();
  }, [mayaChatOpen]);

  /* ‚îÄ‚îÄ Task-created toast ‚îÄ‚îÄ */
  useEffect(() => {
    const handler = (e) => {
      const task = e?.detail?.task;
      if (!task) return;
      const staffName = (task.staff_name || task.staffName || '◊ß◊ï◊ë◊ô').trim() || '◊ß◊ï◊ë◊ô';
      const emoji = /alma|◊¢◊ú◊û◊î/i.test(staffName) ? 'üßπ'
                  : /avi|◊ê◊ë◊ô/i.test(staffName)   ? '‚ö°' : 'üõ†Ô∏è';
      setToast({ staffName, emoji });
      setTimeout(() => setToast(null), 3500);
    };
    window.addEventListener('maya-task-created', handler);
    return () => window.removeEventListener('maya-task-created', handler);
  }, []);

  /* ‚îÄ‚îÄ Health-check on open ‚Üí greeting ‚îÄ‚îÄ */
  useEffect(() => {
    if (!mayaChatOpen) return;
    fetch(`${API_URL}/health`)
      .then((r) => {
        if (r.ok) {
          setOnline(true);
          if (!connectedRef.current) {
            connectedRef.current = true;
            addMayaMessage({ role: 'assistant', content: '◊ß◊ï◊ë◊ô, ◊ê◊†◊ô ◊û◊ó◊ï◊ë◊®◊™ ◊ï◊û◊ï◊õ◊†◊î ◊ú◊¢◊ë◊ï◊ì◊î! üü¢ ◊ê◊ô◊ö ◊ê◊§◊©◊® ◊ú◊¢◊ñ◊ï◊®?' });
          }
        }
      })
      .catch(() => {});
  }, [mayaChatOpen, addMayaMessage]);

  /* ‚îÄ‚îÄ Proactive occupancy message ‚îÄ‚îÄ */
  useEffect(() => {
    if (!mayaChatOpen || proactiveSentRef.current) return;
    if ((stats?.occupancy?.current ?? 0) >= 87) {
      proactiveSentRef.current = true;
      addMayaMessage({ role: 'assistant', content: t('mayaChat.proactiveOccupancy') });
    }
  }, [mayaChatOpen, stats, addMayaMessage, t]);

  /* ‚îÄ‚îÄ Send message ‚îÄ‚îÄ */
  const handleSend = async () => {
    if (!input.trim() || mayaIsTyping) return;
    const userMessage = input.trim();
    if (hasHebrew(userMessage)) setLang('he'); else setLang('en');
    setInput('');

    addMayaMessage({ role: 'user', content: userMessage });
    setMayaTyping(true);

    try {
      const history = mayaMessages.slice(-6).map((m) => ({ role: m.role, content: m.content }));
      const result  = await maya.processCommand(userMessage, { history });
      const msg     = result?.message ?? result?.displayMessage ?? t('common.taskCompleted');

      addMayaMessage({
        role: 'assistant',
        content: typeof msg === 'string' ? msg : (msg?.content ?? msg?.text ?? JSON.stringify(msg)),
        data: result,
      });
      setOnline(true);

      if (result.success) {
        const notifMsg = result?.message ?? result?.displayMessage;
        addNotification({
          type: 'success', title: 'Maya',
          message: typeof notifMsg === 'string' ? notifMsg : (notifMsg ? String(notifMsg) : ''),
        });
      }
      if (result.taskCreated || result.action === 'add_task' || result.task) {
        addNotification({
          type: 'info',
          title: t('notifications.agentUpdate'),
          message: t('notifications.agentTaskCreated', { defaultValue: '◊û◊©◊ô◊û◊î ◊ó◊ì◊©◊î ◊†◊ï◊¶◊®◊î ◊ï◊©◊ï◊ô◊õ◊î ◊ú◊¶◊ï◊ï◊™.' }),
        });
        window.dispatchEvent(new CustomEvent('maya-task-created', {
          detail: { task: result.task || result.task_data },
        }));
      }
    } catch (err) {
      setOnline(false);
      const errMsg = err?.message || '◊©◊í◊ô◊ê◊î - ◊†◊°◊î ◊©◊ï◊ë.';
      addMayaMessage({ role: 'assistant', content: errMsg, isError: true });
    } finally {
      setMayaTyping(false);
    }
  };

  const handleQuickAction = (cmd) => {
    setInput(cmd);
    setTimeout(handleSend, 80);
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) { e.preventDefault(); handleSend(); }
  };

  const timeLocale = i18n.language === 'he' ? 'he-IL' : 'en-US';

  const quickActions = [
    { id: 'scan',   icon: Search,   label: t('mayaChat.quickActions.scan'),       command: 'scan new Airbnb leads' },
    { id: 'report', icon: FileText, label: t('mayaChat.quickActions.report'),     command: 'generate daily report' },
    { id: 'clean',  icon: Droplets, label: t('mayaChat.quickActions.cleaning'),   command: '◊ú◊©◊ú◊ï◊ó ◊û◊†◊ß◊î' },
    { id: 'post',   icon: Image,    label: t('mayaChat.quickActions.post'),       command: 'create an Instagram marketing post' },
    { id: 'auto',   icon: Zap,      label: t('mayaChat.quickActions.automation'), command: 'enable automations' },
  ];

  const guestChips = [
    { emoji: 'üöø', label: '◊û◊í◊ë◊ï◊™', cmd: '◊ê◊†◊ô ◊¶◊®◊ô◊ö ◊û◊í◊ë◊ï◊™ ◊ë◊ë◊ß◊©◊î' },
    { emoji: 'üßº', label: '◊†◊ô◊ß◊ô◊ï◊ü', cmd: '◊ú◊©◊ú◊ï◊ó ◊û◊†◊ß◊î ◊ú◊ó◊ì◊® ◊©◊ú◊ô' },
    { emoji: 'üõ†Ô∏è', label: '◊™◊ß◊ú◊î',  cmd: '◊ô◊© ◊™◊ß◊ú◊î ◊ë◊ó◊ì◊® ◊©◊ú◊ô' },
    { emoji: '‚ùì', label: '◊¢◊ñ◊®◊î',   cmd: '◊ê◊†◊ô ◊¶◊®◊ô◊ö ◊¢◊ñ◊®◊î' },
  ];

  return (
    <div className="maya-container">

      {/* ‚îÄ‚îÄ iOS toast ‚îÄ‚îÄ */}
      {toast && (
        <div className="maya-toast-ios">
          ◊û◊©◊ô◊û◊î ◊ó◊ì◊©◊î ◊ú-{toast.staffName} ◊†◊©◊ú◊ó◊î! {toast.emoji}
        </div>
      )}

      {/* ‚îÄ‚îÄ iPhone Shell (only when open) ‚îÄ‚îÄ */}
      {mayaChatOpen && (
        <div className="maya-phone" dir={isRTL ? 'rtl' : 'ltr'}>

          {/* ‚ïê‚ïê‚ïê WhatsApp-style Header ‚ïê‚ïê‚ïê */}
          <div className="maya-wa-header">
            {/* decorative pill notch at the very top */}
            <div className="maya-phone-notch" />

            <div className="maya-header-row">
              {/* Left: close + avatar + name */}
              <div className="maya-header-left">
                <button
                  type="button"
                  onClick={() => toggleMayaChat?.()}
                  className="maya-hdr-btn"
                  aria-label="Close"
                >
                  <X size={20} />
                </button>

                <div className="maya-hdr-avatar-wrap">
                  <img src={MAYA_AVATAR_URL} alt="Maya" className="maya-hdr-avatar" />
                  {mayaOnline && <span className="maya-hdr-dot" />}
                </div>

                <div className="maya-hdr-info">
                  <span className="maya-hdr-name">Maya ¬∑ AI Concierge</span>
                  <span className="maya-hdr-status">
                    {mayaOnline ? 'üü¢ Online' : '‚è≥ ◊û◊™◊ó◊ë◊®◊™...'}
                  </span>
                </div>
              </div>
            </div>
          </div>

          {/* ‚ïê‚ïê‚ïê Quick-action chips (staff shortcuts) ‚ïê‚ïê‚ïê */}
          <div className="maya-quick-bar">
            {quickActions.map((a) => (
              <button
                key={a.id}
                type="button"
                className="maya-quick-chip"
                onClick={() => handleQuickAction(a.command)}
              >
                <a.icon size={13} />
                <span>{a.label}</span>
              </button>
            ))}
          </div>

          {/* ‚ïê‚ïê‚ïê Messages ‚ïê‚ïê‚ïê */}
          <div className="maya-messages">
            {mayaMessages.map((msg) => {
              const text = typeof msg.content === 'string'
                ? msg.content
                : (msg.content?.content ?? msg.content?.text ?? String(msg.content ?? ''));
              const rtl = hasHebrew(text);
              return (
                <div key={msg.id} className={`maya-msg ${msg.role}`}>
                  <div className="msg-avatar">
                    {msg.role === 'assistant'
                      ? <img src={MAYA_AVATAR_URL} alt="Maya" className="msg-avatar-img" />
                      : <User size={15} />}
                  </div>
                  <div
                    className={`msg-bubble ${msg.isError ? 'error' : ''} ${rtl ? 'msg-rtl' : 'msg-ltr'}`}
                    dir={rtl ? 'rtl' : 'ltr'}
                  >
                    <p>{text}</p>
                    {msg.data?.leads && (
                      <div className="msg-data">
                        <span className="msg-data-label">{t('mayaChat.leadsFoundLabel')}</span>
                        <span className="msg-data-val">{msg.data.leads.length}</span>
                      </div>
                    )}
                    <span className="msg-time">
                      {new Date(msg.timestamp).toLocaleTimeString(timeLocale, {
                        hour: '2-digit', minute: '2-digit',
                      })}
                      {msg.data?.taskCreated && (
                        <span className="msg-check" title="Sent">
                          <Check size={13} strokeWidth={2.5} />
                        </span>
                      )}
                    </span>
                  </div>
                </div>
              );
            })}

            {mayaIsTyping && (
              <div className="maya-msg assistant">
                <div className="msg-avatar">
                  <img src={MAYA_AVATAR_URL} alt="Maya" className="msg-avatar-img" />
                </div>
                <div className="msg-bubble typing">
                  <span /><span /><span />
                </div>
              </div>
            )}
            <div ref={messagesEndRef} />
          </div>

          {/* ‚ïê‚ïê‚ïê Guest Quick Chips ‚ïê‚ïê‚ïê */}
          <div className="maya-guest-chips">
            {guestChips.map((c) => (
              <button
                key={c.label}
                type="button"
                className="maya-guest-chip"
                onClick={() => handleQuickAction(c.cmd)}
              >
                <span>{c.emoji}</span>
                <span>{c.label}</span>
              </button>
            ))}
          </div>

          {/* ‚ïê‚ïê‚ïê Input Bar ‚ïê‚ïê‚ïê */}
          <div className="maya-input-bar">
            <div className="maya-input-pill" dir={isRTL ? 'rtl' : 'ltr'}>
              <input
                ref={inputRef}
                type="text"
                value={input}
                onChange={(e) => setInput(e.target.value)}
                onKeyPress={handleKeyPress}
                placeholder={t('mayaChat.askPlaceholder')}
                className="maya-input-field"
                disabled={mayaIsTyping}
                dir={isRTL ? 'rtl' : 'ltr'}
              />
            </div>
            <button
              type="button"
              onClick={input.trim() ? handleSend : undefined}
              disabled={mayaIsTyping}
              className={`maya-send-btn ${input.trim() ? 'active' : ''}`}
              aria-label={input.trim() ? 'Send' : 'Voice'}
            >
              {mayaIsTyping
                ? <Loader2 size={20} className="spin" />
                : input.trim()
                  ? <Send size={20} />
                  : <Mic size={20} />}
            </button>
          </div>

        </div>
      )}

      {/* ‚îÄ‚îÄ Floating Toggle Button ‚îÄ‚îÄ */}
      <button
        type="button"
        onClick={() => toggleMayaChat?.()}
        className="maya-fab"
        aria-label="Toggle Maya"
      >
        {mayaChatOpen ? <X size={26} /> : <MessageCircle size={26} />}
        <div className="maya-fab-ring" />
      </button>
    </div>
  );
};

export default MayaChat;
