import React, { useState, useEffect, useCallback, useRef } from 'react';
import { CalendarCheck, Phone, Building2, User, CheckCircle } from 'lucide-react';
import { getPropertyTasks, updatePropertyTaskStatus } from '../../services/api';
import './TaskCalendar.css';

export default function TaskCalendar() {
  const [tasks, setTasks] = useState([]);
  const [loading, setLoading] = useState(true);

  const loadingRef = useRef(false);
  const loadTasks = useCallback(async () => {
    if (loadingRef.current) return;
    loadingRef.current = true;
    setLoading(true);
    try {
      const list = await getPropertyTasks();
      setTasks(Array.isArray(list) ? list : []);
    } catch {
      setTasks([]);
    } finally {
      setLoading(false);
      loadingRef.current = false;
    }
  }, []);

  useEffect(() => {
    loadTasks();
  }, []);

  useEffect(() => {
    const onTaskCreated = () => loadTasks();
    window.addEventListener('maya-task-created', onTaskCreated);
    return () => window.removeEventListener('maya-task-created', onTaskCreated);
  }, []);

  const handleToggleStatus = useCallback(async (taskId, newStatus) => {
    if (!taskId) return;
    try {
      await updatePropertyTaskStatus(taskId, newStatus);
      setTasks((prev) =>
        prev.map((t) => (t.id === taskId ? { ...t, status: newStatus } : t))
      );
    } catch (e) {
      window.alert(e?.message || 'Failed to update');
    }
  }, []);

  const handleMarkDone = useCallback((taskId) => handleToggleStatus(taskId, 'Done'), [handleToggleStatus]);

  const isDone = (task) => (task?.status || '').toLowerCase() === 'done';

  const formatDate = (str) => {
    if (!str) return '—';
    try {
      const d = new Date(str);
      return d.toLocaleDateString('he-IL', {
        weekday: 'short',
        day: 'numeric',
        month: 'short',
        hour: '2-digit',
        minute: '2-digit',
      });
    } catch {
      return str;
    }
  };

  return (
    <div className="task-calendar p-10 bg-[#FBFBFB] min-h-screen" dir="rtl">
      <div className="flex items-center gap-3 mb-10">
        <div className="w-14 h-14 rounded-2xl bg-amber-100 flex items-center justify-center">
          <CalendarCheck size={28} className="text-amber-600" />
        </div>
        <div>
          <h1 className="text-3xl font-black text-gray-900">לוח משימות</h1>
          <p className="text-gray-500 mt-1">משימות שנוצרו על ידי מאיה – ניקיון, תחזוקה ושירות</p>
        </div>
      </div>

      {loading ? (
        <p className="text-gray-400 py-12 text-center">טוען משימות...</p>
      ) : tasks.length === 0 ? (
        <div className="task-calendar-empty bg-white rounded-2xl p-12 text-center border border-gray-100">
          <CalendarCheck size={48} className="text-gray-300 mx-auto mb-4" />
          <p className="text-gray-500 font-bold">אין משימות כרגע</p>
          <p className="text-sm text-gray-400 mt-1">כשמאיה תשלח הודעה לעובד, המשימה תופיע כאן</p>
        </div>
      ) : (
        <div className="task-calendar-grid">
          {tasks.map((t) => (
            <div
              key={t.id}
              className={`task-card ${isDone(t) ? 'task-done' : 'task-pending'}`}
            >
              <div className="task-card-header">
                <span className={`task-status-badge ${isDone(t) ? 'done' : 'pending'}`}>
                  {isDone(t) ? 'הושלם' : 'ממתין'}
                </span>
                <span className="task-date">{formatDate(t.created_at)}</span>
              </div>
              <p className="task-description">{t.description}</p>
              <div className="task-meta">
                {t.property_name && (
                  <div className="task-meta-row">
                    <Building2 size={16} />
                    <span>{t.property_name}</span>
                  </div>
                )}
                {t.staff_name && (
                  <div className="task-meta-row">
                    <User size={16} />
                    <span>{t.staff_name}</span>
                  </div>
                )}
                {t.staff_phone && (
                  <div className="task-meta-row task-phone">
                    <Phone size={16} />
                    <a href={`tel:${t.staff_phone}`} className="task-phone-link">
                      {t.staff_phone}
                    </a>
                  </div>
                )}
              </div>
              <button
                type="button"
                onClick={() => handleToggleStatus(t.id, isDone(t) ? 'Pending' : 'Done')}
                className={isDone(t) ? 'task-revert-btn' : 'task-done-btn'}
              >
                {isDone(t) ? (
                  <>חזרה לממתין</>
                ) : (
                  <><CheckCircle size={18} /> סמן כהושלם</>
                )}
              </button>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}
