import React, { useEffect, useRef, useState } from 'react';
import { CheckCircle2, Languages, Wrench, Sparkles, Brush } from 'lucide-react';
import useStore from '../../store/useStore';
import { setWorkerLanguage, clockInStaff, getStaffTasks, updateStaffTaskStatus } from '../../services/api';
import './FieldView.css';

const MARKET_LANGUAGES = {
  US: [
    { code: 'en', label: 'English' },
    { code: 'es', label: 'Spanish' },
  ],
  IL: [
    { code: 'he', label: 'Hebrew' },
    { code: 'th', label: 'Thai' },
    { code: 'hi', label: 'Hindi' },
  ],
};

const TASK_ICONS = {
  Cleaning: Brush,
  Maintenance: Wrench,
  'VIP Treats': Sparkles,
};

const FieldView = () => {
  const { fieldLanguage, setFieldLanguage, market, staffProfile, setStaffProfile } = useStore();
  const marketKey = market === 'IL' ? 'IL' : 'US';
  const languages = MARKET_LANGUAGES[marketKey] || MARKET_LANGUAGES.US;
  const [staffIdInput, setStaffIdInput] = useState(staffProfile.staffId || '');
  const [staffNameInput, setStaffNameInput] = useState(staffProfile.name || '');
  const [staffPhoneInput, setStaffPhoneInput] = useState(staffProfile.phone || '');
  const [tasks, setTasks] = useState([]);
  const [loadingTasks, setLoadingTasks] = useState(false);
  const [toast, setToast] = useState('');
  const [cameraOn, setCameraOn] = useState(false);
  const videoRef = useRef(null);
  const scanLoopRef = useRef(null);
  const prevTaskCountRef = useRef(0);

  useEffect(() => {
    const syncWorkerLanguage = async () => {
      try {
        await setWorkerLanguage(fieldLanguage);
      } catch (error) {
        console.error('Failed to sync worker language:', error);
      }
    };
    syncWorkerLanguage();
  }, [fieldLanguage]);

  useEffect(() => {
    const stopCamera = () => {
      if (scanLoopRef.current) {
        cancelAnimationFrame(scanLoopRef.current);
      }
      const stream = videoRef.current?.srcObject;
      if (stream) {
        stream.getTracks().forEach((track) => track.stop());
        if (videoRef.current) videoRef.current.srcObject = null;
      }
    };
    if (!cameraOn) {
      stopCamera();
      return;
    }
    const start = async () => {
      try {
        const stream = await navigator.mediaDevices.getUserMedia({ video: { facingMode: 'environment' } });
        if (!videoRef.current) return;
        videoRef.current.srcObject = stream;
        await videoRef.current.play();
        if ('BarcodeDetector' in window) {
          const detector = new window.BarcodeDetector({ formats: ['qr_code'] });
          const scan = async () => {
            if (!videoRef.current) return;
            try {
              const barcodes = await detector.detect(videoRef.current);
              if (barcodes && barcodes.length > 0) {
                const value = barcodes[0].rawValue || '';
                if (value) {
                  setStaffIdInput(value);
                  setToast('QR scanned');
                  setTimeout(() => setToast(''), 1500);
                  setCameraOn(false);
                  return;
                }
              }
            } catch (error) {
              // ignore scan errors
            }
            scanLoopRef.current = requestAnimationFrame(scan);
          };
          scanLoopRef.current = requestAnimationFrame(scan);
        }
      } catch (error) {
        setToast('Camera unavailable');
        setTimeout(() => setToast(''), 2000);
        setCameraOn(false);
      }
    };
    start();
    return stopCamera;
  }, [cameraOn]);

  useEffect(() => {
    const loadTasks = async () => {
      if (!staffProfile.staffId) return;
      setLoadingTasks(true);
      try {
        const data = await getStaffTasks(staffProfile.staffId);
        setTasks(data);
      } catch (error) {
        console.error('Failed to load tasks:', error);
      } finally {
        setLoadingTasks(false);
      }
    };
    loadTasks();
  }, [staffProfile.staffId]);

  useEffect(() => {
    const count = tasks.length;
    if (count > prevTaskCountRef.current) {
      if (navigator.vibrate) {
        navigator.vibrate([200, 100, 200]);
      }
      try {
        const ctx = new (window.AudioContext || window.webkitAudioContext)();
        const osc = ctx.createOscillator();
        const gain = ctx.createGain();
        osc.type = 'sine';
        osc.frequency.value = 880;
        gain.gain.value = 0.05;
        osc.connect(gain);
        gain.connect(ctx.destination);
        osc.start();
        setTimeout(() => {
          osc.stop();
          ctx.close();
        }, 150);
      } catch (error) {
        // audio not available
      }
      setToast('New task assigned');
      setTimeout(() => setToast(''), 1500);
    }
    prevTaskCountRef.current = count;
  }, [tasks]);

  const handleClockIn = async () => {
    if (!staffIdInput) return;
    try {
      const result = await clockInStaff({
        staff_id: staffIdInput,
        name: staffNameInput,
        phone: staffPhoneInput,
      });
      setStaffProfile({
        staffId: result.id,
        name: result.name,
        phone: result.phone,
        goldPoints: result.gold_points ?? result.points ?? 0,
        rank: result.rank ?? null,
      });
      const data = await getStaffTasks(result.id);
      setTasks(data);
      setToast('Clocked in');
      setTimeout(() => setToast(''), 1500);
    } catch (error) {
      console.error('Clock-in failed:', error);
    }
  };

  const handleTaskUpdate = async (taskId, status) => {
    try {
      await updateStaffTaskStatus(taskId, status);
      const data = await getStaffTasks(staffProfile.staffId);
      setTasks(data);
      setToast('Task updated');
      setTimeout(() => setToast(''), 1500);
    } catch (error) {
      console.error('Failed to update task:', error);
    }
  };

  return (
    <div className="field-view">
      <div className="field-header">
        <div className="field-title">
          <h2>Field Ops</h2>
          <span className="field-subtitle">Mobile-first task feed</span>
        </div>
        {staffProfile.staffId && (
          <div className="field-rank">
            <span className="field-rank-label">Rank</span>
            <span className="field-rank-value">{staffProfile.rank ?? '-'}</span>
            <span className="field-rank-points">{staffProfile.goldPoints ?? 0} pts</span>
          </div>
        )}
        <div className="field-lang-toggle">
          <Languages size={16} />
          <div className="field-lang-options">
            {languages.map((lang) => (
              <button
                key={lang.code}
                className={`field-lang-btn ${fieldLanguage === lang.code ? 'active' : ''}`}
                onClick={() => setFieldLanguage(lang.code)}
              >
                {lang.label}
              </button>
            ))}
          </div>
        </div>
      </div>

      <div className="field-task-list">
        <div className="field-clockin glass-card">
          <h3>Clock In</h3>
          <div className="clockin-form">
            <input
              type="text"
              placeholder="Scan QR / Staff ID"
              value={staffIdInput}
              onChange={(e) => setStaffIdInput(e.target.value)}
            />
            <button className="btn-secondary" onClick={() => setCameraOn((prev) => !prev)}>
              {cameraOn ? 'Stop Camera' : 'Scan QR'}
            </button>
            <input
              type="text"
              placeholder="Name (optional)"
              value={staffNameInput}
              onChange={(e) => setStaffNameInput(e.target.value)}
            />
            <input
              type="tel"
              placeholder="Phone (optional)"
              value={staffPhoneInput}
              onChange={(e) => setStaffPhoneInput(e.target.value)}
            />
            <button className="btn-primary" onClick={handleClockIn}>
              Clock In
            </button>
          </div>
          {cameraOn && (
            <div className="camera-preview">
              <video ref={videoRef} muted playsInline />
            </div>
          )}
        </div>

        {loadingTasks && <div className="field-loading">Loading tasks...</div>}
        {tasks.map((task) => {
          const Icon = TASK_ICONS[task.task_type] || CheckCircle2;
          return (
            <div key={task.id} className={`field-task-card ${task.status}`}>
              <div className="task-main">
                <div className="task-icon">
                  <Icon size={18} />
                </div>
                <div className="task-info">
                  <span className="task-title">{task.task_type}</span>
                  <span className="task-meta">{task.room}</span>
                </div>
              </div>
              <div className="task-actions">
                <button
                  className="task-action-btn"
                  onClick={() => handleTaskUpdate(task.id, 'on_my_way')}
                  disabled={task.status === 'finished'}
                >
                  On My Way
                </button>
                <button
                  className="task-action-btn"
                  onClick={() => handleTaskUpdate(task.id, 'started')}
                  disabled={task.status === 'finished'}
                >
                  Start Task
                </button>
                <button
                  className="task-action-btn primary"
                  onClick={() => handleTaskUpdate(task.id, 'finished')}
                  disabled={task.status === 'finished'}
                >
                  Finish
                </button>
              </div>
            </div>
          );
        })}
      </div>
      {toast && <div className="field-toast">{toast}</div>}
    </div>
  );
};

export default FieldView;
